     table.applicationcontainer table#stateindicator,
     div#contentnavtop,
     div.globalbuttonbar,
     div.globalbuttonbar button,
     div.contentbuttonbar,
     div.contentbuttonbar button
       {padding:0 !important; margin:0 !important;}
