@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* DEFAULT URLBAR MOD */

#feed-button
{
list-style-image: url("data:image/png;base64,") !important;
}

#star-button
{
margin-left: 5px !important;
margin-right: 5px !important;

list-style-image: url("data:image/png;base64,") !important;
}

#star-button[starred="true"]
{
list-style-image: url("data:image/png;base64,") !important;
}

#go-button
{
list-style-image: url("data:image/png;base64,") !important;
}

.search-go-button
{
list-style-image: url("data:image/png;base64,") !important;
}

/* URLBAREXT MOD */

#urlbarext-copy, #urlbarext-copy:hover,
#urlbarext-tiny, #urlbarext-tiny:hover,
#urlbarext-search, #urlbarext-search:hover,
#urlbarext-up, #urlbarext-up:hover,
#urlbarext-tags, #urlbarext-tags:hover,
#urlbarext-seq, #urlbarext-seq:hover,
#urlbarext-phproxy, #urlbarext-phproxy:hover
{
padding: 0px !important;
width: 16px !important;
margin-right: 1px !important;

list-style-image: url("data:image/png;base64,") !important;
}

#urlbarext-grippy
{
background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAHCAYAAAAvZezQAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAHRJREFUeNpiCAoKkvr//z87EDOAMBMnJ+fTy5cv+zAwMLADMQOToKAgw8GDB9fcvHkTLMjCzMzMICYmxrBnz541f/78CWH5+/cvw6tXrxhcXV1D1NXVtzBER0f/v3TpUjDMYJbv379L6+rqvgXq/wkyFCDAAK3KMqaidUH4AAAAAElFTkSuQmCC") no-repeat center center !important;
}

#urlbarext-grippy[closed="true"]
{
margin-right: 3px !important;

background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAHCAYAAAAvZezQAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAG1JREFUeNpi+P//PwMUswcFBUkxMUAA++XLl304OTmfggTYb9686XPw4ME1goKCDExXr1712bNnzxoxMTEGZmZmBiZtbe0tLi4uIa9evWL4+/cvA9zAS5cuBUdHR/+HGfpTV1d3y/fv36UBAgwAhkQx8wmEFQcAAAAASUVORK5CYII=") no-repeat center center !important;
}

.urlbarext-tooltip
{
-moz-appearance: none !important;
-moz-border-radius: 0 !important;
background-color: #f2c464 !important;
font: message-box !important;
padding: 3px 7px !important;
border-left-color: #f9e2b2 !important;
border-top-color: #f9e2b2 !important;
border-right-color: #c29d50 !important;
border-bottom-color: #c29d50 !important;
}

.statusbarpanel-text[value*="PH: "],
.statusbarpanel-text[value*="UrlbarExt: "],
.statusbarpanel-text[value*="Info: "],
.statusbarpanel-text[value*="Warning: "],
.statusbarpanel-text[value*="Error: "]
{
font: message-box !important;
color: -moz-DialogText !important;
}
