css:
@-moz-document regexp(".*rutracker.org/.*") {
  iframe {
  	display: none !important;
    opacity: 0;
  }
}

@-moz-document regexp(".*rutracker.org/forum/tracker.php.*")
{
  table#tor-tbl>thead>tr>th:nth-child(4), table#tor-tbl>thead>tr>th:nth-child(5) {
    opacity: 1;
  }
  
  #bn-top-block {
  	display: none !important;
  }
  
  body table#tor-tbl > thead > tr > th:nth-child(6) {
  	text-align: center!important;
  }
  
  tr.tablesorter-headerRow>th:nth-child(3), tr.tablesorter-headerRow>th:nth-child(5) {
    display: table-cell;
	}
  
  a.small.tr-dl.dl-stub:before {
  	left: 500px !important;
  }
  
  #search-results {
  	margin-top: 30px;
  }
  
  tr.tablesorter-headerRow>th:nth-child(3) {
  	width: 30% !important;
  }
}
