css:
/**** Created by Ryan ****/
@-moz-document domain('www.youtube.com'), url-prefix('https://www.youtube.com/watch') {

#guide-inner-content.ytd-app {
	background: #000000;
}

.title.ytd-guide-entry-renderer {
	color:#FFFFFF;
}

ytd-guide-section-renderer[guide-refresh] #guide-section-title.ytd-guide-section-renderer {
	color: #ffffff;
}

ytd-guide-collapsible-section-entry-renderer.ytd-guide-section-renderer:not(:first-child) {
	border-top: 1px solid #f3f3f3;
}

ytd-rich-grid-row {
	background: #000000;
}

#video-title.ytd-rich-grid-media {
	color: #ffffff;
}

#button.ytd-menu-renderer yt-icon.ytd-menu-renderer {
	color: #FFFFFF;
}

.ytd-menu-renderer[button-renderer].ytd-menu-renderer:hover yt-icon.ytd-menu-renderer, #button.ytd-menu-renderer:hover yt-icon.ytd-menu-renderer {
	color: #ffffff;
}

ytd-rich-section-renderer
{
    background: #000000;
}

ytd-rich-shelf-renderer {
    border-top: 4px solid #555555;
	border-radius: 10px;
}

.guide-icon.ytd-guide-entry-renderer{
	color: #ffffff;
}

#primary.ytd-two-column-browse-results-renderer {
    background-color: #222222;
}

#chips.ytd-feed-filter-chip-bar-renderer
{
      background: #000000;
}

yt-chip-cloud-chip-renderer.ytd-feed-filter-chip-bar-renderer{
	background: #055966;
    color: #FFFFFF;
}


yt-chip-cloud-chip-renderer[chip-style=STYLE_DEFAULT]:not([selected]):hover, yt-chip-cloud-chip-renderer[chip-style=STYLE_HOME_FILTER]:not([selected]):hover, yt-chip-cloud-chip-renderer[chip-style=STYLE_REFRESH_TO_NOVEL_CHIP]:not([selected]):hover, yt-chip-cloud-chip-renderer[chip-style=STYLE_DEFAULT]:not([selected]):focus, yt-chip-cloud-chip-renderer[chip-style=STYLE_HOME_FILTER]:not([selected]):focus, yt-chip-cloud-chip-renderer[chip-style=STYLE_REFRESH_TO_NOVEL_CHIP]:not([selected]):focus
{
 background: #898989; 
}

#sections.ytd-guide-renderer>*.ytd-guide-renderer:not(:last-child)
{
  border-bottom: 1px solid #f3f3f3;
  border-bottom-style: dashed;
}

/***** added this for right arrows *****/

#left-arrow-button.ytd-feed-filter-chip-bar-renderer, #right-arrow-button.ytd-feed-filter-chip-bar-renderer {
  background-color: rgb(0 0 0);
}
  
button.yt-icon-button > yt-icon {
	color: #bb0909;
}

#container.ytd-masthead
{
	background: #000000;
}

yt-icon.ytd-logo
{
  color:#FFFFFF;
}

/******* For Playing VIdeos ******/
ytd-watch-flexy[flexy][is-two-columns_] #columns.ytd-watch-flexy{
  background: #000000;
}

ytd-page-manager>*.ytd-page-manager{
  background:#000000;
}

canvas, caption, center, cite, code, dd, del, dfn, div, dl, dt, em, embed, fieldset, font, form, h1, h2, h3, h4, h5, h6, hr, i, iframe, img, ins, kbd, label, legend, li, menu, object, ol, p, pre, q, s, samp, small, span, strike, strong, sub, sup, table, tbody, td, tfoot, th, thead, tr, tt, u, ul, var, ytd-button-renderer.style-text[is-icon-button] #text.ytd-button-renderer, ytd-button-renderer.style-default[is-icon-button] #text.ytd-button-renderer, ytd-button-renderer.style-opacity[is-icon-button] #text.ytd-button-renderer
{
 color: #FFFFFF; 
}

yt-formatted-string.ytd-video-primary-info-renderer{
  color: aqua;
}

#content-text.ytd-comment-renderer, ytd-toggle-button-renderer.style-text[is-icon-button] #text.ytd-toggle-button-renderer
{
      color: #FFFFFF;
}

yt-chip-cloud-chip-renderer.yt-chip-cloud-renderer
{
  background: #898989;
}

ytd-toggle-button-renderer #button.ytd-toggle-button-renderer {
color: red;
}

ytd-toggle-button-renderer.force-icon-button a.ytd-toggle-button-renderer
{
 color: #999999;
}

yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string {
    color: #FFFFFF;
}

yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string:hover {
    color: #777777;
}

.sbqs_c {
    color: #000000;
}

#guide-icon.ytd-masthead {
    color: #e70000;
}

#guide-icon.ytd-app {
    fill: #ffffff;
}

ytd-guide-entry-renderer[guide-refresh] #endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:hover, ytd-guide-entry-renderer[guide-refresh] #endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:focus {
    border-radius: 3px;
    background: #333333;
}

#title.ytd-rich-shelf-renderer, #video-title.ytd-video-renderer, ytd-grid-video-renderer #video-title.yt-simple-endpoint.ytd-grid-video-renderer, iron-input.tp-yt-paper-input > input.tp-yt-paper-input, #subtitle.ytd-shelf-renderer, #video-title.ytd-rich-grid-slim-media, #title.ytd-grid-channel-renderer, #video-title.ytd-reel-item-renderer, .title.ytd-recognition-shelf-renderer
 {
    color: #ffffff;
}


#channel-header.ytd-c4-tabbed-header-renderer, #tabs-inner-container.ytd-c4-tabbed-header-renderer
{
background-color: #000000;	
}

iron-input.tp-yt-paper-input > input.tp-yt-paper-input
{
border: 1px solid #333333;
line-height: 0;
}

#title.ytd-shelf-renderer{
color: #00c7e7;
}

 #contents.ytd-item-section-renderer>ytd-shelf-renderer.ytd-item-section-renderer:last-child
{
background:#000000;
}

#dismissible.ytd-post-renderer span, #dismissible.ytd-post-renderer #text, #dismissible.ytd-post-renderer #button.ytd-menu-renderer yt-icon.ytd-menu-renderer {
color:#000000;
}

ytd-subscribe-button-renderer[button-style=COMPACT_GRAY] tp-yt-paper-button.ytd-subscribe-button-renderer
{
color:#a33030;
}


}
