@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

#side .stats td .numeric,
#side #me_name,
.status-body .screen-name,
ul.sidebar-menu li.active a,
#side .collapsible h2.sidebar-title,
body #content h2,
body.timeline #content h1,
.bar h3 label,
.bar span strong,
ul.sidebar-menu span.stat_count,
#latest_text strong,
#side .promotion .definition strong {
  font-family:Georgia !important;
  font-weight:normal !important;
}

#side .stats td .numeric, #side #me_name { font-size:16px !important; }
#side .stats td a:not(:hover) { color:inherit !important; }

.status-body strong .screen-name:after { content:":" }
.status-body .screen-name:not(:hover) {
  color:inherit !important;
}

