css:
/* ghad13's style based on mazuffer's style for Freekick v1.1.1 */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.freekick.org") {




/* ############ striped bars (no longer striped though) ############ */


.green-head {
    background-image: none !important;
    background-color: #030 !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
    -moz-border-radius-bottomright: 2px;
    -moz-border-radius-bottomleft: 2px;
}
.red-head{
    background-image: none !important;
    background-color: #030 !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
    -moz-border-radius-bottomright: 2px;
    -moz-border-radius-bottomleft: 2px;
}
.blue-head{
    background-image: none !important;
    background-color: #030 !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
    -moz-border-radius-bottomright: 2px;
    -moz-border-radius-bottomleft: 2px;
}
.green-title{background-image: none !important;}
.red-title{background-image: none !important;}
.blue-title{background-image: none !important;}
.green-title a, .red-title a, .blue-title a{
    color:#fff !important;
}

.green-crn-re{background-image: none !important;}
.red-crn-re{background-image: none !important;}
.blue-crn-re{background-image: none !important;}

.red-crn-r{background-image: none !important;}
.green-crn-r{background-image: none !important;}
.blue-crn-r{background-image: none !important;}

.green-crn-l{background-image: none !important;}
.red-crn-l{background-image: none !important;}
.blue-crn-l{background-image: none !important;}




/* ############ skill & age colors ############ */


.awesome {color: #d61 !important;}
.brilliant {color: #f83 !important;}
.superb {color: #f96 !important;}
.excellent {color: #f9c !important;}
.good {color: #c3c !important;}
.decent {color: #fff !important;}
.weak {color: #3cc !important;}
.poor {color: #ff0 !important;}
.awful {color: #f00 !important;}

.age-old { color:#c66723 !important; }
.drop { color:#c73 !important; }
.pot { color: #5e3 !important; }
.abovepot { color: #ec0 !important;}
.age-senior { color:#FFFFFF!important; }
.age-youth-0 { color:#99DDFF!important; }
.age-youth-1, .age-youth { color:#4488FF!important; }
.age-youth-2 { color:#8855FF!important; }
.age-youth-3 { color:#CC33CC!important; }
.age-youth-4 { color:#FF8833!important; }
.age-youth-5 { color:#c66723!important; }
.age-retired { color: #F00 !important; } 




/* ############ menu and status ############ */


#lang-select{
    background-image:none !important;
    background-color: #261 !important;
    font-style: italic !important;
    font-weight: bold !important;
    cursor: default !important;
    visibility: visible !important;
}

.menu-header{
    -moz-border-radius-topright: 5px;
}

.menu-bottom{
    -moz-border-radius-bottomright: 5px;
}

.menu-sprite{
    background-image: none !important;
    background-color: #030 !important;
}

.header-sprite{
    background-image: none !important;
}

#left-wrap .green-stripe{
    border-bottom: 3px double #DD7700 !important;
}




#menu-left{
    margin-top:1px !important; /* to align the menu with the content box */
    width: 160px !important;
    background-image: none !important;
    background-color: #030 !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-bottomright: 5px;
}
#menu-left2{
    width: 160px !important;
    background-image: none !important;
    background-color: #030 !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-bottomright: 5px;
}
#inner-menu{    
    background-image: none !important;
    background-color: transparent !important;
}
#inner-menu2{    
    background-image: none !important;
}
.menu-btm{
    background-image: none !important;
    background-color: #030 !important;
    -moz-border-radius-bottomright: 5px;
}
.club-name a{
    color:#fff !important;
}
.settings-header {
    color:#fff !important;
}
.makeMenu li{
    background-image:none !important;
    border-bottom: 1px dotted #008800 !important;
    padding-bottom:2px !important;
}
.makeMenu li a{
    color: #fff !important;
    font-weight: bold !important;
    border-left: 4px solid #d70 !important;
}
.makeMenu li a:visited{
    color: #fff !important;
}
.makeMenu li ul{
    border:3px solid #040 !important;
    background-color:#030 !important;
    -moz-border-radius: 5px;
}
.makeMenu ul li a{
    border-left: 4px solid #d70 !important;
}
.makeMenu li a:hover{
    background-color: #151 !important;
}
#inner-menu2 ul li a{
    color:#fff !important;
    border-left: 4px solid #d70 !important;
}
#inner-menu2 ul li:hover{
    background-color: #151 !important;
}
#inner-menu2 div.status-left p{
    color:#fff !important;    
}
#inner-menu2 .greyed-out p{
    color: #eee !important;
}
#inner-menu2 .greyed-out p:hover{
    background-color: #151 !important;
}
.on { /* changes the color of the "on" text, use .off to change "off" */
    color:#595 !important;
}
.status-right{
    color:#fff !important;
}
#inner-menu p.green-stripe, #inner-menu2 p.green-stripe {
    width: 135px !important;
}

.right-stats td
{
    white-space: normal !important;
}




/* ############ main layout stuff ############ */


body {
    background-color: #363 !important;
}
.width{ /* changes background of the whole site */
    background-color: #261 !important;
}
#main-green-wrap{    
    background-image: none !important;
    background-color: #020 !important;
    color: #fff !important;
    -moz-border-radius: 5px;
}
#green-round{ /* removes rounded corner */
    background-image: none !important;
    height: 10px !important;
}
#white-content{
    -moz-border-radius: 5px;
    background-color:#282 !important;
    background-image: none !important;
    width: 790px !important; /* since I removed the background I need to do this */
}
#main-content{padding-right:0px !important;} /* makes table of players etc. slightly wider, done because of width change in white-content */
.rigth-crn{ /* removes rounded corner */
    background-color:#282 !important;
    background-image: none !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
}
.btm-shadow{
    background-color:#282 !important;
    background-image: none !important;
    -moz-border-radius-bottomleft: 5px;
    -moz-border-radius-bottomright: 5px;
}
#content-l{
    border-right:2px solid #252 !important; /* changes color of the white horizontal line, originally 1px and white */
}
#content-r{
    border-left: none !important;
    width: 200px !important; /* because of changing the width of #white-content I need to do this too */
}
#content-r a{
    color: #fff !important;
}

.small-news-header {
    background-color:#074700 !important;
}

.loading
{
    padding: 3px 50px !important;
    color: #FFFFFF !important;
    font-weight: bold;
    font-size: 14px;
    background-position: 8px center !important;
    background-color: #003300 !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
    -moz-border-radius-bottomright: 5px;
    -moz-border-radius-bottomleft: 5px;
}


.right{
    float: right !important;
}



/* ############ topmenu aka. bluebar ############ */


#blue-bar{        
    background-image: none !important;
    background-color: #030 !important;
    height:24px !important;
    -moz-border-radius-bottomleft: 5px;
    -moz-border-radius-bottomright: 5px;
}
#blue-nav{
    background-image: none !important;
}
#blue-bar a{
    color:#fff !important;
}




/* ############ player tabs on tactics page and on other teams' pages ############ */


.players-tabs li {
    background-image: none !important;
    padding: 2px 2px 0px 2px !important;
}
.players-tabs li.players-selected {
    background-image: none !important;
    background-color: #09F !important;
    color: #FFF !important;
    margin: 0 2px !important;
    padding: 1px 1px 0px 1px !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
}
.players-tabs li.players-selected a {
    background-image: none !important;
    background-color: #292 !important;
    border-right: 8px solid #292 !important;
    color: #FFF !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
}
.players-tabs li a {
    background-image: none !important;
    background-color: #236 !important;
    border-right: 8px solid #236 !important;
    color: #AAF !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
}
div.players-wrap {
    background-color: #282 !important;
    border-style: solid !important;
    border-color: #005 !important;
    border-width: 1px 0 !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
    -moz-border-radius-bottomright: 5px;
    -moz-border-radius-bottomleft: 5px;
} 

/* ############ league table ############ */

.brdRGrey{border-right:2px solid #242 !important;}

.promotion-demotion-automatic {
    background-color:#000 !important;
}
.promotion-demotion-qualification{
    background-image:none !important;
    background-color: #114011 !important;
    height: 3px !important;
}
.promotion-demotion-none{
    background-image:none !important;
}




/* ############ economy ############ */


.income {color: #4f4 !important;}
.income a.textlink{color: #4f4 !important;}
.expense {color: #f52 !important;}
.expense a.textlink{color: #f52 !important;}
#finances .income {color: #4f4 !important;}
.financesMain td{background-color: #363 !important;}
.finances a {color: #000 !important;}
.bid-inactive {color: #aaa !important;} /* used when your bid is lower than average bid */




/* ############ before logging in ############ */


.reg a{color:#f52 !important;}
.quest{color: #fff !important;}
.freekick{color: #fff !important;}
.profile-name {color: #fff !important;}
.grey{color: #fff !important;}
.bg, .bg-dwn, .answ-left-up, .answ-left-dwn, .answ-right-up, .answ-right-dwn, .answ-content {
    background-image: none !important;
    background-color: #151 !important;
    border: 0px !important;
}

.answ-content {
    width: 96% !important;
    padding: 0 10px !important;
    text-align: justify;
}

.bg, .answ-left-up, .answ-right-up {
    -moz-border-radius-topleft: 5px;
    -moz-border-radius-topright: 5px;
}
.bg-dwn, .answ-left-dwn, .answ-right-dwn {
    -moz-border-radius-bottomleft: 5px;
    -moz-border-radius-bottomright: 5px;
}
#form-login li label {color: #5a5 !important;}
.title-menu-l {color: #5a5 !important;}
.signup-crn-l {
    -moz-border-radius-topleft: 5px;
    -moz-border-radius-bottomleft: 5px;
    border-left: 2px solid #141;
    border-top: 2px solid #141;
    border-bottom: 2px solid #141;
}
.signup-crn-r {
    -moz-border-radius-topright: 5px;
    -moz-border-radius-bottomright: 5px;
    border-right: 2px solid #141;
    border-top: 2px solid #141;
    border-bottom: 2px solid #141;
}
.signup p{
    background-color: #fff !important;
}




/* ############ set pieces ############# */

select#side
{
    width: 100px;
}



/* ############ other stuff ############ */


.section-head{ /* main header on every page */
    color:#fff !important;
}
.red-stripe{ /* line under section-head */
    background-image:none !important;
    background-color:#030 !important;
}
#footer-tabs span{
    background-image: none !important;
}
.sortval, .sortreversed, .sortcaseinsensitive, .sortvaltext, .sortnumeric, .sortcaseinsensitiveemptyhighest, .sortnumericemptyhighest { /* changes color of the links to sort tables*/
    color: #fff !important;
}
.odd, tr.odd td { /* odd table rows*/
    background-color: #060 !important;
}    
.even, tr.even td { /* even table rows*/
    background-color: #005a00 !important;
}
.odd a, tr.odd td a, .even a, tr.even td a {
    color: #fff !important;
}
.marked, tr.marked td, .odd-marked, tr.odd-marked td { /* used for high-lighting table rows*/
    background-color: #333 !important;
}
.even-marked, tr.even-marked td { /* used for high-lighting table rows*/
    background-color: #333 !important;
} 
.even-marked a, tr.even-marked td a, .marked a, tr.marked td a, .odd-marked a, tr.odd-marked td  a{
    color: #fff !important;
}
table th{
    background-color:#040 !important;
    color: #fff !important;
}
table.dotted tr th , table.dotted tr td, table.dotted-header tr th , table.achievers tr th , table.achievers tr td, table.transfers tr th , table.transfers tr td, table.moments tr th , table.moments tr td, table.clubnews tr th , table.clubnews tr td, table.standard-list tr th, table.standard-list tr td, .players td, .players th, .view-active, .view-passive, .select-list, .message-header, .message-content, .promotion-demotion-none, .plain-dotted-list li, .dots, .dotsLine, .training-history tr td.dotted { /* removes the use of the ugly dots_grey.gif */
    background-image:none !important;
    border-bottom: 1px dotted #696 !important;
}
table.dotted tr th.noline {
    border: 0px !important;
}
table.dotted tr td.empty {
    border: 0px !important;
}

div .dotted{
    border-bottom: 1px dotted #77AA77 !important;
    padding-top: 3px !important;
    padding-bottom: 3px !important;
}

p.dots {
    border: 0px !important;
}
.greyLine{
    background-color: #141 !important
}
.guestbook-title {
    background-color:#282 !important;
    color:#030 !important;
}
.guestbook-comment {
    color:#030 !important;
}
table.player-achievements .header { 
    background-color: #004400 !important;
    color:#fff;
    text-align:center;
    font-weight:bold;
}
.transfer-navigator tr td a {                    
    font-weight: bold !important;
    color: #f72 !important;
}        
.transfer-navigator tr td a.current {
    color:#000 !important;
}
.transfer-column-l {
    width: 37% !important;
}
.noline a{ 
    color: #fff !important;
}
.empty { 
    background-color: #484 !important;
}
.lightGreyBg{background-color:#141 !important;}

.header {
    background-color: #030 !important;
}
.blue {color: #030 !important} /* blue? more like green ;) -used in press releases */ 
.news-title {
    background-color: #171 !important;
}
.news-header a{
    color: #FFF !important;
}
.message-open {
    background-color:#151 !important;
}
.message-header a{
    color: #FFF !important;
}
.view-active {
    background-color:#173 !important;
}
.online {color: #00AA00 !important; font-weight: bold !important;}
.offline {color: #a33 !important;  font-weight: bold !important;}
.club, .user, .nation {
    color:#fff !important;
}

a.nation
{
    white-space: normal !important;
}
.strong {font-weight: bold !important;}
.moments a{
    color:#fff !important;
    font-weight: bold !important;
}

#participating a{color: #fff !important; font-weight: bold !important;}
#fk_table_header {border-left: 0px !important;}


.blue-round { /* used on tactics page */
    background-image: none !important;
    background-color: #166 !important;
    -moz-border-radius: 5px;
}
.roundtop, .roundbottom {visibility: hidden !important;} /* used on tactics page */

.green-bl, .green-br, .green-tl, .green-tr{ /* used on match report page */
    background-image: none !important;
    background-color: #252 !important;
    -moz-border-radius: 5px;
}
.blue-bl, .blue-br, .blue-tl, .blue-tr { /* used on match report page */
    background-image: none !important;
    background-color: #363 !important;
    -moz-border-radius: 5px;
}
.matchreport-club {color: #fff !important;}
#main-tabs {
    padding-left: 15px !important;
}
#main-tabs li.tab-hidden{                        
    background-color: #BB9 !important;
    background-image: none !important ;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
}
#main-tabs li.tab-active{            
    background-image: none !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
}
.none-player { /* used on CO page, maybe somewhere else too */
    color: #fff !important;
    font-weight: bold !important;
}
.player {
    color: #fff !important;
}
.form-prediction-denom  /* used in form-tooltip */
{
    background-color: #005500 !important;
}

.rating-denom  /* used in rating-tooltip */
{
    color: #448800 !important;
    font-weight: bold !important;
}

td.news-header a{
    color: #fff !important;
}
hr{ /* used on CO page, maybe somewhere else too */
    background-color: #141 !important;
    height: 3px;
    border: 0px;
}
.co-dashed{ /* used on CO page, maybe somewhere else too */
    border-bottom: 1px dotted #030 !important;
}
.lineup-status{
    -moz-border-radius: 5px;
}

.green, .green a
{
    color: #00AA00 !important;
}




/* ############ MATCHREPORT ############ */ 


.matchreport_legendary {
    color: #E51 !important;
    font-style: italic !important;
    font-size: 12px;
    font-weight: bold;}
.matchreport_unbelievable {
    color: #E51 !important;
    font-style: normal !important;
    font-weight: bold;}
.matchreport_masterful {
    color: #D51 !important;
    font-style: normal !important;
    font-weight: bold;} 
.matchreport_awesome {
    color: #D61 !important;
    font-style: normal !important;
    font-weight: bold;} 
.matchreport_brilliant {
    color: #F83 !important;
    font-style: normal !important;
    font-weight: bold;} 
.matchreport_superb {
    color: #F96 !important;
    font-style: normal !important;
    font-weight: bold;} 
.matchreport_excellent {
    color: #F9C !important;
    font-style: normal !important;
    font-weight: bold;} 
.matchreport_good {
    color: #C3C !important;
    font-style: normal !important;
    font-weight: bold;} 
.matchreport_decent {
    color: #FFF !important;
    font-style: normal !important;
    font-weight: bold;} 
.matchreport_weak {
    color: #3CC !important;
    font-style: normal !important;} 
.matchreport_poor {
    color: #FF0 !important;
    font-style: normal !important;} 
.matchreport_awful {
    color: #F00 !important;
    font-style: normal !important;}
.matchreport_dreadful {
    color: #AAA !important;
