    font-weight: bold;}
.matchreport_there {
    color: #F9C !important;
    font-style: normal !important;
    font-weight: bold;}
.matchreport_close {
    color: #3CC !important;
    font-style: normal !important;}
.matchreport_too_late {
    color: #F00 !important;
    font-style: normal !important;}

.matchreport_right_spot{
    color: #D61 !important;
    font-style: normal !important;
    font-weight: bold;}
.matchreport_ready{
    color: #F9C !important;
    font-style: normal !important;
    font-weight: bold;}
.matchreport_hesitant{
    color: #3CC !important;
    font-style: normal !important;}
.matchreport_fooled{
    color: #FF0 !important;
    font-style: normal !important;}
.matchreport_totally_wrong{
    color: #F00 !important;
    font-style: normal !important;}

.matchreport_net_wide_open {
    color: #d61 !important;
    font-style: normal !important;}
.matchreport_totally_free {
    color: #d61 !important;
    font-style: normal !important;}

.min, .matchreport-penaltykicks, .matchreport-halftime, .matchreport-penaltyround {
    color: #c80 !important;
    font-weight: bold !important;}

.matchreport-goalattempt, .matchreport-throwin, .matchreport-cornerkick, .matchreport-freekick, .matchreport-penaltykick, .matchreport-playcontinued, .matchreport-counterattack { 
    color: #4f4 !important;
    font-weight: bold !important;}

.matchreport-goal{
    color: #d61 !important;
    font-weight: bold !important;
    font-size: 13px !important;}

.int-fixtures th {border-left-style: none !important;}



/* ############ Additional ############ */


.news-text a, .message-content a {
    color: #09F !important;
}

.news-edit a {
    color: #FFF !important;
    font-weight: bold;
}

.darkGrey { /* line under the banner */
    background-color: #09F !important;
    height: 1px !important;
}

.hidden-result {
    color: #999 !important;
}

input.login-submit
{
    margin: 0 !important;
}



/* ########  Message form  ######## */


input
{
    margin: 1px 0 !important;
}

textarea
{
    width: 90% !important;
    min-height: 100px !important;
}




/* ############ needed for the 'potentials calculator' ############ */


.player-column-r { 
    width: 48% !important;
}

.algnValue {
    text-align: center !important;
    padding-right: 2px !important;
}
.algnValue.awesome, .algnValue.brilliant, .algnValue.superb, .algnValue.excellent, .algnValue.good, .algnValue.decent  {
    font-weight: bold !important;
}


}     /* ### END of www.freekick.org ### */




@-moz-document url("http://www.freekick.org/?loc=world_cup_senior") {
#content-l a{
    color: #fff !important;
    font-weight: bold !important;
}
}
@-moz-document url("http://www.freekick.org/?loc=world_cup_youth") {
#content-l a{
    color: #fff !important;
    font-weight: bold !important;
}
}
@-moz-document url-prefix(http://www.freekick.org/forum) {
body {background-color: #efefef !important;}
}




