css:
/* ==UserStyle==
@name           Dark Matter (neutron for iichan.hk)
@namespace      USO Archive
@author         716C3A64-D63A-11DF-9BE2-44A0DFD72085, Kuroi_Mato_O
@description    `A bastard hybrid of iichan dark theme and 2-ch.ru neutron. Use with caution.`
@version        20120725.3.2
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("iichan.hk"){

/*	Name: Dark Matter (neutron for iichan.ru) */
html{
    background-color: #181818;        
}
body 
{
  color:#B0C4DE !important;
  background-color: #181818 !important;
  font-family:"Trebuchet MS",Trebuchet,tahoma,serif !important;
}

hr
{
  border: 0 !important;
  height: 2px;
  background-color: #698CC0 !important;
}

input[type="text"], textarea
{
  -moz-appearance:none !important;
  -moz-border-radius:5px 5px 5px 5px !important;
  -moz-box-sizing:content-box !important;
  background-color:#111111 !important;
  border:2px solid #545454 !important;
  color:#FFFFFF !important;
}

td.prohibition, td.prohibition li
{
  color:#993333 !important;
}

a, .logo a, .sublogo a  
{
	color: #DDC771 !important;
}

a:hover, .logo a:hover, .sublogo a:hover 
{
	color: #FFF369 !important;
}

a[href$=sage],  a[href$=Sage], a[href$=SAGE] 
{
	color: #993333 !important;
}

a[href$=sage]:before,  a[href$=Sage]:before, a[href$=SAGE]:before 
{
	content: "[sage] " !important;
}

a:hover[href$=sage],  a:hover[href$=Sage], a:hover[href$=SAGE] 
{
	color: #CC1212 !important;
}

.logo 
{
        clear:both;
        text-align:center;
        font-size:2em;
        color:  #DDC771 !important;
        width:100%;
}

.sublogo {
        clear:both;
        text-align:center;
        font-size:1em;
        color:  #DDC771 !important;
        width:100%;
	padding: 2px 0px 12px 0px;
}
.theader, .passvalid, .replymode {
	background:#282828 !important;
	text-align:center !important;
	padding:2px !important;
	color:#698CC0 !important;
	clear: both !important;
	font-weight: bold !important;
	margin-bottom: .5em !important;
	border: solid 1px #757575 !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px  !important;
}

.adminbar
{
  font-size: 80%;
}

.postarea {
}
.postblock {
  background:none repeat scroll 0 0 transparent !important;
  color:#698CC0 !important;
  font-weight:bold !important;
}
.footer {
        text-align:center;
        font-size:12px;
        font-family:serif;
	clear: both;
	padding-top: 10px;
}

.managehead {
	background:#AAAA66;
	color:#400000;
	padding:0px;
}
.postlists {
	background:#FFFFFF;
	width:100%;
	padding:0px;
	color:#800000;
}
.row1 {
	background:#EEEECC;
	color:#800000;
}
.row2 {
        background:#DDDDAA;
        color:#800000;
}
.unkfunc {
	background:inherit;
	color:#B6D648 !important;
}
.pcount {
	color:#5D934D;
}
.filesize 
{
  -moz-border-radius:8px 8px 8px 8px !important;
  background-color:#181818 !important;
  border:1px solid #757575 !important;
  color:#999999 !important;
  font-size:0.8em !important;
  padding:2px !important;
  text-decoration:none !important;
}
.filesize a
{
  color:#B6D648 !important;
}
.filetitle 
{
  background:inherit !important;
  color:#698CC0 !important;
  font-size:1.2em !important;
  font-weight:bold !important;
}
.postername 
{
  color:#C0E9F0 !important;
  font-weight:800 !important;
}
.postertrip {
	color:#228854;
}
.oldpost {
        color:#CC1105;
        font-weight:800;
}
.omittedposts {
        color:#707070;
}
.reply 
{
  -moz-border-radius:5px 5px 5px 5px !important;
background:url("data:image/png;base64,%3D%3D") no-repeat scroll right bottom #282828 !important;
  border:1px solid #757575 !important;
  color:#B0C4DE !important;
  margin:0 !important;
  padding:0 !important;
}
.doubledash {
 	vertical-align:top;
	clear:both;
	float:left;
}
.replytitle {
	font-size: 1.2em;
        color:#E1E2CF !important;
        font-weight:800;
}
.commentpostername {
  color:#C0E9F0 !important;
  font-weight:800 !important;
}
.thumb {
  border:medium none;
  float:left;
  padding:2px;
}
.thumbnailmsg {
	font-size: small;
	color:#707070 !important;
}
.abbrev {
        color:#707070;
}
.highlight {
	background:#282828 !important;
	color:#CDD8F7 !important;
	border: 2px dashed #678CB1 !important;
}

/* board list */

.category {
  font-family: sans-serif;
  font-size: small;
  margin-bottom: 8px;
  background-color: #181818 !important;
  border: none !important;
}

.header {
	background:#111111 !important;
	border: solid 1px #757575 !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px  !important;
	margin-top: 1em !important;
	padding-left: .5em !important;
	cursor: pointer !important;
	color: #B0C4DE !important;
	font-weight: normal !important;
}

.list
{
  border-right: none !important;
  border-bottom: none !important;
  border-left: none !important;
}

.list div a {
	margin-left: .5em !important;
	padding-left: .5em !important;
	border-left: solid 1px #757575 !important;
	background:#181818 !important;
}

.list div a:hover {
	background: #4C4C4C !important;
}

/* admin posts */

.adminname {
	color: #FF4A4A !important;
}

/* rules */

.rules {
        font-size:0.7em;
}

.rules table {
	font-size: 1.1em;
	margin: 5px 0px 5px 0px;
}

.prohibition {
	color: #66747B !important;
}

.prohibition ul, .info ul {
	margin: 0px;
	padding: 0px 0px 0px 20px;
}

/* title page */

.news {
    background: #111;
	color: #ff4a4a;
	padding: 0px 3px 0px 3px;
	font-weight: bold;
	text-align: center;
	margin-bottom: 5px;
    border-radius: 5px;
    border: solid 1px #757575; 
}

.newscntnr table tr td{
    border: none;
}
    
input[value="Назад"],input[value="Далее"],select[name="switcher"],input[type="password"],input[value="Удалить"]{
    background: #181818;
    color: #ddc771;
    border-radius: 5px;
    border: 0.5px solid #b0c4de;
}

input[value="Назад"],input[value="Далее"]{    
    padding:4px 4px 4px 4px;
    margin-top:1px;    
}
input[value="Назад"]:hover,input[value="Далее"]:hover,input[value="Удалить"]:hover{
    cursor: pointer;
    color: #FFF369;
}
    
select[name="switcher"]{
    margin-top:5px;
    padding:2px 2px 2px 2px;
}
select[name="switcher"] option{
    margin-top: -5px;            
}    
    
.greeting {
	text-align: center;
	font-weight: bold;
	color: #B0C4DE !important;
}

span.spoiler, span[onmouseout*="color=\'black\'"] {
  background:none repeat scroll 0 0 #575757 !important;
  color:#575757 !important;
}

span.spoiler:hover, span[onmouseover*="color='white'"]:hover {
	color: #48B0FD !important;
}

img[alt=""] {
	background: #F0F0F0 !important;
}

.captcha 
{
background-color: #181818 !important;
}

}
