css:
@-moz-document domain(forums.gamewinners.com) {

body
{
	background: #FFFFFF !important;
	color: #000000 !important;
	font: 10pt verdana !important;
	
}
a:link
{
	color: #000000 !important;
	text-decoration: none !important;
}
a:visited
{
	color: #000000 !important;
	text-decoration: none !important;
}
a:hover, a:active
{
	color: #999999 !important;
}
.page
{
	background: #FFFFFF !important;
	color: #000000 !important;
}
.page a:link
{
	text-decoration: none !important;
}
.page a:visited
{
	text-decoration: none !important;
}
td, th, p, li
{
	font: 10pt verdana !important;
}
.tborder
{
	color: #2B4C81 !important;
	border-top: thin solid #cdcdcd !important;
	border-right: thin solid #444444 !important;
	border-bottom: thin solid #444444 !important;
	border-left: thin solid #cdcdcd !important;
}
.tcat
{
	background: #dbdbdb !important;
	color: #000000 !important;
	font: bold 10pt verdana !important;
}
.tcat a:link
{
	text-decoration: none !important;
}
.tcat a:visited
{
	text-decoration: none !important;
}
.tcat a:hover, .tcat a:active
{
	text-decoration: underline !important;
}
.thead
{
	background: #2B4C81 !important;
	color: #FFFFFF !important;
	font: bold 11px verdana !important;
}
.thead a:link
{
	color: #E3E3E3 !important;
	text-decoration: none !important;
}
.thead a:visited
{
	color: #E3E3E3 !important;
	text-decoration: none !important;
}
.thead a:hover, .thead a:active
{
	color: #FFFFFF !important;
}
.tfoot
{
	background: #dbdbdb !important;
	color: #000000 !important;
}
.tfoot a:link
{
	color: #000000 !important;
	text-decoration: none !important;
}
.tfoot a:visited
{
	color: #000000 !important;
	text-decoration: none !important;
}
.tfoot a:hover, .tfoot a:active
{
	color: #999999 !important;
}
.alt1, .alt1Active
{
	background: #ECECEC !important;
	color: #000000 !important;
}
.alt1 a:link, .alt1Active a:link
{
	text-decoration: none !important;
}
.alt1 a:visited, .alt1Active a:visited
{
	text-decoration: none !important;
}
.alt2, .alt2Active
{
	background: #dbdbdb !important;
	color: #000000 !important;
}
.alt2 a:link, .alt2Active a:link
{
	text-decoration: none !important;
}
.alt2 a:visited, .alt2Active a:visited
{
	text-decoration: none !important;
}
td.inlinemod
{
	background: #FFFFCC !important;
	color: #000000 !important;
}
.wysiwyg
{
	background: #ECECEC !important;
	color: #000000 !important;
	font: 10pt verdana !important;
}
.wysiwyg a:link
{
	text-decoration: none !important;
}
.wysiwyg a:visited
{
	text-decoration: none !important;
}
textarea, .bginput
{
	font: 10pt verdana !important;
}
.button
{
	font: 11px verdana !important;
}
select
{
	font: 11px verdana !important;
}
option, optgroup
{
	font-size: 11px !important;
	font-family: verdana !important;
}
.smallfont
{
	font: 11px verdana !important;
}
.time
{
	color: #666686 !important;
}
.navbar
{
	font: 11px verdana !important;
}
.navbar a:link
{
	text-decoration: none !important;
}
.navbar a:visited
{
	text-decoration: none !important;
}
.highlight
{
	color: #FF0000 !important;
	font-weight: bold !important;
}
.fjsel
{
	background: #3E5C92 !important;
	color: #E0E0F6 !important;
}
.fjdpth0
{
	background: #F7F7F7 !important;
	color: #000000 !important;
}
.panel
{
	background: #ECECEC !important;
	color: #000000 !important;
	padding: 10px !important;
	border: 2px outset !important;
}
.panel a:link
{
	text-decoration: none !important;
}
.panel a:visited
{
	text-decoration: none !important;
}
.panelsurround
{
	background: #EFEFEF !important;
	color: #000000 !important;
}
legend
{
	color: #000000 !important;
	font: 11px tahoma, verdana !important;
}
.vbmenu_control
{
	background: #2B4C81 !important;
	color: #FFFFFF !important;
	font: bold 11px verdana !important;
	padding: 3px 6px 3px 6px !important;
	white-space: nowrap !important;
}
.vbmenu_control a:link
{
	color: #E3E3E3 !important;
	text-decoration: none !important;
}
.vbmenu_control a:visited
{
	color: #E3E3E3 !important;
	text-decoration: none !important;
}
.vbmenu_control a:hover, .vbmenu_control a:active
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_popup
{
	background: #FFFFFF !important;
	color: #000000 !important;
	border: 1px solid #0B198C !important;
}
.vbmenu_option
{
	background: #ECECEC !important;
	color: #000000 !important;
	font: 11px verdana !important;
	white-space: nowrap !important;
	cursor: pointer !important;
}
.vbmenu_option a:link
{
	color: #000000 !important;
	text-decoration: none !important;
}
.vbmenu_option a:visited
{
	color: #000000 !important;
	text-decoration: none !important;
}
.vbmenu_option a:hover, .vbmenu_option a:active
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_hilite
{
	background: #597BB3 !important;
	color: #FFFFFF !important;
	font: 11px verdana !important;
	white-space: nowrap !important;
	cursor: pointer !important;
}
.vbmenu_hilite a:link
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_hilite a:visited
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_hilite a:hover, .vbmenu_hilite a:active
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
/* ***** styling for 'big' usernames on postbit etc. ***** */
.bigusername { font-size: 12pt !important; }

/* ***** background to make the spoiler2 tag work. ***** */
.spoiler { color: #000000 !important; background-color: #000000 !important; }
.spoilertitle { color: red !important; background-color: #ECECEC !important; }

/* ***** Indent tag ***** */
span.ind { position:relative !important;left:30px !important; }

/* ***** White links ***** */
a.white:link {color: #FFFFFF}     /* unvisited link */
a.white:visited {color: #EEEEEE}  /* visited link */
a.white:hover {color: #DDDDDD}   /* mouse over link */
a.white:active {color: #FFFFFF}   /* selected link */ 

/* ***** Signature length variables ***** */
img.x { vertical-align:text-top !important; left:0px !important; top:0px !important; z-index:-1 }
img.y { vertical-align:text-top !important; right:0px !important; top:0px !important; z-index:-1 }

/* ***** small padding on 'thead' elements ***** */
td.thead, div.thead { padding: 4px !important; }

/* ***** basic styles for multi-page nav elements */
.pagenav a { text-decoration: none !important; }
.pagenav td { padding: 2px 4px 2px 4px !important; }

/* ***** define margin and font-size for elements inside panels ***** */
.fieldset { margin-bottom: 6px !important; }
.fieldset, .fieldset td, .fieldset p, .fieldset li { font-size: 11px !important; }

/* ***** don't change the following ***** */
form { display: inline !important; }
label { cursor: default !important; }
.normal { font-weight: normal !important; }
.inlineimg { vertical-align: middle !important; }
.fieldset  {
border-top: thin solid #cdcdcd !important;
border-right: thin solid #777777 !important;
border-bottom: thin solid #777777 !important;
border-left: thin solid #cdcdcd !important;
padding: .1em 1em 0em .5em !important;
background-color: #ECECEC !important;
}

.legend  {
display: inline !important;
position: relative !important;
margin-left: 2em !important;
bottom: -1em !important;
padding: 0em .2em .2em .2em !important;
font-size: xx-small !important;
font-weight: normal !important;
color: #000000 !important;
background-color: #ECECEC !important;
border-top: thin solid #cdcdcd !important;
border-right: thin solid #777777 !important;
border-bottom: thin solid #777777 !important;
border-left: thin solid #cdcdcd !important;
}

.textbutton  {
border-top: thin dashed #DDDDBB !important;
border-right: thin dashed  #DDDDBB !important;
border-bottom: thin dashed  #DDDDBB !important;
border-left: thin dashed #DDDDBB !important;
font-size: 10pt !important;
font-style: italic !important;
font-family: 'Trebuchet MS', verdana,arial,helvetica,sans-serif !important;
letter-spacing: 0.15em !important;
padding: .1em .5em .1em .5em !important;
background-color: transparent !important;
}

}
