/* ==UserStyle==
@name           Huawei DG8045 ADSL Router
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */
@-moz-document url-prefix("https://192.168.1.1/") {
    body {
        -webkit-text-size-adjust: 100%;

        -moz-text-size-adjust: 100%;
        -ms-text-size-adjust: 100%;
        text-size-adjust: 100%;
        font-family: -apple-system, BlinkMacSystemFont, 'Open Sans', HelveticaNeue, sans-serif;
        color: rgb(76, 124, 238);
        word-wrap: break-word;
        line-height: 1.28;
        -webkit-font-smoothing: subpixel-antialiased;
        -moz-osx-font-smoothing: auto;
    }
    body {
        background: #303030;
        color: rgb(58, 57, 251);
        font: 13px/1.231 arial, helvetica, clean, sans-serif;
        *font-size: small;
        *font: x-small;
    }
    .product_title{
	text-shadow: 1px 1px 5px #fff, 3px 2px aliceblue;
}
    .top_div {
        border-radius: 5px;
        background: #0f0;
    }
    #loginusername:before {
        content: "Welcome: ";
        color: #008000;
        text-shadow: 0 0 5px #fff;
    }
    #loginusername {
        color: rgb(58, 57, 251);
        font-weight: bold;
        font-size: 1.1em;
    }
    #signout_ctrl{
        font-weight: bold;
        background: antiquewhite;
        font-size: 1.0em;
    }
    #signout_ctrl span::after{
        content: " âžª"
    }
    #lzt-logo {
        width: 68px;
        height: 50px;
        float: left;
        margin-top: 4px;
        margin-left: -5px;
        background: url(https://i.imgur.com/PJaUHsY.png) no-repeat;
        background-size: 100%;
    }
    #index_show_status{
        font-weight: bold;
    }
    #homenetwork_settings_menu div::before, #homenetwork_settings_menu span::before{
        content: "\1F517   ";
        display: inline
    }
    #internet_settings_menu div::before, #internet_settings_menu span::before{
        content: "\01F310   ";
        display: inline
    }
    #sharing_settings_menu div::before, #sharing_settings_menu span::before{
        content: "\1F4E8  ";
        display: inline
    }
    #maintain_settings_menu div::before, #maintain_settings_menu span::before{
        content: "\1F527   ";
        display: inline
    }
    #home_menu div::before, #home_menu span::before{
        content: "\1F3E0   ";
        display: inline
    }
    .linkcolor, .change_del{
        font-weight: bold;
        padding:2px;
        border-radius:3px;
        border: 1px solid brown;
        background: #ecd6d2
    }
    .linkcolor:hover, .change_del:hover{
        color: #2d587c;
         background: #d5ab22;
        text-shadow: 0 1px 2px rgba(0,0,0,0.5);
        cursor:pointer;
    }
    .div_ssid_part,  .div_six_part{
        color: green;
        font-weight: bold;
        font-size: larger;
    }
    .div_six_part{
       /* text-indent: -121px;*/
        line-height: 0; /* Collapse the original line */
    }
    .div_six_part::after{
        content:"\001BE4";
        text-indent: 0;
        display: inline;
        line-height: initial; 
        color:blue;
        visibility:visible
    }
    #InternetGatewayDevice_LANDevice_1_WLANConfiguration_2__name::after{
        content:"2 \001BE4";
    }
    #InternetGatewayDevice_LANDevice_1_WLANConfiguration_3__name::after{
        content:"3 \001BE4";
    }
    #InternetGatewayDevice_LANDevice_1_WLANConfiguration_4__name::after{
        content:"4 \001BE4";
    }
    #InternetGatewayDevice_LANDevice_1_WLANConfiguration_2__name, #InternetGatewayDevice_LANDevice_1_WLANConfiguration_3__name, #InternetGatewayDevice_LANDevice_1_WLANConfiguration_4__name{
        visibility:hidden;
    }
    #gate_info .ember-view, #index_show_status{
        background-color:#fdf4c5;
    }
    .alert{
        padding:5px;
        margin:10px;
        font-weight:bold;
        min-height:35px;
    }
    .alert label{
        font-weight: bold;
        font-size: large;
        line-height: 34px;
    }
    #user_restore, #user_find{
        font-weight: bold;
        line-height: 1.7em;
        
    }
    #user_restore div.text_left, #user_find .getaccount_info{
        background: #fdf4c5;
        padding: 5px;        
    }
    #i18n-6::before{
        content: '\0026A0';
        font-size: x-large;
        padding-right: 5px;
        font-weight: bold;
        text-shadow: -1px -1px 2px #48232a;
    }
    #input_username_pass *{
        font-size: large;
    }
    #login_window .fontsize_18{
        font-size: xx-large;
        text-align: center;
        letter-spacing: 2px;
    }
    
    footer span{
        color: burlywood;
    }
    footer a span{
        color: cyan;
    }
    footer a span::after{
        content:'\00279A';
        font-size:large;
    }
    footer a:hover span{
        color: greenyellow;
    }    
    body,
    div,
    dl,
    dt,
    dd,
    ul,
    ol,
    li,
    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    pre,
    code,
    form,
    fieldset,
    legend,
    input,
    button,
    textarea,
    p,
    blockquote,
    th,
    td {
        margin: 0;
        padding: 0;
    }
    * {
        text-rendering: optimizeLegibility;
        -webkit-font-smoothing: antialiased;
        -moz-osx-font-smoothing: grayscale;
    }
    body {
        display: block;
        margin: 8px;
    }
    
    body::-webkit-scrollbar {
        width: 15px;
    }

    body::-webkit-scrollbar-thumb {
        background-color: rgb(148, 148, 148);
        border: 3px solid rgb(0, 0, 0);
        border: 3px solid rgba(0, 0, 0, 0);
        _border: 3px solid rgb(0, 0, 0);
        background-clip: padding-box;
        border-radius: 10px;
    }
    body::-webkit-scrollbar-track {
        background-color: #303030;
    }
}
