/* ==UserStyle==
@name           The Unbeveling
@namespace      youtube.com
@version        1.5.0
@description    YouTube with squares
@author         Wrapfield
==/UserStyle== */

@-moz-document domain("youtube.com") {
/*
I recommend this style in tandem with YouTube for Normal People https://userstyles.world/style/6258/youtube-for-normal-people

I would put YTfNP in here, but church & state and all that...
*/

/*# thumbnails*/
ytd-thumbnail[size=large] a.ytd-thumbnail, ytd-thumbnail[size=large]:before {
    border-radius:3px;
}
.ytp-videowall-still-round-medium .ytp-videowall-still-image {
    border-radius:0;
}
.ytp-ce-video.ytp-ce-large-round, .ytp-ce-playlist.ytp-ce-large-round {
    border-radius:1px;
}
ytd-thumbnail[size=medium] a.ytd-thumbnail, ytd-thumbnail[size=medium]:before{
    border-radius:3px;
}


/*# avatars*/
/*navbar*/
#avatar{
    border-radius: 3px!important;
}
yt-img-shadow.ytd-topbar-menu-button-renderer {
    border-radius: 3px!important;
}
/*posts, subcommentors, comment teaser*/
#author-thumbnail.ytd-post-renderer yt-img-shadow.ytd-post-renderer, ytd-comment-replies-renderer #creator-thumbnail.ytd-comment-replies-renderer yt-img-shadow.ytd-comment-replies-renderer,#hearted-thumbnail.ytd-creator-heart-renderer, ytd-comments-entry-point-header-renderer:not([modern-metapanel]) .avatar.ytd-comments-entry-point-header-renderer{
    border-radius:3px!important;
}
/*subscriptions section*/
yt-img-shadow.ytd-guide-entry-renderer{
    border-radius:3px!important;
}
/*fullsize posts*/
#author-thumbnail.ytd-backstage-post-renderer yt-img-shadow.ytd-backstage-post-renderer{
    border-radius:3px!important;
}
/*commenters*/
#author-thumbnail.ytd-comment-renderer yt-img-shadow.ytd-comment-renderer,#author-thumbnail.ytd-comment-simplebox-renderer, #author-thumbnail.ytd-commentbox, ytd-commentbox[is-reply][is-backstage-comment] #author-thumbnail.ytd-commentbox{
    border-radius:3px!important;
}
/*enlarge subcommenters*/
ytd-comment-renderer:not([comment-style=backstage-comment])[is-reply] #author-thumbnail.ytd-comment-renderer yt-img-shadow.ytd-comment-renderer{
    width:40px;
    height:40px;
    margin-right:10px;
}



/*# search bar*/

/*

it seems they went back on the search bar thing (at least for me). i guess even google knew it was too much. I'll put on a search box beveler for the magnifying edge like the old screenshot.

*/
#search-icon-legacy.ytd-searchbox{
    border-top-right-radius:20px;
    border-bottom-right-radius:20px;
}


/*## configurable*/
/*left side*/
ytd-searchbox[desktop-searchbar-style=rounded_corner_autofocus] #container.ytd-searchbox{
    border-top-left-radius:3px;
    border-bottom-left-radius:3px;
}
/*right side*/
ytd-searchbox[desktop-searchbar-style=rounded_corner_autofocus] #search-icon-legacy.ytd-searchbox{
    border-top-right-radius:3px;
    border-bottom-right-radius:3px;
}

/*## left alone*/
ytd-searchbox[desktop-searchbar-style=rounded_corner_autofocus] #container.ytd-searchbox{/*left side*/
    border-top-right-radius:0;
    border-bottom-right-radius:0;
}
ytd-searchbox[desktop-searchbar-style=rounded_corner_autofocus] #search-icon-legacy.ytd-searchbox{/*right side*/
    border-top-left-radius:0;
    border-bottom-left-radius:0;
}
ytd-searchbox[has-focus][desktop-searchbar-style=rounded_corner_autofocus] #container.ytd-searchbox{
    border-bottom-left-radius:0;
    border-bottom-right-radius:0;
    
    
}
/*# search box*/
.sbdd_b {
    border-top-left-radius:0;
    border-top-right-radius:0;
    border-bottom-left-radius:3px;
    border-bottom-right-radius:3px;
}
.gstl_50{
    top:47px!important;
}

/*# etc*/
/*categories*/
yt-chip-cloud-chip-renderer{
    border-radius:3px;
    margin: 3px!important;
}
/*that branding thing in the corner of videos (can you believe they never rounded that *at all*, and just left it to be a perfect square?)*/
.iv-branding .branding-img-container img{
    border-radius:3px;
}
/*## notifications*/
ytd-multi-page-menu-renderer[menu-style=multi-page-menu-style-type-comments], ytd-multi-page-menu-renderer[menu-style=multi-page-menu-style-type-notifications] {
    border-radius:3px;
    opacity:95%;
}
/*thumbnails*/
.thumbnail-container.ytd-notification-renderer{
    border-radius:3px;
    opacity:100%;
}
/*avatars*/
.thumbnail.ytd-notification-renderer{
    opacity:100%;
}
.text.ytd-notification-renderer{
    opacity:100%;
}
/*notification badge*/
.yt-spec-icon-badge-shape__badge{
    border-radius:5px;
}
}
