css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url-prefix('chrome://') {

#menu-button {-moz-appearance: none !important;
margin: 0px !important;
padding: 0px !important;
border: none !important;
list-style-image: url("data:image/png;base64,") !important;}

#menu-button {-moz-image-region: rect(0px 32px 24px 0px) !important;}
#menu-button:hover {-moz-image-region: rect(24px 32px 48px 0px) !important;}
#menu-button[open="true"] {-moz-image-region: rect(48px 32px 72px 0px) !important;}

}
