/* Randall Barker */
/* The original theme had to be rewritten due to a roblox theme update */
/* Some pages may have issues with the CSS on them */

@-moz-document domain("roblox.com")
{
  .ad, .ad *, .img_ad, .ad-annotations, #google_image_div, #aw0, #AdvertisingLeaderboard
  {
    display: none !important;
  }

  .ad, .ad *, .img_ad, .ad-annotations, #google_image_div, #aw0, #AdvertisingLeaderboard
  {
    display: none !important;
  }

  #fb-root
  {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url(https://cdn.wallpapersafari.com/43/74/T27FKP.jpg) !important;
    background-size: cover !important;
    background-attachment: fixed !important;
  }
/* .rbx-header{
  background-color: black !important;
}
.nav-menu-title, .age-bracket-label, .nav-robux-amount, .text-header{
  color: white !important;
}
.icon-logo-r{
  background-image: url(https://cdn.wallpapersafari.com/43/74/T27FKP.jpg) !important;
} */
  .content
  {
    background-color: transparent !important;
  }

  .home-friends .people-list
  {
    background-color: rgba(255,255,255,0.8) !important;
    border-radius: 15px !important;
    color: black !important;
  }

  .home-header-content
  {
    color: white !important;
  }

  .game-cards .game-card
  {
    background-color: rgba(255,255,255,0.8) !important;
    border-radius: 15px !important;
    white-space: pre-line !important;
  }

  .game-card-link .game-cards .game-tile
  {
    padding: 3.5px !important;
  }

  li.list-item.game-card.game-tile.ng-scope:Hover, li.list-item.game-card.game-tile:Hover
  {
    box-shadow: 5px 5px 10px -3px rgba(0,0,0,0.75) !important;
  }

  .game-tile-container .game-card-thumb
  {
    border-radius: 5px !important;
  }

  .game-card-container
  {
    text-align: center !important;
  }

  .bg-gray-4
  {
    background-color: transparent !important;
  }

  .game-card-link .game-card-thumb-container
  {
    padding: 7px !important;
  }

  .game-card-thumb-container:before
  {
    display: none !important;
 /*Remove Hover Over Darken on games*/;
  }

  .game-card-container .game-card-thumb-container
  {
    background-color: transparent !important;
  }

  .tab-content, .left-wrapper-placeholder .left-wrapper .section-content
  {
 /*for avatar page*/
    border-radius: 5px;
    background-color: white;
  }

  .col-xs-12.section-content.game-main-content
  {
    background-color: white;
    border-radius: 5px;
  }

  .carousel-inner
  {
    width: 95% !important;
    margin: auto !important;
    top: 5% !important;
    border-radius: 7px !important;
  }

  .game-main-content .game-calls-to-action .game-title-container
  {
    padding: 20px 12px 0 0 !important;
  }

  .gig-bar-container.gig-share-bar-container
  {
    visibility: hidden !important;
 /* hide social share buttons*/;
  }

  .text.game-description.linkify
  {
    padding-left: 20px;
  }

  .games-list-container
  {
    background-color: #ffffff00 !important;
  }
}
