css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(vk.com), domain(vkontakte.ru) {
  * { font-family: Liberation Sans !important; }
  #pageLayout { width: 100% !important; }
  #pageHeader { width: 100% !important; background-image: none !important; }
  #pageBody { width: 80% !important; }
  #checkboxFeed { width: 15em !important; }
  #userProfile { background-image: none !important; }
  #sideBar { width: 12em !important; }
  #leftColumn { width: 33% !important; }
  #rightColumn { width: 65% !important; }
  .friendTable td { width: 7em !important; }
  .profileTable .dataWrap { width: 100% !important; }
  .feedStory { width: 100% !important; }
  .fSub .fDetails { width: 75% !important; }
  #nav li#myprofile a.hasedit { width: 70% !important; }
  #nav li#myprofile a.edit { width: 20% !important; }
  #group.profile .left { width: 70% !important; }
  #group.profile .dataWrap { width: 100% !important; }
  .wallpost .info .text { width: 100% !important; }
  #filter0 { width: 10em !important; }
}
