    font-size: 14px;
    border: 1px solid #ff5757;
    border-radius: 20px;
}

.pagination__pages > span:not(.nav_ext), .pagination__pages-btn > span {
    background: #3c3c3c;
    color: #ffffff;
}

.mobile-menu {
    width: 280px;
    height: 100%;
    overflow-x: hidden;
    overflow-y: auto;
    padding: 10px 20px;
    z-index: 999;
    position: fixed;
    left: -320px;
    top: 0;
    transition: left .4s;
    background: #181818;
    color: #787878;
}

.mobile-menu .header__menu > li > a {
