@namespace url(http://www.w3.org/1999/xhtml);

/* Last modified: 2012-09-03T03:24:02 UTC-04:00
 * DocumentName: PMoCB60_SB_NYPMBlue.UserStyle.css
 * StylishName: "cb60@NYPMBlue"
 * DocumentType: text/css
 * CSSLevelReq: CSS Level 3 + (selectors)
 * DirectPurpose: restyle the presentational characteristic for
 * the Firefox browser (and related browsers) of the Chatterbox CB60
 * in the browser Sidebar (See URI www.perlmonks.org/?node_id=236794#history)
 * AuthorAndCopyrightHolder: Sören Andersen [Intrepid]
 * Email: <somian08@gmail.com>
 * HomePage: https://github.com/somian/FFx-userstyles (GitHub repository)
 * SrcKitArchive: https://github.com/somian/FFx-userstyles/zipball/master
 * LicenseAndWarrantyDisclaimer: see bottom of document.
 *     Uploaded to GitHub on 2012-09-03
 */


@-moz-document
   domain("mini-cb60.datenzoo.de"),
   domain("mini-cb60.flux8.com")
{

  body , dl > dt, h1 > a[title="Perlmonks Fullpage Chat"]:link:visited
      {
             color             : #edddA2 !important;
             background-color  : #00002a !important;
      }

  hr  {
          color : #0F0  !important ;
          background-color : #0F0  !important ;
          height : 1px !important;
      }
 
  hr + p { display : none !important; }
  a[href="http://mini-cb60.flux8.com/?showform=yes#bottom"],
  a[href="http://mini-cb60.datenzoo.de/?showform=yes#bottom"]
      {
           display : none !important
      }

  p > small > a[href="http://mini-cb60.flux8.com/#bottom"],
  p > small > a[href="http://mini-cb60.datenzoo.de/#bottom"],
  p > small > a[href="http://mini-cb60.flux8.com/#top"],
  p > small > a[href="http://mini-cb60.datenzoo.de/#top"]
      {
     font-size  :x-small !important;
       color    :#578bc2  !important;
     text-decoration  : overline underline !important;
          margin-left : -0.4ex !important;
      }


  a         { text-decoration :none !important }
  a:visited { color : #FBE604 !important;} /* #A4EE9C or #AB684C */
  a:active  { color : #FBB30C !important;}
  a:hover
      {
                 color : #FBE604 !important;
       text-decoration : underline !important;
      }

  h1
      {
          font-size   : small  !important;
          font-family :
           "Lucida Handwriting", serif  !important;
      }

  h1 > a:first-child
      {
         text-decoration :overline underline  !important;
         padding-bottom  :2px !important;
         font-size       :medium !important;
         font-variant    :small-caps !important;
         text-shadow : 0 0 0.3em #AA0033,  0 0 0.3em #AA0033,  0 0 0.3em #000033 !important;
      }

  h1>a:first-child:before { content :"| "  !important; color :#FBE604 !important; }
  h1>a:first-child:after  { content :" |"  !important; color :#FBE604 !important; }

  h1 > a[title="Click to refresh"]:link:visited
      {
                   color : #578bc2  !important;
      }
  h1 > a[title="Click to refresh"]:hover
      {
                   color : #578bc2  !important;
         text-decoration : none  !important;
      }
  h1 > a[title="Perlmonks Fullpage Chat"]:link:visited,
  h1 > a[title="Perlmonks Fullpage Chat"],
  h1 > a:first-child:link:visited
      {
          font-family :
           "Century Gothic", sans-serif  !important;
          font-size   : medium  !important;
          letter-spacing : 0.2px  !important;
             color   : #0F0 !important;
      }
  h1 > a[title="Perlmonks Fullpage Chat"]:hover
      {
         text-shadow : 0 0 0.3em #AAAAAA,  0 0 0.3em #AAAAAA, 0 0 0.3em #AAAAAA !important;
         color       : #edddA2 !important;
      }

  h1 + small {  font-size : xx-small   !important;  }


/* chatter */
  dl > dd
      {
          font-family : verdana, avantgarde, "avant-garde", sans-serif !important;
            font-size : small !important;
          margin-left : 0ex !important;
         margin-right : 1px    !important;
        margin-bottom : 0.9ex !important;
      }

  dl > dd tt  /* code */
      {         font-family :
           "envy code r", "inconsolata", monospace !important;
                  font-size : 11pt  !important;
            color : #FFAAFF  !important;
            white-space  : pre-wrap  !important;
         margin-left : -0.5ex !important;
      }


  dl > dd tt:before  /* code */
      {
            content : "◊ "   !important;
      }

   dl > dd > a         ,
   dl > dd > a.link    ,
   dl > dd > a.visited
      {
          color  :#5d94cf !important;
      }

   dl > dd > a:before { color  :#FF5CD6 !important ;content :"["  !important; }
   dl > dd > a:after  { color  :#FF5CD6 !important ;content :"]"  !important; }


/* usernames and timestamps */
  dl > dt > small
      {
          font-size    : xx-small !important;
          font-family  : "dejavu sans" !important;
          font-stretch : extra-expanded !important;
               color   : #679423 !important;
          background-color : #00002A !important;
              margin-left  : -20px !important;
      }

  dl > dt > a[href^="?node_id="]
      {
                color  : #ed8a47 !important;
             font-size :  10pt  !important;
            font-style : italic !important;
      }
                   
  dl > dt > a:after  { content  :" |" !important; }
   
  dd > i > a[href^="?node_id="]:link:visited,
  dd > i >               a[href^="?node_id="]
      {
                     /* style /me ...does... actions */
                 color : #5d94cf !important;
      }
  dd > i > a[href^="?node_id="]:before { color  :#FF5CD6 !important ;content :"["  !important; }
  dd > i > a[href^="?node_id="]:after  { color  :#FF5CD6 !important ;content :"]"  !important; }

}

