@-moz-document domain("hattrick.org") {




/*pÃ¤Ã¤palkit*/

#ctl00_ctl00_CM_CI_pnlNormal {

  display: none !important;

}

#hattrick,#hattrickNoSupporter 
{
        margin:auto!important; 
        position:relative;
        left:-25px;width:993px!important;
}

h1 {

  color: #4289c1 !important;

  border-bottom: solid thin darkgrey !important;

  background: none !important;

}

h2 {

  color: #000000 !important;

  border-bottom: solid thin darkgrey !important;

  height: 16px !important;

  padding-top: 3px !important;

  background: url(http://i32.tinypic.com/w9taar.jpg) !important;

}



h3 {

  color: #439ddc !important;

  border-bottom: solid thin darkgrey !important;

  background: none !important;

}



a

{

        color: #006699 !important;

	text-decoration: none !important;

}



a:hover

{

	color: #439ddc !important;

}



.date

{

	color: #4289c1 !important;

        font-size: xx-small !important;

	text-decoration: underline !important;

	padding-right: 3px !important;

        margin-left: 2px !important;

	font-weight: 400;

}



/*logo*/

#logo {

  background: none !important;

}



/*...*/

#infoBar a {

  background-color: black !important;

  color: black !important;

}



/*ilmotusjuttu*/

#infoBar table {

  background: black !important;

  -moz-border-radius: 2%;

}



/*ilmotusjuttu*/

#ticker div {

  background: none !important;

}



body {  

  background: #083d1c url(http://i26.tinypic.com/2mgl4c4.png) !important;

}




#top
{
	height: 10px !important;
	position: relative !important;
	z-index: 50 !important;
        background: none !important;
}



#header 
{
        background: url(http://i28.tinypic.com/2v0mrt1.png) !important;
	height: 103px !important;
	padding-top: 50px !important;
}


#footer a
{
        color: #ffffff !important; 
}

/*fondo pagina arrotondato*/
div[id="bottom"]
{
        background: url(http://i26.tinypic.com/105wk6h.png) !important;
}


/* central page background */
#content
{

        background: url(http://i26.tinypic.com/14y1ylz.jpg) !important;

}



#menu {

	height: 25px !important;

	margin-top: 5px !important;

        background: none !important;

}

#menu a {

	line-height: 25px !important;

        background: none !important;

}



div#menu a
{
        color:#000000 !important;
}



div#menu a:hover 
{
        color:#ffffff !important;
}



#ticker

{

	position: absolute !important;

	top: 45px !important;

	left: 470px !important;

}



#ticker div a
{
	padding-bottom: 2px !important;
	color: #000000 !important;
	text-decoration: none !important;
	line-height: 1.1em !important;
}



#shortcuts

{

	margin-top: -4px !important;

	margin-right: 2px !important;

}

img.scNewMail 
{
	background: transparent url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/Newmail.png) no-repeat !important;
}

#teamLinks

{

        color: #000000 !important;

        margin-top: 15px !important;

	margin-left: 12px !important;

}



div#teamLinks a

{

        color: #ffffff !important;

}



/*Users online - week - date - time*/



div[id="online"] 

{

        color: #000000 !important; 

        float:right!important;

        margin-right:80pt!important;

        margin-top:-68pt!important;

}

div[id="time"]

{

        color: #ffffff !important;

        float:right!important;

        margin-right:5pt!important;

        margin-top:10pt!important;

}



/*---------Forum---------*/

.cfHeader

{

        background:  url(http://i30.tinypic.com/eamxjq.jpg) !important;

}



#cfPollHeader

{

	background: url(http://i30.tinypic.com/eamxjq.jpg) !important;

	padding: 5px 10px;

	font-weight: bold;

	color: #000000;

}



/* cornice mess. forum*/
.cfWrapper

{

	border: solid 1px #000000 !important;

	margin: 0 0 12px 0;

}



/* forum mess. nome e serie */
.cfHeader a

{

	color: #000000 !important;

	font-weight: bold;

	text-decoration: none;

}



.cfWrapper a

{

	text-decoration: none;

        /*color: #000000 !important;*/

}



.cfUserInfo a

{

        color: #000000 !important;

}



.cfMessage a

{

	/*color: #000000 !important;*/

}



/*Foxtrick pop-up menu*/



span.myht1:hover div.myht2 {

display: inline;

position: absolute;

left: 20px;

background-color: #ffd900 !important;

border: solid 1px #000000 !important;

padding: 0px;

z-index: 999


}



div.playerInfo {

overflow: visible !important;

}



span.myht1 table>tr>td>a {

font-weight: normal !important;

text-decoration: none !important;

color: #000000 !important;

}



span.myht1 table>tr>td:hover {

background-color:#d9b900 !important;

}



div[class*="ad_"]

{

        display:none !important;

}

