css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("tvtropes.org") {

/* hidden */
#wikicmds script, 
#wikicmds input.hidden, 
#wikicmds style	{display:none !important}


/* logo */
#wikilogo	{line-height:10px !important;margin-left:180px !important;z-index:888}
#wikilogo * 	{text-align:center !important;display:inline !important}
#wikilogo #searcher	{position:absolute !important;top:1em !important;left:200px !important;z-index:999}


/* left/top main navigation links */
#wikileftpage h1, 
div.buttonframe, 
div.buttoninterior	{-moz-border-radius:0 !important;background:#f0f0f0 !important;border:0 !important;color:#444 !important;cursor:pointer !important;font-family:sans-serif !important;font-size:15px !important;margin:.22em 0 !important;text-shadow:#fff 1px 1px !important}
#wikileftpage h1 a	{color:#000 !important;font-size:15px !important;text-shadow:#fff 1px 1px !important}
#wikileftpage h1 a:visited	{color:#444 !important}
#wikileftpage h1 a:hover	{color:#000 !important;font-size:15px !important}
#wikileftpage	{position:relative !important;top:-1em !important}


/* google search */
#wikicmds *, div.cse-branding-bottom	{display:inline !important}
#wikicmds	{text-align:right !important;display:inline !important;float:right !important}
div.cse-branding-bottom	{position:relative !important;right:-1px !important}
div.cse-branding-logo img	{width:0px !important;height:0px !important}
div.cse-branding-text	{width:0px !important;height:0px !important;font-size:0px !important}


/* link pane: new edits, forum, hitory etc. */
#wikilogo center table	{display:inline !important;float:right !important}
div.buttonframe *	{background:none !important;font-size:11px !important;font-weight:500 !important;color:#000 !important;text-shadow:#fff 0px 0px !important}
#wikilogo div	{background:none !important}



/* title, heading, alternative titles */
div.pagetitle, div.alt_title	{border:0 !important;color:#999 !important}
div.pagetitle	{color:#000 !important;font-family:sans-serif !important;font-size:22px !important;padding:4em 1em 0 1em !important;text-align:center !important}
div.alt_title	{padding-bottom:2em !important;text-align:center !important;color:#fff !important;font-size:0px !important;text-indent:-26px !important}
div.alt_title i	{color:#000 !important;font-style:normal !important;font-size:11px !important}
div.pagetitle span	{color:#000 !important;font-family:sans-serif !important;font-size:32px !important;letter-spacing:-1px}



/* link pane: make to do, send a pm etc. */
#watchmsg, #watchmsg *	{background:none !important;border:none !important}
#watchmsg	{text-align:right !important;position:absolute !important;right:12px;top:3.2em}
#wikititle div.namespacebuttons	{display:inline !important;float:right;position:relative !important;right:6px !important}



/* actual text page */
#wikitext 	{padding:0 2em !important;line-height:125% !important;max-width:55em !important;margin:0 auto !important;text-align:justify !important;font-size:90% !important}
#wikitext ul li 	{border:0px !important;padding: 2px 0 2px 0 !important}

div.indent	{font-size:12px !important;color:#444 !important;line-height:120% !important;padding-bottom:1em !important}

img.embeddedimage	{margin:0 20px !important}

div.acaption,div.acaptionright	{border:0 !important;padding-bottom:4em !important;font-size:small !important;margin:0 20px !important}
div.quote,div.quoteright	{border:0 !important;padding:0 !important;margin:0 20px !important}


table.pathholder	{border:0px !important;background:none !important}
hr	{border-style:solid !important;margin:2em 0 !important;display:block !important}
.spoiler	{border-bottom:1px dotted #f00 !important;border-width:0 0 1px 0 !important;color:black !important}
li span.asscaps	{font-variant: small-caps}



/* links */
#wikibody a	{color:#00c !important;text-decoration: underline !important}
#wikitext a:visited	{color:#551a8b !important}
#wikitext a:hover	{color:#00f !important}

#wikitext a:hover,
#wikitext a:hover	{color:#00f !important}

#wikitext div.indent a:link,
#wikitext div.acaption a:link	{color:#000 !important}

#wikitext div.indent a:visited,
#wikitext div.acaption a:visited	{color:#777 !important}

#wikitext div.indent a:hover,
#wikitext div.acaption a:hover	{color:#00e !important}

}
