css:
@-moz-document domain("play.pokemonshowdown.com")
{
	@font-face {
		font-family: 'Abel';
		font-style: normal;
		font-weight: 400;
		src: url(https://fonts.gstatic.com/s/abel/v18/MwQ5bhbm2POE2V9BPQ.woff2) format('woff2');
		unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
	}

  .statbar { font-size: 1.2em; transform: translateY(-8px); }

  .backdrop { opacity: 1 !important; }

  .leftbar { background-color: transparent; border: none; }

  .rightbar { background-color: transparent; border: none; }

  .trainer {
    text-align: center;
    position: absolute;
    left: 0;
    right: 0;
    bottom: 75px;
  }

  .trainer-far { bottom: 75px; }

  .trainer strong {
    display: block;
    font-size: 8pt;
    margin-bottom: 2px;
    word-wrap: break-word;
    color: #fff;
    text-shadow: -1px -1px #000, 0 -1px #000, 1px -1px #000, 1px 0 #000, 1px 1px #000, 0 1px #000, -1px 1px #000, -1px 0 #000;
  }

  .statbar.rstatbar {
    background-image: url("/*[[rpaint]]*/");
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    width: 181px;
    box-sizing: border-box;
    height: 80px;
    padding-top: 11px;
    transform: translateY(-11px);
  }
  .statbar.rstatbar strong { margin: 0 2px 0 10px; }
  .statbar.rstatbar .hpbar {  margin-left: 10px; }

  .statbar.lstatbar {
    background-image: url("/*[[lpaint]]*/");
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    width: 197px;
    box-sizing: border-box;
    height: 80px;
    padding-top: 18px;
    transform: translateY(-18px);
  }
  .statbar.lstatbar strong {  margin: 0 20px 0 8px; }
  .statbar.lstatbar .hpbar { margin-left: 8px; }

	.statbar strong {
		display: flex;
    align-items: center;
		margin: 0;
		color: #fff;
		font-weight: 400;
    letter-spacing: -1px;
    text-align: start;
		text-shadow: -1px -1px #000, 0 -1px #000, 1px -1px #000, 1px 0 #000, 1px 1px #000, 0 1px #000, -1px 1px #000, -1px 0 #000;
	}
  .statbar strong small {
    color: #ebc465;
    font-weight: 600;
	}
  .statbar strong small:nth-child(1) { margin-left: auto; }

	.statbar img { margin-left: auto; }
	.statbar img[alt~="M"] {
		content: url("https://i.imgur.com/qZTiMw3.png");
		width: 13px;
    height: 13px;
	}
	.statbar img[alt~="F"] {
		content: url(https://i.imgur.com/aAyWAi2.png);
		width: 13px;
    height: 13px;
	}

  .statbar .hpbar {
    position: absolute;
    border: 1px solid #161616;
    border-width: 1px 1.5px 0px 2.5px;
    background: #161616;
    padding: 1px;
    height: 10px;
    border-radius: 8px 3px 10px 8px !important;
    transform-style: preserve-3d;
    transform-origin: left;
    transform: perspective( 380px ) rotateY( -48deg ) translateX(-3px);
  }

  .statbar .hpbar .hptext {
    position: absolute;
    color: #EEEEEE;
    text-align: center;
    left: 20px !important;
    top: 7px;
    right: unset;
    font-size: 12px;
    background: none;
    text-shadow: -1px -1px #000, 0 -1px #000, 1px -1px #000, 1px 0 #000, 1px 1px #000, 0 1px #000, -1px 1px #000, -1px 0 #000;
    transform: rotateY(48deg) translateZ(24px);
    font-weight: bold;
  }

	.statbar .hpbar .hptextborder { display: none !important; }

  .statbar .hpbar .hp {
    height: 6px;
    border-top: 1px solid rgba(0,255,8,1);
    background: linear-gradient(90deg, rgba(163,255,26,1) 0%, rgba(0,255,8,1) 100%);
    border-bottom: 1px solid rgba(0,255,8,1);
    border-right: 1px solid rgba(0,255,8,1);
    border-radius: 8px 3px 10px 8px !important;
  }

  .statbar .hpbar .hp-yellow {
    border-top-color: rgba(254,170,50,1);
    background: linear-gradient(90deg, rgba(255,250,31,1) 0%, rgba(254,170,50,1) 100%);
    border-bottom-color: rgba(254,170,50,1);
    border-right-color: rgba(254,170,50,1);
  }

  .statbar .hpbar .hp-red {
    border-top-color: #dc1509;
    background: #dc1509;
    border-bottom-color: #dc1509;
    border-right-color: #dc1509;
  }

  .statbar .hpbar .prevhp {
    background: #3a3a3a;
    height: 8px;
    border-radius: 8px 3px 10px 8px !important;
  }
  .statbar .hpbar .prevhp-yellow { background-color: #3a3a3a; }
  .statbar .hpbar .prevhp-red { background-color: #3a3a3a; }

  .statbar .status {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    min-height: 10px;
    font-size: 8pt;
    color: #fff;
    text-transform: uppercase;
    position: absolute;
    width: 135px;
    transform: rotateY( 48deg ) translate(57px, 3px) scale(0.8);
  }

  .statbar span {
    padding: 1px 3px;
    border: 1px solid #f40;
    color: #fff !important;
    border-radius: 0;
  }

  .statbar span.bad, .statbar span.good {
    margin: -1px 1px;
  }

  .statbar span.bad {
    border-color: #4c0603;
    background: #a61000;
    text-shadow: -1px -1px #4c0603, 0 -1px #4c0603, 1px -1px #4c0603, 1px 0 #4c0603, 1px 1px #4c0603, 0 1px #4c0603, -1px 1px #4c0603, -1px 0 #4c0603;
  }
  .statbar span.good {
    background: #00a636;
    border-color: #034c16;
    text-shadow: -1px -1px #034c16, 0 -1px #034c16, 1px -1px #034c16, 1px 0 #034c16, 1px 1px #034c16, 0 1px #034c16, -1px 1px #034c16, -1px 0 #034c16;
  }

  @keyframes statusBlink {
    0%   { opacity: 1; }
    30%  { opacity: 0; }
    40%  { opacity: 0; }
    70% { opacity: 1; }
  }

  .statbar span.par, .statbar span.frz, .statbar span.brn, .statbar span.psn, .statbar span.slp {
    opacity: 1;
    animation: statusBlink 3s infinite;
    animation-delay: 2s;
    position: absolute;
    right: 13px;
    top: -36px;
    height: 15px;
    background: none;
    color: transparent;
  }

  .statbar span.par { content: url(https://i.imgur.com/377GRJI.png); }
  .statbar span.frz { content: url(https://i.imgur.com/cInxMZi.png); }
  .statbar span.brn { content: url(https://i.imgur.com/jXOVtFy.png); }
  .statbar span.psn { content: url(https://i.imgur.com/E4TZOib.png); }
  .statbar span.slp { content: url(https://i.imgur.com/9tny9FO.png); }

	/*****************
  * Battle Weather *
  *****************/
  .sunweather, .sunnydayweather, .desolatelandweather {
    background: url(http://i.imgur.com/DNMtOWR.gif) scroll left top;
    background-size: cover;
  }
  .rainweather, .raindanceweather, .primordialseaweather {
    background: url(http://i.imgur.com/NQqkU6j.gif) scroll left top;
    background-size: cover;
  }
  .deltastreamweather {
    background: url(http://i.imgur.com/OEB3hum.gif) scroll left top;
    background-size: cover;
  }
  .hailweather {
    background: url(http://i.imgur.com/PweinWm.gif) scroll left top;
    background-size: cover;
  }
  .sandstormweather {
    background: url(http://i.imgur.com/OEB3hum.gif) scroll left top;
    background-size: cover;
  }
  .pseudoweather {
    background: url(http://play.pokemonshowdown.com/fx/weather-trickroom.png) scroll left top;
    background-size: cover;
  }
  /**********************
  * Battle Weather ~end *
  **********************/

  .movemenu button {
    font-family: 'Helvetica';
    font-size: 13px;
    letter-spacing: -0.2px;
    color: #fff;
    text-shadow: none;
    text-align: left;
    float: left;
    display: block;
    box-shadow: none;
    width: 150px;
    margin: 2px 4px;
    height: 40px;
    position: relative;
    padding: 0 14px;
    border-radius: 0;
    border: none;
    transition: transform 0.2s ease;
    white-space: nowrap;
    text-overflow: ellipsis;
    overflow: hidden;
    clip-path: polygon(133px 40px, 134px 40px, 135px 40px, 136px 40px, 137px 39px, 142px 33px, 149px 23px,150px 20px, 149px 17px,142px 6px, 139px 2px, 138px 1px, 136px 0px, 135px 0px, 134px 0px,133px 0px, 17px 0px,16px 0px,15px 0px,14px 0px,12px 1px,11px 2px,8px 6px,1px 17px,0px 20px,1px 23px,8px 34px,11px 39px,13px 40px,14px 40px,15px 40px,16px 40px,17px 40px);
  }

  .movemenu button:hover {
    transform: scale(1.05);
  }

  .movemenu button::before {
    content: "";
    background-image: inherit;
    width: 150px;
    height: 40px;
    box-sizing: border-box;
    position: absolute;
    border: none;
    top: 0px;
    left: 0px;
    z-index: -2;
		clip-path: polygon(   133px 37px,   135px 36px,   140px 30px,   147px 20px, 136px 3px, 17px 3px,   16px 3px,   15px 3px,   14px 3px,   14px 3px,   12px 6px,   3px 20px,   10px 31px,   14px 37px);
  }

  .movemenu button::after {
    content: "";
    width: 150px;
    transform: rotate(180deg);
    height: 40px;
    box-sizing: border-box;
    position: absolute;
    border-radius: 0;
    border: none;
    top: 0;
    left: 0;
    z-index: -1;
    clip-path: polygon(147px 20px, 137px 3px, 15px 3px, 3px 20px, 4% 62.92%, 9% 63.64%, 10% 63.33%, 11% 62.99%, 12% 62.62%, 13% 62.22%, 14% 61.79%, 15% 61.33%, 16% 60.85%, 17% 60.35%, 18% 59.82%, 19% 59.27%, 20% 58.7%, 21% 58.1%, 22% 57.49%, 23% 56.87%, 24% 56.22%, 25% 55.57%, 26% 54.9%, 27% 54.22%, 28% 53.53%, 29% 52.83%, 30% 52.13%, 31% 51.42%, 32% 50.7%, 33% 49.99%, 34% 49.28%, 35% 48.56%, 36% 47.86%, 37% 47.15%, 38% 46.45%, 39% 45.76%, 40% 45.08%, 41% 44.41%, 42% 43.76%, 43% 43.12%, 44% 42.49%, 45% 41.88%, 46% 41.29%, 47% 40.72%, 48% 40.17%, 49% 39.64%, 50% 39.13%, 51% 38.65%, 52% 38.2%, 53% 37.77%, 54% 37.37%, 55% 37%, 56% 36.66%, 57% 36.35%, 58% 36.07%, 59% 35.82%, 60% 35.6%, 61% 35.42%, 62% 35.27%, 63% 35.15%, 64% 35.07%, 65% 35.02%, 66% 35%, 67% 35.02%, 68% 35.07%, 69% 35.16%, 70% 35.27%, 71% 35.43%, 72% 35.61%, 73% 35.83%, 74% 36.08%, 75% 36.36%, 76% 36.68%, 77% 37.02%, 78% 37.39%, 79% 37.79%, 80% 38.22%, 81% 38.68%, 82% 39.16%, 83% 39.67%, 84% 40.2%, 85% 40.75%, 86% 41.32%, 87% 41.91%, 88% 42.52%, 89% 43.15%, 90% 43.79%, 91% 44.45%, 92% 45.12%, 93% 45.8%, 93% 46%, 94% 47%, 97% 50.74%);
  }

  .movemenu button * {
    pointer-events: none;
  }

	.movemenu button small {
		color: #ffffff !important;
    letter-spacing: initial;
	}

	.movemenu button small.pp {
    float: left;
    font-size: 13px;
    padding-top: 4px;
    margin-left: 4px;
    font-family: 'Helvetica';
    border-color: inherit;
	}

  .movemenu button small.pp::after {
    content: "";
    position: absolute;
    width: 0;
    height: 0;
    left: 2px;
    top: 15px;
    border-top: 5px solid transparent;
    border-bottom: 5px solid transparent;
    border-left: 10px solid;
    border-left-color: inherit;
	}

	.movemenu button small.type {
    font-family: 'Abel', sans-serif;
    font-size: 10px;
    width: 35px;
    height: 10px;
    margin-top: 1.5px;
    margin-left: -3px;
    padding: 0 2px;
    padding-top: 1px;
		box-shadow: 0px 0px 1px #000000;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 600;
    text-transform: uppercase;
    text-shadow: -1px -1px rgb(0 0 0 / 30%), 0 -1px rgb(0 0 0 / 30%), 1px -1px rgb(0 0 0 / 30%), 1px 0 rgb(0 0 0 / 30%), 1px 1px rgb(0 0 0 / 30%), 0 1px rgb(0 0 0 / 30%), -1px 1px rgb(0 0 0 / 30%), -1px 0 rgb(0 0 0 / 30%);
    border-radius: 50px;
    border: 2px solid;
	}

  @keyframes cameraMovementBase {
    10%   { transform: translate(0px, 0px) scale(1); }
    30%  { transform: translate(-20px, 20px) scale(1.1); }
    50%  { transform: translate(0px, 0px) scale(1); }
    70% { transform: translate(20px, -20px) scale(1.1); }
    90%   { transform: translate(0px, 0px) scale(1); }
  }

  @keyframes cameraMovementStage {
    10%   { transform: translate(0px, 0px) scale(1); }
    30%  { transform: translate(-23px, 36px) scale(1.1); }
    50%  { transform: translate(0px, 0px) scale(1); }
    70% { transform: translate(20px, -5px) scale(1.1); }
    90%   { transform: translate(0px, 0px) scale(1); }
  }

  .innerbattle > .backdrop {
    animation: cameraMovementStage 40s infinite !important;
  }

  .innerbattle > *:not(.weather):not(.leftbar):not(.rightbar):not(.messagebar),
  .innerbattle > .psychicterrainweather,
  .innerbattle > .mistyterrainweather,
  .innerbattle > .grassyterrainweather,
  .innerbattle > .electricterrainweather {
    animation: cameraMovementBase 40s infinite;
  }

  .innerbattle > .rightbar + div {
    animation: none !important;
  }

  /********************
  * In-Game Shadows *
  ********************/
  img[src^="https://play.pokemonshowdown.com/sprites/ani"] {
    /* -webkit-filter: drop-shadow(rgba(0, 0, 0, 0.5) 18px 37px 19px);
    filter: drop-shadow(rgba(0, 0, 0, 0.5) 18px 37px 19px);
    transform: scale3d(1, 1, 1.00001); */
    padding-bottom: 10px;
    background-image: radial-gradient(closest-side at 50% 90%, rgba(0,0,0,0.6) 0%, rgba(0,0,0,0.3) 50%, rgba(0,0,0,0) 90%);
  }
  /******************
  * In-Game Shadows ~end *
  ******************/

	/********************
  * Move Button Types *
  ********************/
	.movemenu button.type-Normal, .movemenu button.type-Normal:hover, .movemenu button.type-Normal:active  { background: #301f04; border-color: #301f04; }
	.movemenu button.type-Normal::before { background-image: linear-gradient(0deg, #736751 50%, #8d7655 100%); }
  .movemenu button.type-Normal::after { background: linear-gradient(180deg, rgba(78,68,66,1) 50%, rgba(66,52,42,1) 100%); }
	.movemenu button.type-Normal small.type { background-color: #bbbbaa; border-color: #eeeeee;}

  .movemenu button.type-Fighting, .movemenu button.type-Fighting:hover, .movemenu button.type-Fighting:active  { background: #2c0201; border-color: #2c0201; }
	.movemenu button.type-Fighting::before { background-image: linear-gradient(0deg, rgba(158, 96, 53, 1) 50%, rgba(139, 84, 52, 1) 100%); }
  .movemenu button.type-Fighting::after { background: linear-gradient(180deg, rgba(97, 37, 25, 1) 50%, rgba(107, 55, 40, 1) 100%); }
	.movemenu button.type-Fighting small.type { background-color: #bb5544; border-color: #eecccc;}

  .movemenu button.type-Flying, .movemenu button.type-Flying:hover, .movemenu button.type-Flying:active { background: #312b06; border-color: #312b06; }
  .movemenu button.type-Flying::before { background-image: linear-gradient(0deg, rgba(82, 112, 168, 1) 50%, rgba(101, 133, 158, 1) 100% ); }
  .movemenu button.type-Flying::after { background: linear-gradient(0deg, rgba(29, 28, 96, 1) 50%, rgba(48, 54, 138, 1) 100%); }
	.movemenu button.type-Flying small.type { background-color: #6699ff; border-color: #ddeeff;}

  .movemenu button.type-Poison, .movemenu button.type-Poison:hover, .movemenu button.type-Poison:active  { background: #27012c; border-color: #27012c; }
	.movemenu button.type-Poison::before { background-image: linear-gradient(0deg, rgba(179, 63, 153, 1) 50%, rgba(145, 57, 159, 1) 100%); }
  .movemenu button.type-Poison::after { background: linear-gradient(180deg, rgba(61, 26, 86, 1) 50%, rgba(83, 38, 127, 1) 100%); }
	.movemenu button.type-Poison small.type { background-color: #aa5599; border-color: #eeccee;}

  .movemenu button.type-Ground, .movemenu button.type-Ground:hover, .movemenu button.type-Ground:active { background: #312b06; border-color: #312b06; }
	.movemenu button.type-Ground::before { background-image: linear-gradient(180deg, rgba(177,144,27,1) 50%, rgba(204,154,29,1) 100%); }
  .movemenu button.type-Ground::after { background: linear-gradient(0deg, rgba(124,107,13,1) 50%, rgba(106,79,5,1) 100%); }
	.movemenu button.type-Ground small.type { background-color: #d5b453; border-color: #fce8c2;}

  .movemenu button.type-Rock, .movemenu button.type-Rock:hover, .movemenu button.type-Rock:active  { background: #332109; border-color: #332109; }
	.movemenu button.type-Rock::before { background-image: linear-gradient(0deg, rgba(145, 105, 38, 1) 50%, rgba(126, 95, 35, 1) 100%); }
  .movemenu button.type-Rock::after { background: linear-gradient(180deg, rgba(76, 41, 7, 1) 50%, rgba(90, 62, 17, 1) 100%); }
	.movemenu button.type-Rock small.type { background-color: #bbaa66; border-color: #eeeedd;}

  .movemenu button.type-Bug, .movemenu button.type-Bug:hover, .movemenu button.type-Bug:active  { background: #3d3c00; border-color: #3d3c00; }
	.movemenu button.type-Bug::before { background-image: linear-gradient(0deg, rgba(171, 165, 9, 1) 50%, rgba(140, 142, 0, 1) 100%); }
  .movemenu button.type-Bug::after { background: linear-gradient(180deg, rgba(65, 66, 0, 1) 50%, rgba(87, 96, 5, 1) 100%); }
	.movemenu button.type-Bug small.type { background-color: #aabb22; border-color: #eeeebb;}

  .movemenu button.type-Ghost, .movemenu button.type-Ghost:hover, .movemenu button.type-Ghost:active  { background: #1b063a; border-color: #1b063a; }
	.movemenu button.type-Ghost::before { background-image: linear-gradient(0deg, rgba(147, 122, 126, 1) 50%, rgba(126, 106, 137, 1) 100%); }
  .movemenu button.type-Ghost::after { background: linear-gradient(180deg, rgba(70, 35, 94, 1) 50%, rgba(86, 61, 131, 1) 100%); }
	.movemenu button.type-Ghost small.type { background-color: #6666bb; border-color: #ddddee;}

  .movemenu button.type-Steel, .movemenu button.type-Steel:hover, .movemenu button.type-Steel:active  { background: #071436; border-color: #071436; }
	.movemenu button.type-Steel::before { background-image: linear-gradient(0deg, rgba(118, 122, 119, 1) 50%, rgba(86, 103, 112, 1) 100%); }
  .movemenu button.type-Steel::after { background: linear-gradient(180deg, rgba(16, 35, 46, 1) 50%, rgba(36, 59, 77, 1) 100%); }
	.movemenu button.type-Steel small.type { background-color: #aaaabb; border-color: #eeeeee;}

  .movemenu button.type-Fire, .movemenu button.type-Fire:hover, .movemenu button.type-Fire:active { background: #360504; border-color: #360504; }
	.movemenu button.type-Fire::before { background-image: linear-gradient(180deg, rgba(203,79,30,1) 50%, rgba(167,67,26,1) 100%) }
  .movemenu button.type-Fire::after { background: linear-gradient(0deg, rgba(77,16,3,1) 50%, rgba(103,33,12,1) 100%); }
	.movemenu button.type-Fire small.type { background-color: #ff4422; border-color: #ffb9a8;}

  .movemenu button.type-Water, .movemenu button.type-Water:hover, .movemenu button.type-Water:active  { background: #09363d; border-color: #09363d; }
	.movemenu button.type-Water::before { background-image: linear-gradient(0deg, rgba(20, 158, 157, 1) 50%, rgba(14, 140, 155, 1) 100%); }
  .movemenu button.type-Water::after { background: linear-gradient(180deg, rgba(2, 68, 82, 1) 50%, rgba(5, 97, 122, 1) 100%); }
	.movemenu button.type-Water small.type { background-color: #3399ff; border-color: #cceeff;}

  .movemenu button.type-Grass, .movemenu button.type-Grass:hover, .movemenu button.type-Grass:active  { background: #002209; border-color: #002209; }
	.movemenu button.type-Grass::before { background-image: linear-gradient(0deg, rgba(154, 173, 22, 1) 50%, rgba(117, 151, 13, 1) 100%); }
  .movemenu button.type-Grass::after { background: linear-gradient(180deg, rgba(31, 77, 0, 1) 50%, rgba(52, 109, 6, 1) 100%); }
	.movemenu button.type-Grass small.type { background-color: #77cc55; border-color: #ddffcc;}

  .movemenu button.type-Electric, .movemenu button.type-Electric:hover, .movemenu button.type-Electric:active  { background: #4e4500; border-color: #4e4500; }
	.movemenu button.type-Electric::before { background-image: linear-gradient(0deg, rgba(195, 166, 10, 1) 50%, rgba(168, 153, 0, 1) 100%); }
  .movemenu button.type-Electric::after { background: linear-gradient(180deg, rgba(93, 86, 0, 1) 50%, rgba(116, 115, 0, 1) 100%); }
	.movemenu button.type-Electric small.type { background-color: #ffcc33; border-color: #ffeecc;}

  .movemenu button.type-Psychic, .movemenu button.type-Psychic:hover, .movemenu button.type-Psychic:active { background: #360504; border-color: #360504; }
	.movemenu button.type-Psychic::before { background-image: linear-gradient(180deg, rgba(211,57,85,1) 50%, rgba(185,44,76,1) 100%); }
  .movemenu button.type-Psychic::after { background: linear-gradient(0deg, rgba(105, 4, 38, 1) 50%, rgba(129, 24, 57, 1) 100%); }
	.movemenu button.type-Psychic small.type { background-color: #ff5599; border-color: #ffccdd;}

