css:
@-moz-document url-prefix(https://www.rfc-editor.org/rfc) {
	:root {
    --textColor: #F8F8F2;
    --backgroundColor: #282A36;
    --linkColor: #8BE9FD;
  }

  body {
    background-color: var(--backgroundColor);
  }

  h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
    color: var(--textColor);
  }

  .newpage, .pre {
    color: var(--textColor);
  }

  .selflink {
    color: var(--textColor) !important;
  }

  a {
    color: var(--linkColor);
  }
}
