css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("jango.com") {

body { background:#000 !important; }

#masthead { border: 0px solid red !important; position: absolute !important; left: 0px !important; z-index: 100 !important; 
background: #000 !important; }

#player_artist { width: 220px !important; position: absolute; left: 30px !important; top: 135px !important; }
#player_artist_pics, #player_artist_header, #player_artist_links  { width: 220px !important; margin: 0px !important; padding: 0px !important;  background: none !important; }

#player-outer-box { border: 0px solid red !important; width:0px !important; height: 0px !important; background: none !important; }
#player_wrapper { border: 0px solid red !important; width: 0px !important; height: 0px !important; background: none !important; }

#player-panel { position: absolute; left: 0px !important; width: 250px !important; }
#player_main_pic_wrapper, #player_artist_pics { width: 220px !important; }

#volumeWrapper   { position: absolute !important; left: -110px !important; top: 25px !important; }
#player-controls { position: absolute !important; left:  125px !important; top: 90px !important; }
#timer { position: absolute; left: 175px !important; top: 20px !important; }
#player-info { width: 225px !important; }
#song_info { width: 220px !important; }

#player-tabs { width: 210px !important; left: 30px !important; top: 400px !important; }
#faces-tab, #songs-tab, #station-tab, #player-tab-menu a { width: 67px !important; }
#older { position: absolute !important; left: 199px !important; top: 85px !important; }
#face-left-arrow { position: absolute !important; top: 45px !important; }
#thumbs, #faces-pane, #player-tab-panes, #songs-playing-now, #thank-you-overlay, #tuning-in, #loading-faces, #auto-sanks, 
#station_pane_right 
{ width: 210px !important; height: 190px !important; }
#contains { width: 200px !important; height: 100px !important; position: relative !important; top: -25px !important; }
#artist-header { width: 194px !important; }

#station_select { left: 0px !important; width: 255px !important; }
#sslab { font-size: 0.8em !important; }

#thumbs-scroll { width: 250px !important; height: 180px; position: absolute !important; left: -100px !important; }

#container { border: 0px solid red !important; width: 1px; height: 1px; position: absolute !important; }
#headers, #footer { position: relative !important; left: 0px !important; }

#player-savebuy { position: absolute !important; left: 20px !important; top: 345px !important; }
#share-outer, #buy-outer { left: 0px !important; z-index: 100 !important; }

#left_reveal, #right_reveal, #player_left_pic, #player_right_pic
  {
display: none !important;
top:0px !important;
left:0px !important;
padding:0px !important;
border:0px !important;
margin:0px !important;
background:none !important;
}

#menus-and-logo { position: absolute !important; left: 5px !important; top: 630px !important; }
#header-nav { border: 2px solid red !important; z-index: 200 !important; left: 0px !important; }

#column-wrapper
  {
position: absolute !important; 
top:0px !important;
left:260px !important;
padding:0px !important;
border:0px !important;
margin:0px !important;

}


}
