/* ==UserStyle==
@name           create.roblox.com - 9/1/2022, 9:26:54 AM
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */
@-moz-document domain("create.roblox.com") {
    /* Insert code here... */
    :root {
      --main-bg: #fdfdfc;
      --main-card-header-bg: #eeeedd;
      --main-paper-bg: #ededec;
      --main-fg: #333;
      --fg-1: #444;
      --fg-primary: #226;
      --main-link-bg: #ffe;
      --main-link-fg: #33a;
    }
    
    h1 {
       font-size: 32px !important;
    }

    * {
       font-size: 11px;
    }
    
    .jss12 { /* roblox icon */
        background-color: var(--main-fg);
    }
    
    body {
        background-color: var(--main-bg);  
        color: var(--main-fg);
    }
    
    .MuiTypography-root {
        font-size: 12px;
        background-color: var(--main-bg);
        border-radius: 0 !important;
    }
    
    .MuiPaper-root {
        background-color: var(--main-paper-bg);
        color: var(--main-fg);
    }
    
    aside * {
        background-color: var(--main-paper-bg) !important;
        color: var(--main-fg);        
    }
       
    .MuiTabs-root {
        background-color: var(--main-bg);
        color: var(--main-fg);        
    }

    .MuiCard-root {
        background-color: var(--main-paper-bg);
        color: var(--main-fg);
    }
    
    .MuiGrid-container {
        background-color: var(--main-paper-bg);
        color: var(--main-fg);       
    }

    .MuiCardHeader-root {
        background-color: var(--main-bg);
        color: var(--main-fg);
    }
    
    .MuiIconButton-label {
        color: var(--main-fg);
    }

    .MuiCardContent-root .cm-line {
        font-size: 12px !important;
        line-height: 120% !important;
    }

    .MuiCard-root .cm-content {
        background-color: var(--main-bg);
        color: var(--main-fg);  
    }
    
    .MuiCard-root .cm-gutter {
        background-color: var(--main-bg);
        color: var(--main-fg);          
    }
    
    .MuiCard-root .cm-gutterElement {
        font-size: 12px !important;     
    }

    .MuiGrid-container {
        background-color: var(--main-bg);
        color: var(--main-fg);
        font-size: 12px
    }
       
    .MuiGrid-root {
        background-color: var(--main-bg);
        color: var(--main-fg);
    }

    .MuiGrid-item {
        background-color: var(--main-bg);
        color: var(--main-fg);
    }
    
    .MuiTypography-colorInherit {
        color: var(--fg-1);
    }
    
    .MuiTypography-colorTextPrimary {
        color: var(--fg-primary);
    }
    
    #layout-content-container .MuiLink-root {
        background-color: var(--main-link-bg);
        color: var(--main-link-fg); 
        border-radius: 0 !important;
        border: 0 none;
        padding: 0;
    }
    

}
