css:
/*piotros's ku990.pl forum*/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ku990.pl") {

  div.navbar > div.inner > center, div#header-bg, div#google, div#stg_ads, div#page-body > center > table, div#page-body > center > h5, div#wrap-bg > center > center > font, div#wrap-bg > center > font > a > img, div#page-footer > div.copyright, /*.ltr div > a > img,*/ div#wrap-bg > center > a > img {
      display: none !important;
  }

  .ltr > div > div > a > img {
      opacity: 0.0 !important;
  }

  embed#td_flash {
      visibility: hidden !important;
  }

  .navbar, .reported {
      background-color: #f3f3f3 !important;
  }

  .navbar {
      margin-bottom: 10px !important;
  }

  #wrap-bg {
      opacity: 0.95 !important;
      padding-top: 10px !important;
  }

  .forabg, .forumbg {
	background-color: #597A97 !important;
	background-image: none !important;
        margin-bottom: 10px !important;
  }

  div#page-body > p {
        padding-top: 20px !important;
  }

  .forabg font dl, .forumbg font dl, div.content, div.content span {
	color: #4C4C4C !important;
  }

  div.signature, div.signature span {
	color: #1B476F !important;
  }

  .panel, .rules, tr.bg1:hover, tr.bg2:hover {
       background-color: #F7FBFF !important;
  }

  li.row, .bg1, div.pm {
      background-color: white !important;
      background-image: none !important;
  }

  li.row:hover, .bg2 {
      background-color: #EFEFEF !important;
      background-image: none !important;
  }

  li.row:hover dd {
      border-left-color: #EFEFEF !important;
  }

  a {
      color: #E8E8E8 !important;
  }

  a:hover {
      color: #BBC2C7 !important;
  }

  .forabg a, .forumbg a, .navbar a, h3 a, .post a, .panel a, #shoutbox a {
      color: #597A97 !important;
  }

  .forabg a:hover, .forumbg a:hover, .navbar a:hover, h3 a:hover, .post a:hover, .panel a:hover, #shoutbox a:hover {
      color: #3C4B57 !important;
  }

  a.username-coloured, span[style="color: rgb(153, 204, 255); font-weight: bold;"] {
      color: #1D5587 !important;
  }

  div#page-body > p a.username-coloured {
      color: #ADCCE7 !important;
  }

  a[style="color: rgb(170, 0, 0);"]{
      color: rgb(170, 0, 0) !important;
  }

  div#page-body > p a[style="color: rgb(170, 0, 0);"]{
      color: #F7AAAA !important;
  }

  a[style="color: rgb(0, 102, 0);"]{
      color: rgb(0, 102, 0) !important;
  }

  div#page-body > p a[style="color: rgb(0, 102, 0);"]{
      color: #9BD79A !important;
  }

  div#page-body > p a[style="color: rgb(153, 204, 255);"]{
      color: #ADCCE7 !important;
  }

  a[style="color: rgb(102, 0, 255);"]{
      color: rgb(102, 0, 255) !important;
  }

  div#page-body > p a[style="color: rgb(102, 0, 255);"]{
      color: #DDC7FF !important;
  }

  a[style="color: rgb(255, 153, 0);"]{
      color: rgb(255, 153, 0) !important;
  }

  div#page-body > p a[style="color: rgb(255, 153, 0);"]{
      color: #FFE3B7 !important;
  }

  a[style="color: rgb(204, 0, 153);"]{
      color: rgb(204, 0, 153) !important;
  }

  div#page-body > p a[style="color: rgb(204, 0, 153);"]{
      color: #F7AAE5 !important;
  }

  a[style="color: rgb(0, 0, 255);"]{
      color: rgb(0, 0, 255) !important;
  }

  div#page-body > p a[style="color: rgb(0, 0, 255);"]{
      color: #B7B8FF !important;
  }

  li.header > dl.icon > dt > a {
      color: white !important;
  }

  div#page-body h6 {
      font-size: 100% !important;
      text-align: right !important;
      padding-right: 10px !important;
  }

  div#page-body h2 font, div#page-body h2 font a, div#page-body h2 {
      color: #EFEFEF !important;
  }

  font, font > dd > strong, div#page-body .panel h2, dl.postprofile dd strong, p.author {
      color: #272727 !important;
  }

  div#page-body > p > font, div#page-body > p, div#wrap-bg > center {
      color: white !important;
  }

  blockquote {
      background-color: #E8EAEF !important;
  }

  .topic-actions input.inputbox {
      width: 550px !important;
  }

  form#cse-search-box input, form#search input{
      font: 12px Arial, Helvetica, sans-serif !important;
      color: #4C4C4C !important;
  }

  form#cse-search-box input[name="q"], form#search input.inputbox {
      width: 85% !important;
      border: 1px solid #484848 !important;
      height: 14px !important;
  }

  form#cse-search-box input[name="q"] {
      padding-right: 16px !important;
  }

  form#search input.inputbox {
      padding-left: 16px !important;
  }

  form#cse-search-box input, form#search input.button2 {
      width: 10% !important;
  }

  input {
      border: 1px solid #484848 !important;
  }

  input:hover {
      border: 1px solid #171717 !important;
  }

  p#shoutbox input.inputbox {
      width: 387px !important;
  }

  p#shoutbox ul.forums {
      background-image: none !important;
      background-color: #4C4C4C !important;
      border: 1px solid #2F2F2F !important;
      padding: 5px !important;
  }

  span.posthilit {
      background-color: yellow !important;
  }

  select#f {
      width: 350px !important;
  }

  div#wrap > div#page-body > h3 > font, div#wrap > div#page-body > h3 font a, .tablebg h4, form#postform > h3#review > font, form#postform > h3#review > font a, div#page-body > h3, h3#review, div#page-body > h3 a, h3#review > a {
      color: #EFF3F7 !important;
  }

  .tablebg {
      margin-top: 20px !important;
  }

  .display-options label, .display-options input {
      display: none !important;
  }

  div.panel .pagination {
      color: #597A97 !important;
  }

  div.panel .pagination a {
      color: #3A4F67 !important;
  }

  .pagination, .pagination font {
      color: #BFBFBF !important;
  }

  .pagination a {
      color: #EFEFEF !important;
  }

  .pagination span a {
      color: #506A87 !important;
  }

  .pagination span a:hover {
      background-color: #597A97 !important;
      border: 1px solid #506A87 !important;
      color: #EFEFEF !important;
  }

}

@-moz-document url-prefix("http://ku990.pl/Forum/search.php") {

  input#add_keywords {
      margin-left: -110px !important;
  }

  .topic-actions input.inputbox {
      width: 280px !important;
  }

  .display-options > font > label {
      color: white !important;
      font-size: 100% !important;
  }

}

@-moz-document url-prefix("http://ku990.pl/Forum/ucp.php") {

  div#cp-main h2 {
      color: #4C4C4C !important;
      font-weight: bold !important; 
  }

  .bg3 {
       background-color: #C9D0D8 !important;
  }

}
