.profile-avatar-left,
#tab-wardrobe,
#characterInventorySearch,
#tab-outfits,
#HomeContainer > div.col-xs-12.home-header > a {
    background: transparent !important;
}

#party_none_title,
div.friend_dock_chatbox_titlebar.chat-header-blink-off {
    background: linear-gradient(to bottom, #dc5af4, #bc47d7);
}

#ctl00_cphRoblox_RightColumn,
#Body > table > tbody > tr > td:nth-child(3),
#Body > table > tbody > tr > td:nth-child(4) {
    display: none;
}

#GamesPageLeftColumn {
    width: auto;
    margin-right: 10px !important;
    margin-left: 10px !important;
}

#GamesListsContainer > div {
    background-color: transparent !important;
    width: 100% !important;
}

.light-theme .rbx-left-col,
.light-theme .menu-vertical,
.input-field,
.light-theme .container-footer,
.light-theme .container-empty,
.light-theme .section-content-off,
.light-theme .rbx-tabs-horizontal .nav-tabs,
.light-theme .rbx-tabs-horizontal .rbx-tab .rbx-tab-headingt,
.light-theme .rbx-tab .rbx-tab-heading,
.light-theme#DevelopSplashScreen .wiki-container, .light-theme #DevelopSplashScreen .wiki-container,
.light-theme#DevelopSplashScreen .testimonials-container, .light-theme #DevelopSplashScreen .testimonials-container,
.light-theme#DevelopSplashScreen .body-container .blurbs-container, .light-theme #DevelopSplashScreen .body-container .blurbs-container{
