@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com")
{
    #blueBar
    {
        position: fixed;
        top: 0;
        right: 0;
        left: 0;
        z-index: 1000;
    }

    #pageHead
    {
        position: fixed;
        top: 0;
        left: 0;
        right: 0;
        width: 981px;
        margin: auto;
        z-index: 1001;
        background: #3B5998;
    }

    #headNav
    {
        position: fixed;
        top: 0;
        left: 0;
        right: 0;
        width: 981px;
        margin: auto;
        z-index: 1001;
        background: #3B5998;
    }

    .fb_content
    {
        margin-top: 41px;
    }
}
