css:
 @namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tuenti.com") {

body{
 background: url( http://i49.tinypic.com/ipcwgy.jpg)!important;
} 
.body,.item {
background:#FFFF66 nonecenter center repeat-x !important; color:black !important;}


.header{background: #FFFF66! important;}

.header a.logo {
-moz-background-clip:border! important;
-moz-background-inline-policy:continuous! important;
-moz-background-origin:padding! important;
background:transparent none no-repeat scroll center center! important;
display:block! important;
float:left! important;
height:0px ! important;
width:96px! important;
}

a {
color:#0099CC! important;
}

.mod h3 {background:#FFFF66  repeat-x scroll 0 -182px ! important;}

.container, #latest_photos, .blockedFriends, #banned_photos .body {
-moz-background-clip:border !important;
-moz-background-inline-policy:continuous !important;
-moz-background-origin:padding !important;
background:#FFFF66 none repeat scroll 0 0 !important;
border-color:#ACD0EE !important;
border-style:solid !important;
border-width:0 1px 1px !important;
}

.chatMod form, .chatHeader form {
background-color:#FFFF66 !important;
}

.header .sections a.active, .header .sections a:hover {
background:#FFFFCC none repeat-x scroll 0 -76px !important;
}

.canvas .pager.light {
background-color:#FFFF66 !important;

}

.sidebar form {
background-color:#FFFF66 !important;

}
.subHeader {
background:#FFFF66 url(http://i46.tinypic.com/29qiamu.jpg) repeat-x 
!important}

.personalStatus span.hour {
color:#FFFF66! important;
position:static! important;

}
.hint {
color:#0099CC! important;
}


}
