css:
@-moz-document domain("easyjapanese.net") {
	.level-5 {
		background-color: green !important;
	}
	.level-4 {
		background-color: #00BFFF !important;
	}
	.level-3 {
		background-color: orange !important;
	}
	.level-2 {
		background-color: purple !important;
	}
	.level-1 {
		background-color: red !important;
	}
	.box-detail span.jlpt-n5 {
		text-decoration-color: initial;
		color: green;
		border: 0px solid;
	}
	.box-detail span.jlpt-n4 {
		text-decoration-color: initial;
		color: #00BFFF !important;
		border: 0px solid;
	}
	.box-detail span.jlpt-n3 {
		text-decoration-color: initial;
		color: orange;
		border: 0px solid;
	}
	.box-detail span.jlpt-n2 {
		text-decoration-color: initial;
		color: purple;
		border: 0px solid;
	}
	.box-detail span.jlpt-n1 {
		text-decoration-color: initial;
		color: red;
		border: 0px solid;
	}
	.box-detail span.unknown {
		text-decoration-color: initial;
		color: #FFdddd;
		border: 0px solid;
	}
	.dicWin {
		color: rgb(178, 172, 162) !important;
	}
	#mep_0, #mep_1, .audio-player {
	display:none;
	}
}
