css:
/* i really want this to be global */
/** show furigana only on hover **/
* > rt {
	visibility: hidden !important;
}

*:hover > rt {
	visibility: visible !important;
}

/** Vocab tracker underline custom colors **/
.VocabTracker-User-Word-Status-0-No-Highlight {
	border-bottom-color: red;
}

.VocabTracker-User-Word-Status-1 {
	border-bottom-color: purple !important;
}

.VocabTracker-User-Word-Status-2
	{
	border-bottom-color: orange !important;
}
.VocabTracker-User-Word-Status-3
	{
	border-bottom-color: blue !important;
}

.VocabTracker-User-Word-Status-4
	{
	border-bottom-color: green !important;
}

/** no underline for furigana **/
rt * {
	border: 0 !important;
}


.jlpt-n5 {
		text-decoration-color: initial;
		color: green;
		border: 0px solid;
	}
.jlpt-n4 {
		text-decoration-color: initial;
		color: #00BFFF !important;
		border: 0px solid;
	}
.jlpt-n3 {
		text-decoration-color: initial;
		color: orange;
		border: 0px solid;
	}
.jlpt-n2 {
		text-decoration-color: initial;
		color: purple;
		border: 0px solid;
	}
.jlpt-n1 {
		text-decoration-color: initial;
		color: red;
		border: 0px solid;
	}
.jlpt-n0 {
