css:
@-moz-document regexp('https://.*\\.atlassian\\.net/jira/software/c/projects/.*') {
  :root {
    --topNavigationHeight: 26px !important;
}

#ak-jira-navigation header[role="banner"] {
    height: auto;
    zoom: .7;
}

#ak-main-content div[data-testid="rapidboard-breadcrumbs"] {
    margin-top: 0;
    font-size: 0.7rem;
    margin-left: 20px;
    height: 4px;
    overflow: hidden;
    transition: height .2s ease-out .5s;
}

#content {
    margin: 0 20px;
}

#ghx-header #subnav-title .subnavigator-title {
    font-size: 1rem;
}

span.subnav-container {
    height: auto;
    font-size: 1rem;
    line-height: 1;
}

.adg3 .ghx-top-header.ghx-expermental-top-header #ghx-modes-tools {
    zoom: 0.7;
}

#ghx-header .ghx-sprint-goal {
    margin-top: 0;
    height: 0;
    transition: height .2s ease-out .5s;
}

.adg3 #ghx-header {
    padding: 0;
    margin-bottom: .5em;
}

#ghx-quick-filters {
    margin-bottom: .5em;
}

.adg3 #ghx-operations {
    zoom: .8;
}

button[data-testid="ContextualNavigation-grab-area"] {
    width: 1px
}

@supports ((position:sticky) or (position:-webkit-sticky)) and (display:grid){
#gh #ghx-work #ghx-pool-column.ghx-flexible-board #ghx-column-headers .ghx-column {
    padding: 3px 5px;
}
    
#gh #ghx-work #ghx-pool-column.ghx-flexible-board .ghx-swimlane .ghx-swimlane-header {
    top: 25px;
    height: 35px;
}
}

#ak-main-content div[data-testid="rapidboard-breadcrumbs"]:hover {
    height: 20px;
    transition-delay: 0s;
}

#ghx-header:hover .ghx-sprint-goal {
    height: 20px;
    transition-delay: 0.2s;
}


.ghx-issue .ghx-stat-fields>.ghx-row {
    margin-top: 3px;
}
}
