@namespace url(http://www.w3.org/1999/xhtml);
/* http://web3beta.net           */
/* web3beta (web3beta@gmail.com) */

/*
 * Web3beta RTL All Service
 * Author	 : Web3beta (Web3beta@gmail.com)
 * Weblog      	 : http://Web3beta.net
 * Updated       : 
 * ver           : 106
 * Usage	 : 
 * Description	 : RTL (Persian & Arabic & othaer) Style
                   for All Google Product: 
                   Gmail & Google Buzz & Google Reader &
                   Google Search & Google Calendar & Google groups & Other ...
 */
/* -------------------------------------------------- */
/* google reader */
@-moz-document 
url-prefix(http://www.google.com/reader),
url-prefix(https://www.google.com/reader),
url-prefix(http://www.google.com/reader/shared),
url-prefix(https://www.google.com/reader/shared) {

/* font-family */
* {font-family:tahoma;}
.entry-title-link {font-family:times New Roman;}

/* direction: rtl */
.add-comment-text-area {direction: rtl;}
.textarea {direction: rtl;}
input.title {direction: rtl;}

/* text-align */
.entry-snippet {text-align:justify!important;}
div.item-body p, div.item-body li {text-align:justify !important;}
.rtl {text-align:justify !important;}
.overview-metadata {text-align:justify;}

/* font-size */
#sub-tree-container ul {font-size:98%!important;}

.entry-secondary-snippet  {font-size:7pt !important;}
#entries .entry-body {font-size:9pt !important;}
#sections {font-size:8pt;}
.sub-name-text {font-size:8pt !important;}
#chrome-title {font-size:10pt !important;}
.entry-title-link {font-size:14pt !important;}
.entry-source-title-parent, .entry-author-parent {font-size:8pt !important;}
.entry-title {font-size:8pt !important;}

/* line-height */
#entries .entry-body {line-height:15pt;}

/* text-decoration */
.item-body a {text-decoration: none !important;}

/* max-width */
#entries .entry-body,
#entries .entry-title {max-width: none !important;}
.entry-conversation .comments-container, .comment-entry-comments .entry-comments, .comment-entry .entry-snippet  {max-width:none!important;}
.entry .entry-body, .entry .entry-title, .entry .entry-likers {max-width:none !important;}
.add-comment-text-area {width:99%!important;}

/* smart */
.unread-count {display: inline-block !important; margin-left: 2pt;}
span.title span.unread {display:inline-block !important;}
.entry-title-go-to {display:inline-block !important;}
.entry-main {overflow:hidden!important;}


/* fix from */
.entry-source-title {float: left!important; margin-right: 5px;}
.entry-source-title-parent {font-size: 0pt !important;}
a.entry-source-title-parent {font-size: 8pt !important;}
a.entry-source-title {font-size: 8pt !important;}

/* fix "Shared by" */
.entry-author-attribution-link {font-size: 9pt !important;}
.entry .entry-via {display: inline-block; margin-right: 5px!important; margin-bottom: 0px!important; margin-top: 0px!important;}
.entry-via {font-size: 0pt !important;}

/* snippet? */
#snippet-preview {font-size: 9pt !important;}
.annotation textarea {font-size: 8pt !important;}

/* fix */
.conversation-attribution {font-size: 0pt !important;}
.conversation-attribution a, .add-comment-link {font-size: 8pt !important;}
.conversation-attribution a {margin-right: 5px!important;}

/* other */
#entries .entry-body img {width:auto; height:auto; margin:0; padding:5px;}
#entries .entry-body img[align=right] {margin:0 0 0 10px;}
#entries .entry-body img[align=left] {margin:0 10px 0 0;}
#entries .entry-body img[align=center] {margin:10px;}
#entries .entry-body a,#entries .entry-body a:hover,#entries .entry-body a:visited {text-decoration:none;}

}
/* -------------------------------------------------- */
/* Gmail */
@-moz-document domain("mail.google.com") {

* {font-family:tahoma;}
.iA, .y6, .y2 {font-size: 8pt !important;}

}
/* -------------------------------------------------- */
/* Chat Box */
@-moz-document domain("mail.google.com") {

.Df, .vG {font-size: 8pt !important;}
}
/* -------------------------------------------------- */
/* GTalk */
@-moz-document domain("mail.google.com") {

.ko, .kk, .kl, .kq, .kg {font-size: 8pt !important; text-align: justify !important; /* direction: rtl; */ }
.kn {padding-left: 1em; direction: rtl; float: right !important;}
.kq, .kg {direction: ltr;}

}
/* -------------------------------------------------- */
/* Buzz */
@-moz-document 
domain("mail.google.com")
 {

/* font-family */
* {font-family:tahoma!important;}

/* font-size */

/* text-align */


.YJ {max-width:none !important; text-align: right !important;}

.H4  {max-width:none !important; text-align: right !important; direction: rtl;}

.G4 {display: inline-block !important; margin-right: 20pt;}

.O-L {float:right!important; margin-left:7px !important; margin-right: 25pt !important;}

}
/* -------------------------------------------------- */
/* google search */
@-moz-document url-prefix(http://www.google.com/search) {

* {font-family:tahoma;}
.r {}
.s {font-size:9pt !important;}

}
/* -------------------------------------------------- */
/* twitter */
@-moz-document domain("twitter.com") {


/* font-family */
* {font-family:tahoma;}

/* text-align */
* {text-align: justify;}

/* line-height */
.hentry {line-height:15pt!important;}

/* direction: rtl */
textarea#status, #sidebar_search_q, #text, .status-body, .entry-content {direction: rtl !important;}
#update_notifications {direction: rtl !important;}
#geo_status {direction: ltr;}

/* font-size */
textarea#status, #sidebar_search_q, #text, .status-body, .entry-content {font-size: 9pt !important;}
#bio { font-size: 8pt;}

.entry-meta {display: block !important; direction: ltr; text-align: left;}

.actions-hover {padding-right: 65px !important}

.thumb.author {left: auto !important; right: 0 !important; }

ol.statuses span.status-body {margin-left: auto !important; margin-right: 65px !important;}

ol.statuses .actions {right: auto !important; left: 5px !important;}

#timeline_heading {padding-left:32px !important}

}
/* -------------------------------------------------- */
/* friendfeed */
@-moz-document url-prefix(http://friendfeed.com) {

/* font-family */
* {font-family:tahoma!important;}

/* text-align */
* {text-align:justify}

* {font-size:9pt!important;}

span.title a {font-size:13pt!important; font-family:arial!important;}

div.updated {font-size:7pt!important;}

}
/* -------------------------------------------------- */
/* facebook */
@-moz-document domain("facebook.com") {

/* text-align */
* {text-align:justify}

/* font-family */

/* direction: rtl */
.UIStoryAttachment_Copy {direction: rtl;}
.UIStoryAttachment_Title {direction: rtl;}
.comment_text {direction: rtl;}
.Mentions_Input, textarea {direction: rtl;}


.UIStory_Message, .GenericStory_Message { }

}
/* -------------------------------------------------- */
/* blogger.com */
@-moz-document 
/* http://www.blogger-fa.com/ */
url-prefix(http://www.blogger.com/),
url-prefix(http://www.blogger.com/home),
url-prefix(http://www.blogger.com/profile),
url-prefix(http://draft.blogger.com/home),
url-prefix(http://draft.blogger.com/profile),
url-prefix(http://www.blogger.com/features),
url-prefix(http://draft.blogger.com/features),
url-prefix(http://www.blogger.com/upload-image.g),
url-prefix(http://www.blogger.com/choose-gadget) {

/* font-family */
* {font-family:tahoma!important;}



/* font-size */
* {font-size:99%;}
.options {font-size:7pt!important;}  /* http://www.blogger.com/post-create.g */
div.blog-title-wrapper {font-size:10pt!important;} /* http://draft.blogger.com/home */


/* text-align */
* {text-align:justify;}

/* text-decoration */
a, a:hover, a:visited {text-decoration: none !important; font-weight: normal !important;}

}
/* -------------------------------------------------- */
/* google calendar */
@-moz-document 
url-prefix(http://www.google.com/calendar),
url-prefix(https://www.google.com/calendar) {

/* http://www.google.com/calendar */
.chip dd  {
font-family: Tahoma !important;
font-size: 8pt !important;
direction: rtl !important;
text-align: right !important;
}

.textbox-fill-mid,.textbox-fill-input {
font-family: Tahoma !important;
font-size: 8pt !important;
}

.eb-title, .rb-n, .lv-event-title  {
font-family: Tahoma !important;
font-size: 8pt !important;
direction: rtl !important;
text-align: right !important;
}

.fieldinput {
font-family: Tahoma !important;
font-size: 8pt !important;
}

.mbox-cont {
font-family: Tahoma !important;
font-size: 8pt !important;
}

input, textarea, select {
font-family: Tahoma !important;
font-size: 8pt !important;
}

}
/* -------------------------------------------------- */
/* google groups  */
@-moz-document domain("groups.google.com") {

tbody a  {
font-family: Tahoma !important;
}

td, input {
font-family: Tahoma !important;
}

#inbdy  {
direction: rtl !important;
/* ver4eng */
}

#g_body, #g_body p {
font-family: tahoma !important;
}

}
/* -------------------------------------------------- */
/* userstyles.org  */
@-moz-document domain(userstyles.org) {

#long-description {
font-family: Tahoma !important;
font-size: 9pt !important;
line-height: 15pt;
direction: rtl;
max-width: 860px !important;
}

}
/* -------------------------------------------------- */
@-moz-document domain("fa.forums.wordpress.com") {

* {font-family: tahoma !important;}

.post, .col-10 {text-align: justify;}

}

/* -------------------------------------------------- */
/* creator.zoho.com */
@-moz-document domain("creator.zoho.com") {

.zc-textarea {
font-family: Tahoma !important;
font-size: 8pt !important;
direction: rtl;
}

.zc-formbutton,
.zc-formbutton-hover,
.zc-dropdown,
.zc-inputtext {
font-family: Tahoma !important;
}

}
/* -------------------------------------------------- */
/* brizzly.com */
@-moz-document domain("brizzly.com") {

/* brizzly twitter */
.tweet-text {
font-family: Tahoma !important;
font-size: 9pt !important;
direction: rtl!important;
display: inline-block !important;
text-align: right;
float: right!important;
}

.autolinked {
display: inline-block !important;
}

textarea.tweet-form-input {
font-family: Tahoma !important;
font-size: 9pt !important;
direction: rtl;
}

/* brizzly facebook */
.fb-status-content, .attachment-name {
font-family: Tahoma !important;
font-size: 9pt !important;
direction: rtl;
float: right!important;
text-align: right;
}

.fb-comment-input, textarea.fb-update-area, .fb-comment-content, .r-small {
font-family: Tahoma !important;
font-size: 9pt !important;
direction: rtl;
}

}
/* -------------------------------------------------- */
/* goodreads.com */
@-moz-document domain("goodreads.com") {

/* font-family */
* {font-family:tahoma!important;}

/* direction: rtl */
#comment_list,
.bigBoxBody {direction: rtl;}

/* text-align */
* {text-align:justify!important;}

/* font-size */
*{font-size:97%!important;}

/* line-height */
* {line-height:1.5em;}

}
/* -------------------------------------------------- */
/* Other */
