@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.google.com/search"), url("http://www.google.com/") {

	/* Cleaner Google Skin version 0.21 */

	/* Set the font for all elements */
	*, td a {font:12px ProFontWindows, Monaco !important}
	td a span {font:7px Tahoma !important}

	/* Link color */
	a {color:#900 !important}

	/* Hide & replace the logos */
	img[src="/intl/en_ALL/images/logo.gif"] {border:1px solid #fff !important; width:0 !important; height:0 !important; padding:55px 138px !important; background:url("data:image/gif;base64,") no-repeat !important}
	#logo {display:none !important}

	/* Style for input elements */
	input {background:#fff !important; border:1px solid #900 !important; height:22px !important; margin:2px !important; padding:2px 4px !important}
	input[type="submit"] {vertical-align:-2px;}

	/* Dotted line at top of page */
	#gbarl {border-top:1px dotted #900 !important}

	/* Style for result links */
	h2.r {background:#e5ecf9 !important; border:2px solid #e5ecf9 !important}
	a.l:before {content: "Â« " !important}
	a.l:after {content: " Â»" !important}

	/* Hide G o o o o o o o o g l e */
	#np, #nf, #nc, .nr, #nn {display:none !important}

	/* Style for page links */
	#navbar td {padding:2px 5px!important}

	/* Hide ads */
	#mbend {display:none !important}

	/* Add "Cleaner" text */
	p font:before, .ft font:last-child:before {content:"Cleaner " !important}
}
