css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.socom.com") {
 .navbar{
  background-color:#333333!important;
 }
 a.navbar_link{
  text-decoration:none!important;
 }
 a.navbar_link:hover{
  color:#FFFFFF!important;
  text-decoration:none!important;
 }
 .navbar_text{
  color:#FFFFFF!important;
 }
 .headingbar{
  background:none!important;
  background-color:yellow!important;
  color:#111111!important;
  text-align:left!important;
 }
 .cell_hi,.cell_small_hi{
  background:none!important;
  background-color:#121212!important;
 }
 .cell_small_hi{
  text-align:center!important;
 }
 .cell_hi a,.cell_small_hi a{
  background:none!important;
  text-decoration:none!important;
 }
 .cell_hi a:hover,.cell_small_hi a:hover{
  background:none!important;
  color:#ffffff!important;
  border-bottom:1px dotted yellow!important;
 }
 .cell_1,.cell_2,.cell_small_1,.cell_small_2{
  background:none!important;
  background-color:#1b1b1b!important;
  color:#cccccc!important;
 }
 .cell_small_1,.cell_small_2{
  text-align:center!important;
 }
 .cell_1 a,.cell_2 a,.cell_small_1 a,.cell_small_2 a{
  background:none!important;
  color:#cccccc!important;
  text-decoration:none!important;
 }
 .cell_1 a:hover,.cell_2 a:hover,.cell_small_1 a:hover,.cell_small_2 a:hover{
  background:none!important;
  color:#ffffff!important;
  border-bottom:1px dotted yellow!important;
 }
 .count_text{
  color:#cccccc!important;
 }
 a.subj_unread{
  color:yellow!important;
 }
 .auth_text span[style="color: rgb(178, 122, 61); font-weight: bold;"]{
  color:#cccccc!important;
  font-size:10px!important;
  font-weight:normal!important;
 }
 a.auth_text:hover span{
  color:yellow!important;
  border-bottom:1px dotted white!important;
 }
 .count_new_text{
  color:#cccccc!important;
  text-decoration:none!important;
 }
 .date_text,.time_text{
  color:#747474!important;
 }
 img[src="http://forums.socom.com/i/p.gif"]{
  display:none!important;
 }
 .menubar{
  background-color:#111111!important;
  color:yellow!important;
 }
 .msg_cell,.msg_user_cell{
  background:none!important;
  background-color:#1b1b1b!important;
  font-size:10px!important;
  text-align:center!important;
 }
 .msg_cell_selected,.msg_cell_selected p{
  background-color:#0099FF!important;
  color:#1b1b1b!important;
 }
 td.msg_date_cell{
  height:20px!important;
  background-color:#1b1b1b!important;
  -moz-border-radius-bottomright:5px!important;
 }
 .message-tagging-cell,.msg_rating_cell{
  background:none!important;
  background-color:#666666!important;
 }
 .msg_rating_cell br{
  display:none!important;
 }
 .text_smallest{
  color:yellow!important;
 }
 a.text_smallest:hover{
  text-decoration:none!important;
  color:#ffffff!important;
  border-bottom:1px dotted yellow!important;
 }
 u{
  text-decoration:none!important;
 }
 .subjectbar,.subjectbar_text{
  background:none!important;
  background-color:yellow!important;
  color:#111111!important;
 }
 .userbar{
  background:none!important;
  background-color:#0099FF!important;
 }
 .userbar_text{
  color:#222222!important;
 }
 a.userbar_text:hover{
  color:#000000!important;
  text-decoration:none!important;
  border-bottom:1px dotted #000000!important;
 }
 .group-title,.module-title{
  background:none!important;
  background-color:yellow!important;
  color:#111111!important;
 }
 div.tag-group,.tagging-table-bottom,.tagging-users,.li-tagging,.module-cell{
  background-color:#1b1b1b!important;
 }
 div.tag-group a,.tagging-table-bottom a,.tagging-users a,.li-tagging a,.module-cell a{
  background:none!important;
  text-decoration:none!important;
  color:#dddddd!important;
 }
 div.tag-group a:hover,.tagging-table-bottom a:hover,.tagging-users a:hover,.li-tagging a:hover,.module-cell a:hover{
  background:none!important;
  border-bottom:1px dotted yellow!important;
  color:#ffffff!important;
 }
 a span.tagging-user span[style="color: rgb(178, 122, 61); font-weight: bold;"]{
  color:#cccccc!important;
  font-weight:normal!important;
  font-size:10px!important;
 }
 a:hover span.tagging-user span{
  color:yellow!important;
  border-bottom:1px dotted white!important;
 }
 .menubar_standalone{
  background-color:#111111!important;
 }
 .menubar_standalone a{
  background:none!important;
  text-decoration:none!important;
 }
 .menubar_standalone a:hover{
  background:none!important;
  font-weight:bold!important;
  font-size:11pt!important;
 }
 a.menubar_text{
  text-decoration:none!important;
 }
 a.menubar_text:hover{
  text-decoration:none!important;
  font-weight:bold!important;
  font-size:11pt!important;
 }
 .searchbar{
  background:none!important;
  background-color:#0099FF!important;
 }
 a.searchbar_text{
  color:#333333!important;
  text-decoration:none!important;
  font-size:10px!important;
 }
 a.searchbar_text:hover{
  color:#111111!important;
  border-bottom:1px dotted white!important;
 }
 .UserNavigationBar{
  background:none!important;
  background-color:#0099FF!important;
 }
 .BreadCrumb{
  background:none!important;
  background-color:#333333!important;
 }
 a.LinkDisplay{
  text-decoration:none!important;
 }
 a.LinkDisplay:hover{
  color:#ffffff!important;
 }
 .final-crumb{
  color:#FFFFFF!important;
 }
 .BaseBar{
  background:none!important;
  background-color:#333333!important;
 }
 .feedback{
  background:none!important;
  background-color:#111111!important;
  border:1px solid #0099FF!important;
 }
.message-editor-fields{
  background:none!important;
  background-color:#111111!important;
  border:1px solid #0099FF!important;
  color:yellow!important;
 }
 .editor-label{
  color:yellow!important;
 }
 .editor-button-wrapper{
  background-color:yellow!important;
  border:none!important;
  border:1px solid #333333!important;
 }
 .editor-button-wrapper:hover{
  background-color:#333333!important;
  border:none!important;
  border:1px solid yellow!important;
 }
 a.editor-button{
  color:#111111!important;
 }
 a.editor-button:hover{
  color:yellow!important;
 }
 #preview{
  color:#111111!important;
 }
 #preview:hover{
  color:yellow!important;
 }
 .message-meta-data{
  background:none!important;
  background-color:#111111!important;
  border:1px solid #0099FF!important;
 }
 .editor-label{
  color:#0099FF!important;
 }
 .li-mod,.li-mod_banner,.li-mod-body{
  background:none!important;
  background-color:#333333!important;
 }
 .li-mod-header{
  background:none!important;
  background-color:yellow!important;
  color:#111111!important;
 }
 .ranking{
  text-align:center!important;
 }
 div.username span[style="color: rgb(178, 122, 61); font-weight: bold;"]{
  color:white!important;
 }
 div.ranking span[style="font-weight: bold; color: rgb(178, 122, 61);"]{
  color:yellow!important;
 }
}
