css:
@-moz-document url-prefix(http://itharagaian.duckdns.org/nft/Market.html) {
     body > div {
         text-align: center;
    }
     td, th {
         border: none;
         text-align: center;
         padding: none;
    }
     thead tr {
         height: 50px;
         text-align: center;
         color: white;
         font-family: arial;
         border-bottom: solid 1px white;
    }
     tbody tr {
         height: 130px;
         text-align: center;
         color: white;
         font-family: arial;
    }
     img {
         margin: auto;
         display: block;
    }
     td button {
         border: none;
         padding: 0.5em 0.8em 0.5em 0.8em;
         font-size: 1.5em;
         color: #FFFFFF;
         font-weight: bold;
         height: 100%;
         display: inline-block;
         border-radius: 20px;
         cursor: pointer;
    }
     td button:first-child {
         background-color: #33af0a;
         margin-right: 1em;
    }
     td button:first-child:hover {
         background-color: #00Ff0a;
    }
     td button:last-child {
         background-color: #af0a43;
    }
     td button:last-child:hover {
         background-color: #FF0043;
    }
     #LogButton {
         border: none;
         padding: 0.5em 0.8em 0.5em 0.8em;
         font-size: 1.5em;
         color: #FFFFFF;
         font-weight: bold;
         height: 100%;
         display: inline-block;
         border-radius: 20px;
         cursor: pointer;
         background-color: #afAAFF;
    }
     h2 {
         font-family: calibri;
         font-size: 5em;
         text-align: center;
         color: #eff44a;
         margin: 0.5em;
    }
     #filter {
         border: none;
         padding: 0.5em 0.8em 0.5em 0.8em;
         font-size: 1.5em;
         color: #FFFFFF;
         font-weight: bold;
         height: 100%;
         display: inline-block;
         border-radius: 20px;
        /*! cursor: pointer;
         */
         background-color: #85ae82;
    }
     #resetsort {
         border: none;
         padding: 0.5em 0.8em 0.5em 0.8em;
         font-size: 1.5em;
         color: #FFFFFF;
         font-weight: bold;
         height: 100%;
         display: inline-block;
         border-radius: 20px;
         cursor: pointer;
         background-color: #3e8e56;
         margin-left: 1em;
    }
     #sortedinfo {
         display: block;
         text-align: center;
         font-family: arial;
         line-height: 2em;
         font-size: 1.5em;
         color: white;
    }
}
