css:
@-moz-document domain("pikabu.ru") {
.story-comments__all {
    display: flex;
    flex-direction: column;
}

.comments {
    order: 1;
}

.story-comments__menu {
    padding-bottom: 1em;
    border-bottom: none;
}

section[data-role="answer"] {
    order: 0;
    border-top: none;
    border-bottom: none;
}

.comment-reply {
    margin: 0 24px;
}

.input_comment-reply .input__input {
    padding: 12px 16px;
}

.comment-reply__controls {
    justify-content: space-between;
    padding: 12px;
    background-color: white;
    border-top: none;
    border-bottom-right-radius: 10px;
    border-bottom-left-radius: 10px;
}

.comment-reply__controls li:first-child {
    order: 3;
    margin-right: 0;
}

.comment-reply__controls .comment-reply__draft-date{
    margin-right: 15px !important;
}


.comment-reply_no-border {
    border: 1px solid var(--color-black-440);
    border-radius: 10px;
}

.input_comment-reply {
    background: white;
    /* border: 1px solid rgba(0,0,0,.03); */
    border-radius: 10px;
    border-bottom-right-radius: 0;
    border-bottom-left-radius: 0;
    /* box-shadow: 0 0 0 3px rgb(70 131 217 / 12%); */
    color: black;
}

.input__input {
    min-height: 48px;
}

.input_comment-reply .input__input.medium-editor-placeholder::after, .input_comment-reply .input__input.medium-editor-placeholder-relative::after {
    left: 18px;
}

button, .button {
    border-radius: 8px;
}
}
