/* ==UserStyle==
@name           9/14/2022, 3:14:53 PM
@namespace      github.com/openstyles/stylus
@version        1.0.4
@description    A new userstyle to force Notion loads RTL
@author         Mehrdad Khoddami
==/UserStyle== */

@-moz-document regexp("(https:\/\/)*(www\.)*(notion\.so)(.*)*") {
	.notion-page-content,
	.notion-frame,
	.notion-sidebar,
	.notion-overlay-container {
		direction: rtl;
		text-align: right;
		unicode-bidi: embed;
	}


	.notion-frame .notion-scroller .notion-page-controls svg,
	.notion-overlay-container .notion-scroller .notion-page-controls svg {
		margin-right: 0 !important;
		margin-left: 6px
	}

	.notion-topbar svg.check,
	.notion-overlay-container svg.check {
		margin-right: 0 !important;
		margin-left: 6px;
	}
	.notion-frame .notion-page-content .notion-quote-block .notranslate {
		border-left: 0 !important;
		border-right: 3px solid;
	}

	.notion-frame .notion-page-content .notion-to_do-block .notranslate,
	.notion-frame .notion-page-content .notion-bulleted_list-block .notranslate {
		text-align: right !important;
	}
  
  .notion-scroller:nth-child(2) {
    direction: rtl;
    unicode-bidi: embed;
    margin: auto 5px;
  }


	/* Text */
	.notranslate {
		text-align: right !important;
		margin: 0 2px;
	}

	/* Page Block */

	.notion-page-block > a > div > div {
		margin-left: auto;
		margin-right: 0 !important;
	}

	/* Page Props */
	.notion-scroller:nth-child(2) {
		direction: ltr;
		unicode-bidi: embed;
	}
	/* Page Header */
	.notion-frame .notion-scroller > div {
		direction: rtl;
		unicode-bidi: embed;
	}
	/* Turn into */
	.vertical {
		direction: ltr;
		unicode-bidi: embed;
	}
  
  	.notion-close-sidebar,
   	.notion-open-sidebar {
     	transform: rotate(3.142rad);
    }

	.notion-scroller:nth-child(2) {
		direction: rtl;
		unicode-bidi: embed;
		margin-left: 10px;
	}

	/* Code Block */
	.notion-code-block div {
		text-align: left !important;
		direction: ltr;
		unicode-bidi: embed;
	}
	.notion-collection-item {
		direction: rtl;
		unicode-bidi: embed;
	}

	.notion-focusable span {
		margin: auto 5px;
	}

	.notion-focusable div {
		margin: auto 2px;
	}

	.notion-focusable div img {
		width: auto;
	}

	.notion-scroller.vertical.horizontal {
		direction: rtl;
		align-items: flex-start;
	}

	.notion-scroller.vertical.horizontal > div {
		left: auto !important;
		right: 0;
		width: 100%;
		align-items: flex-start;
	}

	.notion-cursor-listener,
	.notion-topbar div:first-child {
		flex-direction: row-reverse;
	}

	.notion-collection-view-item-add {
     	flex-direction: row-reverse;
    }

	#notion-app .notion-sidebar-container {
		float: right;
		right: 0;
		position: absolute !important;
		max-height: 100%;
		height: 100vh;
	}

	#notion-app .notion-frame {
		float: left;
		left: 0;
		position: fixed;
	}

	.notion-help-button {
		right: 100%;
		left: 33px;
	}
    .notion-focusable[role="button"] > div {
			border-radius: 0 12px 12px 0;
      		flex-direction: row-reverse;
    }
  
    .notion-focusable[role="button"] > div > div {
     		margin: auto 4px !important;
    }
  
  .notion-view-settings-sidebar {
		direction: ltr;
		text-align: left;
  }
  
}
