@-moz-document domain("diyguitarpedals.com.au") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

selector
{
	text-transform: capitalize!important;
}

#cart,
#moduleMenu-wrapper,

.betterCategories a,
.sideBoxContent,
.tabTable,

:focus,
ol#controls li a
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

#productPrices
{
	font-size: 2.5rem!important;
}

#productDescription
{
	font-size: 1.85rem!important;
}

#cartAdd,
#navMain ul,

.larger,
.sideBoxContent,

ADDRESS,
LABEL,
LEGEND,
h4, h5, h6,
h4.optionName,
body
{
	font-size: 1.52rem!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

ol#controls li
{
	padding: 13px 26px!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

#control_div {
    width: auto;
}

.leftBoxContainer, .rightBoxContainer {
    width: 20rem!important;
}

#mainWrapper
{
	width: 98%!important;
}

#navColumnOne
{
	width: 15%!important;
}

}

@-moz-document domain("diyguitarpedals.com.au") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label,
ol#controls li.current a
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

#productDescription,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.menuTitle,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

#productPrices,

.prodListPrice
 {
	color: #07ff24!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

}

@-moz-document domain("diyguitarpedals.com.au") {
/* Background Settings */

#contentMainWrapper,
#navColumnOne,

.sideBoxContent,

body,
html
{
	background: #000!important;
}

#navMainWrapper,
.leftBoxHeading,
.rightBoxHeading
{
	background-image: none!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#cart,
#headerWrapper,
#navBreadCrumb,
#moduleMenu-wrapper,

.betterCategories a,

ol#controls li a
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

[alt="Search"]
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

[alt="Search"]::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

[alt="Search"]:hover::before
 {
  opacity: 1!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.header-checkout
{	
	background: radial-gradient(circle, #09b705a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.header-checkout::before
{
	background: radial-gradient(circle, #000 0%, #09b705 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.header-checkout:hover::before {
  opacity: 1!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.leftBoxContainer,
.rightBoxContainer
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.leftBoxContainer:hover,
.rightBoxContainer:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

#footer
{
	background: linear-gradient(to top, #1f1c1c 0%, #000 100%)!important;
}

/* Code 
_____________________________________________________________________________________________________*/

pre
{
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
	background-size: 100%!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.betterCategories a:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

ol#controls li.current
{
	background: radial-gradient(ellipse at center, #00809d 0%,#0e0e0e00 70%)!important;
	padding: 13px 26px!important;
}


}

@-moz-document domain("diyguitarpedals.com.au") {
/* Image Settings */

.centerBoxContentsAlsoPurch,
.tabTable tr
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.centerBoxContentsAlsoPurch:hover,
.tabTable tr:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img:not([alt="the shopping cart"])
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

@-moz-document domain("diyguitarpedals.com.au") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

/* Shopping Cart ___________________________________________________________________*/

[alt="the shopping cart"]
{
	background: url(https://i.ibb.co/42GPC9s/shoppingcart.png) no-repeat!important;
	box-sizing: border-box;
}

[alt="the shopping cart"]
{
	background-size: 35px!important;
	height: 36px!important;
	padding-left: 36px!important;
	width: 1px!important;
}

}

@-moz-document domain("diyguitarpedals.com.au") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
