css:
@-moz-document domain("allnovelfull.com") {
/* HEADER */
.navbar-default{
  background: #000;
  border-bottom: 0;
}
#nav > div > div.navbar-header > h5 > a{
  width: 200px;
  font-family: 'Nunito', sans-serif;
  text-transform: capitalize;
  font-weight: bold;
  font-size: 0pt;
  visibility: hidden;
}
#nav > div > div.navbar-header > h5 > a:before{
  padding: 5px;
  content: 'All Novel Full';
  font-size: 16pt;
  visibility: visible;
  position: absolute;
  margin-left: 45px;
}
#nav > div > div.navbar-header > h5 > a > img{
  visibility: visible;
}
.container > .navbar-collapse{
  margin-left: 256px;
}
.dropdown-toggle > span{
  margin: 0 2px 0 2px;
}
.form-control, .input-group-btn > button{
  background-color: #000 !important;
  border-color: #282828 !important;
  
}
}
@-moz-document regexp("https://allnovelfull.com/.-*[a-zA-Z0-9].{0,}/(?:chapter-[0-9]{0,}|chapter-[0-9]{0,}-.-*[a-zA-Z0-9].{0,}).html") {
body{
  background-color: #232323;
}
/* HEADER */
.navbar-default{
