.dropdown-toggle > span{
  margin: 0 2px 0 2px;
}
.form-control, .input-group-btn > button{
  background-color: #000 !important;
  border-color: #282828 !important;
  
}
.navbar-breadcrumb{
  width: 100%;
  background: #121212;
  margin: 0;
  padding: 2px 0;
}
.breadcrumb-container{
  width: 70%;
}
/* MAIN */
#container{
  background-color: #232323 !important;
  color: #fff;
}
/* NOVEL TITLE */
.chapter .truyen-title{
  margin-top: 24px;
  font-size: 24px;
  font-family: 'Nunito', sans-serif;
  color: /*[[color]]*/ !important;
  font-weight: bolder;
  text-transform: capitalize;
}
.chapter .chapter-title{
  color: #fff;
}
#chapter-content > h2,
#chapter-content > h3,
#chapter-content > h4{
  color: /*[[color]]*/ !important;
}
#chapter-content{
  font-family: 'Nunito', sans-serif;
  background-color: #121212;
  padding: 24px 48px 12px 48px;
  width: 85%;
  margin: auto;
  color: #fff;
  font-family: 'Nunito', sans-serif !important;
  user-select: /*[[userselect]]*/;
}
.container{
  padding-left: 48px;
  padding-right: 48px;
}
/*
-- BUTTONS
*/
.btn-success{
  background: #000;
  color: #fff;
  border-width: 0 0 3px 0;
  border-color: #383838;
}
.btn-success:hover,
.btn-success:active,
.btn-success.active,
.btn-success.active:visited{
  background: #000 !important;
  color: #fff !important;
  border-width: 0 0 3px 0 !important;
  border-color: /*[[color]]*/ !important;
}
.btn-success:focus{
  background: #000;
  color: #fff;
  border-color: /*[[color]]*/ !important;
}
.btn-success.disabled:hover,
  .btn-success[disabled]:hover,
  fieldset[disabled] .btn-success:hover,
  .btn-success.disabled:focus,
  .btn-success[disabled]:focus,
  fieldset[disabled] .btn-success:focus,
  .btn-success.disabled.focus,
  .btn-success[disabled].focus,
  fieldset[disabled] .btn-success.focus{
    background-color: #000;
    border-color: /*[[color]]*/ !important;
  }
.box-notice{
  border-color: /*[[color]]*/;
}
.bg-info{
  background-color: #121212;
}
.footer{
  background-color: #000;
  border-color: #121212;
  color: #aaa !important;
}
.footer a{
  color: #aaa;
}
}
