css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document /*domain(zip.4chan.org),
domain(img.4chan.org),
domain(orz.4chan.org),
domain(cgi.4chan.org),
domain(boards.4chan.org)*/
url-prefix(http://boards.4chan.org/a/) {
    body[bgcolor] {
	background-color:#000000 !important;
 color:#FFFFFF !important;
 background-image:url(http://img194.imageshack.us/img194/9036/dollarsbg.png) !important;
 background-position: right !important;
 background-repeat:no-repeat !important;
 background-attachment:fixed !important
}

.replytitle{
    color: rgb(255,91,193) !important;
    opacity:0.75
}

.omittedposts{
    color: rgb(50,170,255) !important;
}

.spoiler{
    background-color:#303030 !important;
    color: #303030 !important;
}

span.spoiler:hover{
    background-color:#404040 !important;
    color: #FFFFFF !important;
}

.filterreason{
    color: #FFFFFF !important;
}

.rules{
    color: #FFFFFF !important;
}

.reply {
/*opacity: .75 !important;*/
background-color:#000000 !important;
color:#FFFFFF !important;
/*border:solid 5px rgb(255,255,255) !important;*/
border:solid 5px rgb(166,172,170) !important;
-moz-border-radius: 10px;
}

.replyhl {
/*opacity: .75 !important;*/
background-color:#001E30 !important;
color:#FFFFFF !important;
/*border:solid 5px rgb(255,255,255) !important;*/
border:solid 5px rgb(166,172,170) !important;
-moz-border-radius: 10px;
}

.reply, .pages
{
    background-image: url(http://img10.imageshack.us/img10/5233/75dollars.png) !important;
    background-position:right !important;
    background-repeat:no-repeat !important;
    background-attachment:fixed !important;
}

.replyhl
{
    background-image: url(http://img695.imageshack.us/img695/4151/75dollarshighlight.png) !important;
    background-position:right !important;
    background-repeat:no-repeat !important;
    background-attachment:fixed !important
}

.logo,#header,.reply,.replyhl {
    color:rgb(166,172,170) !important;
}


.pages {
    color:rgb(50,206,242) !important;
    background-color:transparent !important;
}
.postername {
    color:#45e441 !important;
    opacity:0.75
}
[class*="postertrip"] {
    color:rgb(62,151,57) !important;
    opacity:0.75
}
.commentpostername {
    color:#45e441 !important;
    opacity:0.75
}
.linkmail {
    color:#FFFFFF !important;
    opacity:0.75
}
.filetitle {
    color:rgb(249,232,80) !important;
    opacity:0.75
}
img.thumb,img[src*="/thumb/"] {
    border:solid 2px rgb(255,255,255) !important;
}
.delform {
    color:rgb(255,255,255) !important;
}
.postarea .postblock{
    -moz-border-radius:15px /*2px 0 0 2px*/ !important;
    margin-right:-1px !important;
    padding:0 3px !important;
    border:solid 0px #555 !important;
    border-right-style: dashed !important;
    text-align:center !important;
    background-color:rgb(255,255,255) !important;
}
a{text-decoration:none !important;color:rgb(0,206,242) !important;}

a.quotejs:hover, a:hover{/*font-weight:bold !important*/;color:rgb(0,206,242) !important;}
}

a:visited{
color:#FFA4FF !important;
}
