css:
@-moz-document domain("ya.ru")
{
body {
  background: url("https://i.ibb.co/XZK8z0G/image.png") no-repeat;
   height: 100vh;
   --color-g-bg-primary: rgba(255,255,255,0);
}
.search3__input {
	color: #050992;
}
.search3__inner {
border: 0.15rem solid #fc0;
border-radius: 0.7rem;
background: #eef;
}

@media (min-width:640px) {	
.headline__logo {
	position:absolute;
	top:43vh;
	left:5%;
}

}

@media (min-width:926px) {
.alice-promo__icon {
	position:fixed;
	top:44.5%;
	right:3.5%;
}	
.headline__logo {
	position:absolute;
	top:43.1vh;
	left:10%;
}

}
@media (min-width:970px) {
.alice-promo__icon {
	position:fixed;
	top:44%;
	right:8.5%;
}	
}
@media (min-width:1000px) {
.alice-promo__icon {
	position:fixed;
	top:43.4%;
	right:11.5%;
}	
}
@media (min-width:1136px) {
.alice-promo__icon {
	position:fixed;
	top:43.4%;
	right:15.5%;
}	
.headline__logo {
	position:absolute;
	top:43.3vh;
	left:17%;
}
}
}
