css:
@-moz-document domain("p.mrsu.ru") {
body {
  	font-family: system,-apple-system,BlinkMacSystemFont,'Segoe UI','Segoe WP',Roboto,Ubuntu,Oxygen,Cantarell,'Fira Sans','Helvetica Neue',Helvetica,'Lucida Grande','Droid Sans',Tahoma,'Microsoft Sans Serif',sans-serif;
}

/* navbar */

.navbar {
  	min-height: 74px;
}

.navbar-inverse {
  background: linear-gradient(110deg, rgba(190,27,150,1) 0%, rgba(43,48,108,1) 100%);
  border-color: white;
}

.navbar-inverse::after {
  	content: '';
    display: block;
    width: 100%;
    height: 24px;
    border-radius: 24px 24px 0 0;
    background: #fff;
    position: absolute;
    bottom: -1px;
    left: 0;
}

.navbar-inverse .navbar-brand{
  	color: white;
}

.navbar-form .btn-default {
  	color: white;
    background-color: rgba(255, 255, 255, 0.6);
    border-color: transparent;
}

.navbar-form .btn-default:hover {
  	color: #333;
    background-color: rgba(255, 255, 255, 1);
    border-color: transparent;
}

.navbar-inverse .navbar-nav>li>a {
  	color: rgba(255,255,255,0.6);
}

.btn-primary {
  	background-color: #6c3f99;
  	border-color: #673c91;
}

.nav-pills>li.active>a, .nav-pills>li.active>a:hover, .nav-pills>li.active>a:focus {
  	background-color: #6c3f99;
}

a {
  	color: #6c3f99;
}

a:hover, a:focus {
  	color: #6c3f99;
}

.nav>li>a:hover, .nav>li>a:focus {
  	background-color: #f6f6f6;
}

.navmenu-default {
  	margin-top: 24px;
}

nav.navmenu.navmenu-default.navmenu-fixed-left.myMenu-left::-webkit-scrollbar {
  width: 6px;
}

nav.navmenu.navmenu-default.navmenu-fixed-left.myMenu-left::-webkit-scrollbar-thumb {
  height: 50px;
    background-color: #d5d5d5;
    border-radius: 3px;
}

.navmenu-default, .navbar-default .navbar-offcanvas {
  	background-color: #ffffff;
}

.badge {
  	background-color: #6c3f99;
}

.body-content {
  	margin-top: 24px;
}


/* блоки с информацией о дисциплине */

.panel-default>.panel-heading {
  	border-color: #000000;
  	background-color: #ffffff00;
}

h3.panel-title {
  	font-size: 20px;
}

.panel-default {
  	border-color: transparent;
}

.panel-warning {
  	border-color: transparent;
}

.discipline-box a>div {
  	background-color: transparent;
 		border: none;
  	box-shadow: none;
  	-webkit-box-shadow: none;
}

/* Расписание кнопки*/
.pagination>.active>a, .pagination>.active>span, .pagination>.active>a:hover, .pagination>.active>span:hover, .pagination>.active>a:focus, .pagination>.active>span:focus {
  	background-color: #6c3f99;
  	border-color: #58327e
}

.pagination>li>a:hover, .pagination>li>span:hover, .pagination>li>a:focus, .pagination>li>span:focus {
  	color: #59337e;
}

.btn-primary:active, .btn-primary.active, .open>.dropdown-toggle.btn-primary {
  	background-color: #6c3f99;
  	border-color: #58327e
}

.btn-primary:active:hover, .btn-primary:hover, .btn-primary.active:hover, .open>.dropdown-toggle.btn-primary:hover, .btn-primary:active:focus, .btn-primary.active:focus, .open>.dropdown-toggle.btn-primary:focus, .btn-primary:active.focus, .btn-primary.active.focus, .open>.dropdown-toggle.btn-primary.focus {
  	background-color: #58327e;
  	border-color: #58327e
}

.pagination>li>a, .pagination>li>span {
  	color: #666;
} 

::-webkit-scrollbar {
  width: 6px;
}

::-webkit-scrollbar-thumb {
  height: 50px;
    background-color: #d5d5d5;
    border-radius: 3px;
}
}
