css:
@-moz-document domain('online.mospolytech.ru'), url-prefix('http://online.mospolytech.ru') {
body {
  font-family: Arial, Geneva, times new roman,"DefaultFont",sans-serif;
  font-weight: 500;
}

#page-wrapper {
  padding: 0 80px;
}

.clt_course_title {
  height: 63px !important;
  overflow-y: scroll;
   margin-bottom: 6px;
  display: flex;
  align-items: center;
}

.clt_course_title_content {
  font-size: 1.02rem !important;
  line-height: 116%;
  text-align: center;
  color: black;
  font-weight: 500;
}

.clt_course_img {
  box-shadow: 1px 1px 0 #fff, -1px -1px 0 #fff, -1px 1px 0 #fff, 1px -1px 0 #fff!important;
}

.clt_course_catname {
  font-size: 0.8rem !important;
  line-height: 114%;
  color: black !important;
  font-weight: 400;
  padding: 4px;
  text-align: center;
  background: #f0f9ff;
  border-radius: 4px;
}

.clt_coursebox{
    padding: 0.3rem 0.3rem !important;
}

.region-main {
  padding: 0 !important;
}

.dock_item_0_title {
  font-weight: 600 !important;
}

.dockeditem:hover {
  background: none !important;
}

#page-header {
  background-image: url('https://mir-s3-cdn-cf.behance.net/project_modules/2800_opt_1/dcd496117869589.607e2c8b4ec98.png') !important;
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
  padding: 6px 20px;
  filter: drop-shadow(0 -8px 10px blue);
}
.h_top_wrapper {
  background: transparent !important;
}
#dock_bg{
  background: transparent !important;
}
#page-navbar {
  	background:none;
    color: #0080ff;
}
.breadcrumb {
   padding: 1px 0;
   font-size: 0.8rem;
  font-weight: 400;
}
.card-body{
  padding: 0rem!important;
}
.card-text {
  padding: 0rem!important;
}
.block {
  padding: 0.1% 0.9% !important;
  border: none !important;
}
.unlist li div{
  	cursor: pointer;
    color: #290098;
    border-bottom: 2.4px solid;
    margin-bottom: 0.3em;
    border-bottom: 2px solid transparent;
    padding: 3px 6px;
  	border-image: linear-gradient(0.25turn, rgba(255,249,34), rgba(255,0,128), rgba(56,2,155,0));
    border-radius: 13px;
    border-image-slice: 1;
}
.unlist li div:hover{
  background: aliceblue;
  border-radius: 4px 0 4px 0;
}
.h_rightblock_wrapper {
  padding-top: 8px;
}

.container-fluid  .headermain{
  font-weight: 500!important;
}
#h_rightblock_wrapper {
  padding-top: 20px;
}

#h_rightblock_wrapper>div.usernav {
    margin: 12px 0 0 10px;
}
#h_rightblock_wrapper .usermenu .moodle-actionmenu .dropdown-toggle .usertext {
  padding-right: 0;
}
#h_rightblock_wrapper .usermenu .moodle-actionmenu .dropdown-toggle .userbutton .avatars {
      right: auto;
  	position: relative;
}

#h_rightblock_wrapper .usermenu .moodle-actionmenu .dropdown-toggle .userbutton {
	display: flex;
  flex-direction: row-reverse;
  align-items: center;
}
#h_rightblock_wrapper .usermenu .moodle-actionmenu .dropdown-toggle .userbutton .avatar.current img {
  width: auto;
}

.instancename {
  font-weight: 400;
}

.sectionname {
  font-weight: 500 !important;
  font-size: 1.54rem;
  
}
#page-wrapper ul.topics>li, #page-wrapper ul.weeks>li, #page-wrapper ul.format_opentechnology_sections>li {
  border: none;
  margin:0;
}
.section.main:nth-child(2){
  background-color: #fee4cb;
  border-radius: 20px;
}
.section.main:nth-child(3){
  background-color: #e9e7fd;
  border-radius: 20px;
}
.section.main:nth-child(4){
  background-color: #dbf6fd;
  border-radius: 20px;
}
.section.main:nth-child(5){
  background-color: #ffd3e2;
  border-radius: 20px;
}
.section.main:nth-child(6){
  background-color: #c8f7dc;
  border-radius: 20px;
}
.section.main:nth-child(7){
  background-color: #d5deff;
  border-radius: 20px;
}

body.path-course-view #page-wrapper .course-content>ul>li>.content .sectionname, body.path-course-view #page-wrapper .course-content>ul>li>.content .sectionhead, body.path-course-view #page-wrapper .course-content>ul>li>.content .sectionname a, body.path-course-view #page-wrapper .course-content>ul>li>.content .sectionhead a {
  background: none;
}
#page-wrapper ul.topics>li+li, #page-wrapper ul.weeks>li+li, #page-wrapper ul.format_opentechnology_sections>li+li {
  margin-top: 10px;
}

.activityinstance{
  cursor: pointer !important;
}
#dock #dockeditempanel {
  border-radius: 20px;
	border: 0px;
}
.has_dock_top_horizontal #dock .dockeditem .dockedtitle h2 {
  font-weight: 500 !important;
}
.btn {
  border-radius: 6px;
    background-color: #4a8bff;
  color: white !important;
}
.btn-primary {
  background: darkcyan !important;
  border-image: linear-gradient(0.25turn, rgba(255,249,34), rgba(255,0,128), rgba(56,2,155,0));
}
.block a, #page-wrapper .block a {
  display: block;
}
#h_rightblock_wrapper .usermenu .moodle-actionmenu .dropdown-toggle .usertext {
  padding: 0 0px 0 20px;
}
.has_dock_top_horizontal #dock .dockeditem .dockedtitle {
    border-radius: 12px;
}
.has_dock_top_horizontal #dock .dockeditem .dockedtitle:hover {
    background: #b395ff30 !important;
}
.clt_courseinfo_below_image {
  background: bisque;
  border-radius: 6px;
}

.clt_courseinfo_below_image  div{
  font-size: 0.8rem !important;
  display: flex !important;
  align-items: center;
  justify-content: space-evenly;
}

/* Background*/
#page-wrapper {
  background: white !important;
	background-repeat: no-repeat !important;
  background-size: cover !important;
  
}
.block_topten {
  background: none !important;
}
.region-main-content {
  background: none !important;
}
#page-wrapper .container-fluid {
  background: none !important;
}


.clt_coursesblock:not(.clt_passe_partout) .clt_coursebox:hover .clt_course_img:after, .clt_coursesblock:not(.clt_passe_partout) .clt_course_img:hover:after {
  background:  linear-gradient(0.25turn, rgba(255,249,34), rgba(255,0,128), rgba(56,2,155,0)) !important;
	height: 2px !important;
   bottom: -3px;
  border-radius: 2px;
}
}
