css:
@-moz-document domain("real.com") {
  .navbar-default {
    background-color: #0f1418;
  }
  
  .container, .container-fluid {
    background-color: #0f1418;
  }
  
  .section-text {
    color: white;
  }
  
  .section-title {
    color: white;
  }
  
  .section-header {
    color: white;
  }
  
  .bg-grey {
    background: transparent linear-gradient(180deg,#141b20 0,#181e22 100%) 0 0 no-repeat padding-box;
  }
  
  body {
    color: white;
  }
  
  .navbar-default .navbar-nav>li>a {
    color: #fff;
  }
  
  .navbar-nav .dropdown-menu {
    background-color: #13191e;
  }
  
  .navbar-nav .dropdown-menu a {
    color: white;
  }
  
  .navbar-nav .dropdown-menu a:hover {
    background: transparent;
  }
  
  .navbar-default .navbar-collapse, .navbar-default .navbar-form {
    background-color: #1c252c;
  }
  
  .dropdown .navbar-left.visible-sm-block.hidden-md a, .navbar-default .navbar-nav>li>a {
    color: white;
  }
  
  .navbar-default .navbar-nav .open .dropdown-menu>li>a {
    color: white;
  }
  
  .navbar-default .navbar-nav .open .dropdown-menu>li>a:hover {
    background: transparent;
    color: #00a1ff;
  }
  
  .sprite {
    filter: brightness(10);
  }
  
  .sprite:hover {
    filter: brightness(0);
  }
  
  .form-control, .img-thumbnail, body {
    background-color: #0f1418;
  }
  
  :root {
    color-scheme: dark;
  }
}
