css:
@-moz-document domain("ya.ru") {

/* удалить рекламу Яндекс.Недобраузера и Яндекс.Недосервисов */
.dist-stripe, .modal__cell,
.search3__doodle-video, .search3__doodle-link,
.mini-suggest__additional { display: none !important; }

/* снять бесполезное огранимение на минимальную длину страницы */
body, .body__content { overflow: hidden; min-width: 0px; }	


/* полупрозрачность для отвлекающих элементов */
.services-pinned__content:not(:hover),
.alice-promo:not(:hover), .alice-fab:not(:hover), .alice-fab__promo:not(:hover),
.headline__logo:not(:hover), .headline__personal:not(:hover),
.alerts__list:not(:hover) { opacity: /*[[distractions]]*/ !important; }

.services-pinned__content,
.alice-promo,  .alice-fab, .alice-fab__promo,
.headline__logo, .headline__personal,
.alerts__list { transition: opacity .5s !important; }

.services-pinned::before { display: none; }

/* эффект небольшой прозрачности для строки поиска */
html .search3__inner:not(:hover) {
	background: #fffb;
	border-color: #fff0;
	color: #fff0 !important;
}
html.document_dark_yes .search3__inner:not(:hover) {
	background: #2f2f32bb;
}

/* патчи для корректной работы на светлой теме */
html { background-color: #161616 !important; }
html:not(.document_dark_yes) .alert { background: #e8e8e8 !important; }

/* небольшое оттенение для информеров под строкой поиска */
.informers3 * { text-shadow: 0 0 10px #ddd4, 0 0 1px #fff2; } 
html.document_dark_yes .informers3 * { text-shadow: 0 0 10px #3334, 0 0 1px #0002; }
	

/* современный формат отображение сервисов (codename Cerberus) */
/*[[cerberus-test]]*/


/* картинка на заднем фоне */
main::before {
	content: '';
	display: block;	position: absolute;
	width: 102%;	height: 102%;
	z-index: -999;
	background-image: url('/*[[wallpaper]]*/'), url('/*[[wp-base64]]*/');	background-size: cover;
	filter: blur(4px) brightness(0.7) contrast(0.9) saturate(1.2);
	scale: 102%;
}

}
