/* ==UserStyle==
@name           ServiceNow VSDark Theme
@namespace      USO Archive
@author         Paul Paduraru
@description    ServiceNow Dark Theme
@version        1.01
@license        MIT
@preprocessor   uso
==/UserStyle== */
@-moz-document regexp("https:\/\/\w*\.service-now.com\/(?!nav_to.do)((?!\$(?!stream))|(?=$stream))(?!concourseJsDebug)(?!kb_view)(?!sys_report_template)(?!report_home)(?!system_properties_ui).*") {

    :root {
        --background-color: #1E1E1E;
        --background-white: #ffffff;
        --header-color: #383839;
        --font-white: #ffffff;
        --font-grey: #cecbce;
        --font-disabled: #9e9c9c;
        --dark-blue: #0e639c;
        --light-blue: #90aac6;
        --light-grey: #cccccc;
        --anchor-blue: #3385e3;
        --mandatory-red: #f95050;
        --error-red: #E4533B;
        --warning-orange: #FF8800;
        --border-color-white: #ffffff;
        --border-color-blue: #3385e3;
        --border-color-dark: #1E1E1E;
        --border-color-grey: #cecbce;
        --grey-blue: #4e5b6a;
    }


    /* ------------------------------------------------------------ */
    /*                     SECTION: General                         */
    /* ------------------------------------------------------------ */

    HTML[data-doctype="true"] * {
        font-weight: normal;
    }

    HTML[data-doctype="true"] *:focus {
        outline: none !important;
        box-shadow: none;
    }

    HTML[data-doctype="true"] iframe {
        border-color: var(--border-color-dark);
    }

    HTML[data-doctype="true"] body {
        background-color: var(--background-color) !important;
        color: var(--font-grey) !important;
    }

    HTML[data-doctype="true"] body,
    HTML[data-doctype="true"] iframe,
    HTML[data-doctype="true"] table,
    HTML[data-doctype="true"] input:not([id="filter"]),
    HTML[data-doctype="true"] body.section_header_body_no_scroll,
    HTML[data-doctype="true"] TD.bannerLeft,
    HTML[data-doctype="true"] TD.bannerCenter,
    HTML[data-doctype="true"] TD.bannerRight,
    HTML[data-doctype="true"] TR#banner_row,
    HTML[data-doctype="true"] .nav-wrapper,
    HTML[data-doctype="true"] .drag_section_header,
    HTML[data-doctype="true"] BODY.non_standard_lists,
    HTML[data-doctype="true"] .form-control:not([id="filter"]),
    HTML[data-doctype="true"] TD.label,
    HTML[data-doctype="true"] TD.label_right,
    HTML[data-doctype="true"] TD.label_left,
    HTML[data-doctype="true"] TD.foreign,
    HTML[data-doctype="true"] DIV.popup {
        background-color: var(--background-color) !important;
    }


    /* Main Frame */
    HTML[data-doctype="true"] a[style*="color: black"] {
        color: var(--anchor-blue) !important;
    }

    /* Record Producers  */
    HTML[data-doctype="true"] TR.io_label TD {
        background-color: var(--background-color) !important;
    }


    /* Link color */

    HTML[data-doctype="true"] a,
    HTML[data-doctype="true"] a:hover,
    HTML[data-doctype="true"] a:focus {
        color: var(--anchor-blue);
    }

    HTML[data-doctype="true"] .alert-info .alert-link,
    HTML[data-doctype="true"] .notification-info .alert-link {
        color: var(--anchor-blue);
    }

    /* Text Color */
    HTML[data-doctype="true"] BODY,
    HTML[data-doctype="true"] tr.list_even td,
    HTML[data-doctype="true"] tr.list_odd td,
    HTML[data-doctype="true"] .drag_section_header,
    HTML[data-doctype="true"] SELECT:not([readonly="readonly"]),
    HTML[data-doctype="true"] LABEL.nav_menu,
    HTML[data-doctype="true"] A.menu,
    HTML[data-doctype="true"] .form-control,
    HTML[data-doctype="true"] p.header,
    HTML[data-doctype="true"] input.ui-widget-content,
    HTML[data-doctype="true"] .process-breadcrumb>li>a,
    HTML[data-doctype="true"] .process-breadcrumb>li>a:visited,
    HTML[data-doctype="true"] .process-breadcrumb>li>a:hover,
    HTML[data-doctype="true"] TD.label,
    HTML[data-doctype="true"] TD.label_right,
    HTML[data-doctype="true"] TD.label_left,
    HTML[data-doctype="true"] TD.foreign {
        color: var(--font-grey);
    }

    /* Field Message and info boxes */
    HTML[data-doctype="true"] .annotation[data-annotation-type="Info Box Blue"] div,
    HTML[data-doctype="true"] .annotation[data-annotation-type="Section Details"] div,
    HTML[data-doctype="true"] .annotation[data-annotation-type="Text"] div,
    HTML[data-doctype="true"] .alert-info,
    HTML[data-doctype="true"] .notification-info {
        background-color: var(--background-color);
        border-color: var(--border-color-blue);
        color: var(--anchor-blue);
    }

    /* Field messages: close button */

    HTML[data-doctype="true"] button#close-messages-btn {
        color: var(--font-white);
        background-color: transparent;
        text-shadow: 1px 0 0 var(--background-color), 0 -1px 0 var(--background-color), 0 1px 0 var(--background-color), -1px 0 0 var(--background-color);
        opacity: 1;
    }

    HTML[data-doctype="true"] button#close-messages-btn:hover {
        background-color: transparent !important;
        color: var(--background-color) !important;
        text-shadow: 1px 0 0 var(--font-white), 0 -1px 0 var(--font-white), 0 1px 0 var(--font-white), -1px 0 0 var(--font-white);
    }

    /* Error messages */
    HTML[data-doctype="true"] .alert-danger,
    HTML[data-doctype="true"] .notification-danger,
    HTML[data-doctype="true"] .notification-error {
        background-color: var(--error-red);
        color: var(--font-white);
        border-color: var(--border-color-white);
    }

    /* Warning messages */
    HTML[data-doctype="true"] .alert-warning,
    HTML[data-doctype="true"] .notification-warning {
        background-color: var(--warning-orange);
        border-color: var(--border-color-white);
        color: var(--font-white);
    }

    HTML[data-doctype="true"] .outputmsg_warning A,
    HTML[data-doctype="true"] .outputmsg_warning A:link {
        color: var(--background-color);
    }

    /* Top Frame */
    HTML[data-doctype="true"] img#show_loading_gif {
        opacity: 0.5;
    }

    /* Main frame Navbar */

    /* Attachment */
    HTML[data-doctype="true"] LI.attachment_list_items A.content_editable {
        color: var(--light-grey);
    }

    HTML[data-doctype="true"] A.attachment {
        color: var(--anchor-blue);
    }

    /* Breadcrumbs and links */
    HTML[data-doctype="true"] SPAN.breadcrumb_separator,
    HTML[data-doctype="true"] A.breadcrumb_separator {
        color: var(--anchor-blue);
    }

    HTML[data-doctype="true"] BUTTON:not(.btn).breadcrumb_separator {
        background-color: var(--background-color);
        color: var(--anchor-blue);
    }

    HTML[data-doctype="true"] A.web {
        color: var(--anchor-blue);
    }

    HTML[data-doctype="true"] A.web:hover {
        color: var(--anchor-blue);
    }

    HTML[data-doctype=true] A.navigation_link {
        color: var(--anchor-blue);
    }

    HTML[data-doctype=true] a#db_image_url_preview {
        color: var(--anchor-blue) !important;
    }

    /* Scrollbar */
    HTML[data-doctype="true"] ::-webkit-scrollbar {
        width: 12px;
    }

    HTML[data-doctype="true"] ::-webkit-scrollbar-track {
        background-color: var(--header-color);
        -webkit-box-shadow: inset 0 0 6px var(--header-color);
        ;
        box-shadow: inset 0 0 6px var(--header-color);
        border-radius: 10px;
    }

    HTML[data-doctype="true"] ::-webkit-scrollbar-thumb {
        border-radius: 10px;
        -webkit-box-shadow: inset 0 0 10px var(--header-color);
        ;
        box-shadow: inset 0 0 10px var(--header-color);
        ;
        background-color: var(--font-grey);
    }

    /* Buttons & Menus */
    HTML[data-doctype="true"] .btn-default,
    HTML[data-doctype="true"] .btn-destructive-subdued,
    HTML[data-doctype="true"] .btn-success-subdued,
    HTML[data-doctype="true"] .nav-segmented>li {
        background-color: var(--background-color);
    }

    HTML[data-doctype="true"] .btn-default,
    HTML[data-doctype="true"] .btn-destructive-subdued,
    HTML[data-doctype="true"] .btn-success-subdued,
    HTML[data-doctype="true"] .nav-segmented>li {
        color: var(--font-white) !important;
    }

    HTML[data-doctype="true"] .btn:hover,
    HTML[data-doctype="true"] .nav-segmented>li:hover,
    HTML[data-doctype="true"] .btn:focus,
    HTML[data-doctype="true"] .nav-segmented>li:focus {
        color: var(--anchor-blue) !important;
        background-color: var(--background-color) !important;
        border-color: var(--border-color-white) !important;
    }

    HTML[data-doctype="true"] .btn-default:focus,
    HTML[data-doctype="true"] .btn-destructive-subdued:focus,
    HTML[data-doctype="true"] .btn-success-subdued:focus,
    HTML[data-doctype="true"] .nav-segmented>li:focus {
        border-color: var(--border-color-blue);
    }

    HTML[data-doctype="true"] .btn-default:hover,
    HTML[data-doctype="true"] .btn-destructive-subdued:hover,
    HTML[data-doctype="true"] .btn-success-subdued:hover,
    HTML[data-doctype="true"] .nav-segmented>li:hover,
    HTML[data-doctype="true"] .btn-default.hover,
    HTML[data-doctype="true"] .hover.btn-destructive-subdued,
    HTML[data-doctype="true"] .hover.btn-success-subdued,
    HTML[data-doctype="true"] .nav-segmented>li.hover {
        background-color: var(--light-grey);
    }

    HTML[data-doctype="true"] .context_menu {
        background: var(--background-color);
        border-color: var(--border-color-grey);
    }

    HTML[data-doctype="true"] .btn-primary {
        background-color: var(--anchor-blue) !important;
        color: var(--font-white) !important;
        border-color: transparent !important;
    }

    HTML[data-doctype="true"] .btn-icon:active {
        border: var(--anchor-blue);
    }

    HTML[data-doctype="true"] .context_item {
        color: var(--font-white);
    }

    HTML[data-doctype="true"] .context_item.context_menu_hover,
    HTML[data-doctype="true"] .context_item:focus {
        background-color: var(--anchor-blue);
    }


    HTML[data-doctype="true"] BUTTON.edge {
        color: var(--light-grey) !important;
    }

    HTML[data-doctype="true"] span.color-blue-dark {
        color: var(--anchor-blue);
    }

    HTML[data-doctype="true"] BUTTON.edge.active,
    HTML[data-doctype="true"] BUTTON.edge:hover,
    HTML[data-doctype="true"] BUTTON.edge:focus,
    HTML[data-doctype="true"] BUTTON.edge:hover .icon.color-red,
    HTML[data-doctype="true"] BUTTON.edge:hover .icon.color-blue,
    HTML[data-doctype="true"] BUTTON.edge:hover .icon.color-orange,
    HTML[data-doctype="true"] BUTTON.edge:hover .icon.color-green,
    HTML[data-doctype="true"] BUTTON.edge:hover .icon.color-yellow,
    HTML[data-doctype="true"] BUTTON.edge:hover .icon.color-gray,
    HTML[data-doctype="true"] BUTTON.edge:focus .icon.color-red,
    HTML[data-doctype="true"] BUTTON.edge:focus .icon.color-blue,
    HTML[data-doctype="true"] BUTTON.edge:focus .icon.color-orange,
    HTML[data-doctype="true"] BUTTON.edge:focus .icon.color-green,
    HTML[data-doctype="true"] BUTTON.edge:focus .icon.color-yellow,
    HTML[data-doctype="true"] BUTTON.edge:focus .icon.color-gray {
        color: var(--anchor-blue);
    }

    /* ------------------------------------------------------------ */
    /*                     SECTION: Navigation                      */
    /* ------------------------------------------------------------ */
    HTML[data-doctype="true"] .nav_edge,
    HTML[data-doctype="true"] .nav_edge_inner,
    HTML[data-doctype="true"] .edge_bottom_chevron {
        background: var(--header-color) !important;
    }

    HTML[data-doctype="true"] LABEL.nav_menu {
        background: var(--header-color);
    }

    HTML[data-doctype="true"] .navbar {
        padding: 6px 0;
        border-radius: 0;
        border: none;
        box-shadow: rgb(0 0 0 / 8%) 0 1px 2px;
    }

    HTML[data-doctype="true"] .navbar-default {
        background-color: var(--header-color);
        color: var(--font-grey) !important;
    }

    HTML[data-doctype="true"] .navbar-default .navbar-text {
        color: var(--font-white);
        font-weight: normal;
    }

    HTML[data-doctype="true"] .section .navbar-default {
        background-color: var(--header-color);
        border-color: var(--border-color-dark);
    }

    /* Navbar buttons: hamburger, attachment, arrows */
    HTML[data-doctype="true"] .navbar-btn {
        color: var(--font-white);
    }

    /* Navbar title */
    HTML[data-doctype="true"] H1.form_header {
        color: var(--font-white);
    }

    HTML[data-doctype="true"] .navbar-btn:hover {
        background-color: rgba(0, 0, 0, 0.1);
        color: var(--anchor-blue);
    }

    HTML[data-doctype="true"] .navbar-btn:focus {
        background-color: rgba(0, 0, 0, 0.1);
        color: var(--anchor-blue);
    }

    HTML[data-doctype="true"] .navbar .navbar-title {
        color: var(--font-white) !important;
        font-weight: normal;
    }

    /* Template popover link */

    HTML[data-doctype="true"] .popover-item-list .popover-body button,
    HTML[data-doctype="true"] .popover-item-list .popover-body a {
        color: var(--font-white);
    }


    HTML[data-doctype="true"] .popover-item-list .popover-body button:focus,
    HTML[data-doctype="true"] .popover-item-list .popover-body a:focus {
        border: none;
        outline: none;
    }

    HTML[data-doctype="true"] .popover-item-list {
        overflow: hidden !important;
        padding: 1px;
    }

    HTML[data-doctype="true"] .popover-body .row,
    HTML[data-doctype="true"] .popover-body .form-group,
    HTML[data-doctype="true"] .popover-body legend {
        color: var(--font-white);
    }

    HTML[data-doctype="true"] .popover-item-list #document_tags {
        width: 100% !important;
    }

    HTML[data-doctype="true"] .popover-item-list #document_tags li a.tagit-close {
        top: 12px;
    }

    HTML[data-doctype="true"] .popover-item-list #document_tags li a.tagit-close>span {
        color: var(--font-white) !important;
    }

    HTML[data-doctype="true"] .popover-item-list #document_tags li {
        background-color: var(--anchor-blue) !important;
    }

    HTML[data-doctype="true"] .popover-item-list #document_tags li .icon-user,
    HTML[data-doctype="true"] .popover-item-list #document_tags li .icon-user-group,
    HTML[data-doctype="true"] .popover-item-list #document_tags li a {
        color: var(--font-white) !important;
    }

    HTML[data-doctype="true"] .popover-item-list #form_tags {
        margin: 5px !important;
    }

    HTML[data-doctype=true] #templateOverflowContainer .popover-body .template_overflow_items li a {
        color: var(--font-grey);
    }

    HTML[data-doctype="true"] .modal-content {
        background-color: var(--background-color);
    }

    /* UI Actions with dropdown */

    HTML[data-doctype="true"] .btn-group .dropdown-menu {
        background-color: var(--background-color);
        border: 1px solid var(--font-white);
    }

    HTML[data-doctype="true"] .btn-group .dropdown-menu>li>a {
        color: var(--font-white) !important;
    }

    HTML[data-doctype="true"] #connectFollowWidget a:hover {
        background-color: var(--anchor-blue) !important;
    }

    /* ------------------------------------------------------------ */
    /*                     SECTION: Tables                          */
    /* ------------------------------------------------------------ */

    HTML[data-doctype=true] .table>thead {
        background-color: var(--header-color) !important;
    }

    HTML[data-doctype=true] .table>thead>tr>th {
        color: var(--font-white) !important;
    }

    HTML[data-doctype="true"] tr.list_odd td {
        background-color: var(--background-color);
        color: var(--font-white);
    }

    HTML[data-doctype="true"] tr.list_odd .list_hier.button,
    HTML[data-doctype="true"] span label {
        background-color: transparent;
    }


    HTML[data-doctype="true"] tr.list_even .list_decoration_cell {
        background-color: var(--background-color);
        ;
    }

    HTML[data-doctype="true"] tr.list_odd .list_decoration_cell {
        background-color: var(--background-color);
    }

    HTML[data-doctype="true"] tr.list_even td,
    HTML[data-doctype="true"] tr.list_even .list_decoration_cell {
        background-color: var(--background-color);
        ;
    }

    HTML[data-doctype="true"] .list_nav.list_nav_top {
        background-color: var(--header-color);
    }

    HTML[data-doctype="true"] .list_message_row {
        background: var(--background-color);
    }

    HTML[data-doctype="true"] .tabs2_list.list_v2 {
        background-color: var(--background-color);
    }

    HTML[data-doctype="true"] .list_hierarchical_hdr .tabs2_list.list_v2 {
        border-color: var(--border-color-dark);
    }

    HTML[data-doctype="true"] .table td a,
    HTML[data-doctype="true"] .table th a {
        color: #999999;
    }

    HTML[data-doctype="true"] .table.table-hover>tbody>tr:hover>td,
    HTML[data-doctype="true"] .table.table-hover>tbody>tr:hover>th,
    HTML[data-doctype="true"] .table.table-hover>tbody>tr.hover>td,
    HTML[data-doctype="true"] .table.table-hover>tbody>tr.hover>th,
    HTML[data-doctype="true"] .table.table-hover.table-striped.table-detail-row>tbody>tr:hover>td,
    HTML[data-doctype="true"] .table.table-hover.table-striped.table-detail-row>tbody>tr:hover>th,
    HTML[data-doctype="true"] .table.table-hover.table-striped.table-detail-row>tbody>tr.hover>td,
    HTML[data-doctype="true"] .table.table-hover.table-striped.table-detail-row>tbody>tr.hover>th {
        background-color: var(--background-color);
    }

    HTML[data-doctype="true"] .table.table-striped.table-detail-row>tbody>tr:nth-child(4n + 1)>td,
    HTML[data-doctype="true"] .table.table-striped.table-detail-row>tbody>tr:nth-child(4n + 1)>th,
    HTML[data-doctype="true"] .table.table-striped.table-detail-row>tbody>tr:nth-child(4n + 2)>td,
    HTML[data-doctype="true"] .table.table-striped.table-detail-row>tbody>tr:nth-child(4n + 2)>th {
        background-color: var(--background-color);
    }

    HTML[data-doctype="true"] .table.table-striped.table-detail-row>tbody>tr:nth-child(4n + 3)>td,
    HTML[data-doctype="true"] .table.table-striped.table-detail-row>tbody>tr:nth-child(4n + 3)>th,
    HTML[data-doctype="true"] .table.table-striped.table-detail-row>tbody>tr:nth-child(4n + 4)>td,
    HTML[data-doctype="true"] .table.table-striped.table-detail-row>tbody>tr:nth-child(4n + 4)>th {
        background-color: var(--background-color)
    }

    /* ------------------------------------------------------------ */
    /*                     SECTION: Modals                          */
    /* ------------------------------------------------------------ */

    /* Popups */
    HTML[data-doctype="true"] tr.list_odd img.list_popup.list_decoration {
        background-color: var(--background-color);
    }

    HTML[data-doctype="true"] DIV.popup {
        -moz-box-shadow: rgba(255, 255, 255, 0.2) 0 4px 23px 5px;
        -webkit-box-shadow: rgba(255, 255, 255, 0.2) 0 4px 23px 5px;
        box-shadow: rgba(255, 255, 255, 0.2) 0 4px 23px 5px;
    }

    HTML[data-doctype="true"] .glide_pane_header {
        background-color: var(--background-color);
    }

    HTML[data-doctype="true"] #settings_modal {
        color: var(--light-grey);
    }

    HTML[data-doctype="true"] #settings_modal .settings-tabs .settings-tab {
        color: var(--light-grey);
    }

    HTML[data-doctype="true"] .popover {
        background-color: var(--background-color);
    }

    HTML[data-doctype="true"] .popover-header {
        background-color: var(--header-color);
    }

    HTML[data-doctype="true"] .popover-title {
        background-color: var(--header-color);
        color: var(--font-white) !important;
    }


    HTML[data-doctype="true"] rendered_body div[style*='background:#e0e0e0'] {
        background: var(--header-color) !important;
    }

    HTML[data-doctype="true"] .modal-body rendered_body div[style*="background:#e0e0e0"] table {
        background: var(--header-color) !important;
    }

    /* Attachments modal */

    HTML[data-doctype="true"] .modal .modal-header {
        background-color: var(--header-color);
        color: var(--font-white);
    }


    HTML[data-doctype=true] .modal .modal-header .help {
        color: var(--anchor-blue) !important;
    }

    HTML[data-doctype="true"] .modal .modal-header .close {
        color: var(--font-white);
    }

    HTML[data-doctype="true"] td#attachFileCell {
        color: var(--font-white);
        background-color: var(--background-color);
    }

    HTML[data-doctype="true"] input#loadFileXml {
        display: inline-block;
        margin-bottom: 0;
        font-weight: normal;
        text-align: center;
        vertical-align: middle;
        cursor: pointer;
        background-image: none;
        border: 1px solid var(--font-white);
        white-space: nowrap;
        padding: 6px 9px;
        font-size: 13px;
        line-height: 1.42857;
        border-radius: 3px;
        -webkit-user-select: none;
        -moz-user-select: none;
        -ms-user-select: none;
        user-select: none;
    }

    HTML[data-doctype="true"] form[target="upload_target"] table {
        background-color: var(--background-color) !important;
    }

    HTML[data-doctype="true"] #attachment_table_body input[type="checkbox"] {
        color: transparent;
        position: relative;
        margin-right: 8px;
        margin-top: 0;
        background: transparent;
        border: 1px solid var(--font-white);
        display: inline-block;
        box-sizing: border-box;
        width: 20px;
        height: 20px;
        text-align: center;
        vertical-align: middle;
        content: "";
        border-radius: 3px;
        appearance: none;
        -webkit-appearance: none;
        outline: none;
    }

    HTML[data-doctype="true"] #attachment_table_body input[type="checkbox"]:checked:after {
        color: var(--font-white);
        content: "\2714";
    }

    HTML[data-doctype="true"] .modal-body table {
        background-color: var(--background-color) !important;
    }

    HTML[data-doctype="true"] img#please_wait {
        background-color: var(--background-color) !important;
        margin-left: 33% !important;
        margin-top: 23px !important;
    }

    HTML[data-doctype="true"] .modal .modal-header .close:hover,
    HTML[data-doctype="true"] .modal .modal-header .close:focus {
        color: var(--anchor-blue);
        background-color: var(--header-color) !important;
        text-shadow: 0 1px 0 var(--anchor-blue);
    }

    /* Email preview modals */

    HTML[data-doctype="true"] iframe#email_preview_iframe,
    HTML[data-doctype="true"] iframe#simulated_html_iframe {
