css:
@-moz-document url-prefix("https://10ff.net")
{



/* ==UserStyle==
@name           10ff.net - 22/09/2022, 17:17:11
@version        1.0.0
@description    A new userstyle
@author         Rishi581b
==/UserStyle== */
:root{
    --backgroundColor:#11141D;
    --lightgreen: #16ffbd; 
    --darkgreen:#37b76d;
    --pink:#f070a1;
    
}

::-webkit-scrollbar
{
    display: none;
}



body{
    background:var(--backgroundColor);
    padding:14%;
    padding-top:1%;
    
}


.interface-wrapper input{
    background:#ffffffdb;
    box-shadow: rgba(50, 50, 93, 0.25) 0px 30px 40px -22px inset, rgba(0, 0, 0, 0.3) 0px 18px 36px -18px inset;
    text-align:center;
}

#game .place-wrapper{
    background:var(--pink);
    border:none;
    box-shadow: #eb2e76 0px 0px 0px 5px, #eb2e76 -1px 0px 0px -1px, #910038 0px 2px 0px inset;
    margin-bottom:15px;
}
.interface{
    background:var(--backgroundColor);
   
}
.interface-wrapper{
    background:var(--backgroundColor);
}
.btn.btn-new-game{
    background:var(--darkgreen);
}
.map{
    color:var(--lightgreen);
}
.info.info-server{
    color:#ffa600;
}
/*

#game span{
    color:white;
}*/
#game span.highlight{
    background:#ffffffeb;
    box-shadow: rgba(50, 50, 93, 0.25) 0px 30px 60px -12px inset, rgba(0, 0, 0, 0.3) 0px 18px 36px -18px inset;
    backdrop-filter:blur(5px);
    border-radius:10px;
}
#game .zone{
    background:var(--backgroundColor);
    border:none;
}
#game .place{
    color:black;
}

.game-result.more-game-result thead{
    background-color:var(--pink)!important;
}
tr{
    border:3px solid var(--pink);
    
}

td{
    color:#ffa600;
}
td:first-child{
    color:var(--lightgreen);
}
span.correct{
    color:var(--lightgreen);
}

.game-result tbody > tr:nth-child(odd) > td, .game-result > tbody > tr:nth-child(odd) > th{
    background:var(--backgroundColor);
    
}
tbody{
    background:var(--backgroundColor);
}
.game-result{
    background:var(--backgroundColor);
    border:none;
}

label{
    color:#ffa600;
}

.container form input{
    background:#11141d;
    border:3px solid #f070a1;
    border-radius:15px;
    color:#16ffbd;
}

form input[type="submit"]{
    background:var(--darkgreen);
    border:none;
    color:var(--backgroundColor);
    border-radius:10px;
}
::placeholder{
    color:white;
    opacity:0.8;
}


}
