css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.conquerclub.com") {
html, body {color: #000000 !important; background-color:#333333 !important;}

/* header + top nav */
.hnav { height:22px !important; margin:0px !important; padding:0px !important; text-align: left !important; background-color:#333333 !important; background: url(http://img33.imageshack.us/img33/9448/logoct.png) no-repeat !important; }
.hnav ul {padding-left: 205px !important;}
.hnav ul li {}
.hnav ul li a:link, .hnav ul li a:visited{ color:#C0C0C0 !important; }
.hnav ul li a:hover, .vnav ul li a:hover{ color:#FF0000 !important; }
.hnav ul li a{ background: none !important; color:#C0C0C0 !important; border-left:0px !important; border-right:0px !important; padding-left:0px !important;}

/* links */
a, .vnav ul li a {color:#000000 !important;}
a:hover, #invertedtabs a span:hover, .vnav ul li a:hover  {color:#A30202 !important;}
a:visited {color:#000000 !important;}

.vnav ul li a {background-color:#57616B !important;} 
.vnav ul li {border-color: #333333 !important;}
#bobmenuUl li ul {border-color: #A30202 !important;}

#player4 {color:black !important;}

#action-form fieldset {background-color:#D5DADF !important;} 


#contOverviewWrapper {height: 50% !important; } 
#contOverview  {background-color:#BEC4CB !important; padding:5px !important;}
#contOverview span {padding:5px !important;}
#dashboard td{
vertical-align: top !important;}

h4 {display:none !important;}
#time-remaining {display:block !important;}

#dashboard tbody{background-color: transparent !important;}

.insidegame tr, #mapinspect, #redemption, .insidegame fieldset, #chat, #log{background-color:transparent !important;}

table.listing tr.odd td{background-color:#AFB5BB !important;}
table.listing tr.even td{background-color:#C2C9CF !important;}

.insidegame fieldset, #chat, #log, #summary{background-color:#4F5C63 !important;}

#middleColumn, #leftColumn, #rightColumn, #outerColumnContainer, #innerColumnContainer, #pageWrapper, #SOWrap{ background-color:black !important;}

#outerColumnContainer{ background-color:black !important;}

/* Set basic background and border color */
#footer{background-color:#333333 !important; border-color:#333333 !important;}



/* Set slightly lighter background color */
#middleColumn th, #invertedtabs a, #invertedtabs a span, #headerline {background-color:#57616B !important; border-color:transparent !important; color:#000000 !important;}

/* Set lightest background color */
#middleColumn, #leftColumn, #rightColumn, #outerColumnContainer, #innerColumnContainer, #pageWrapper, #SOWrap   {background-color:#79838F !important; border-left-color:transparent !important; border-right-color:transparent !important;}

/* no borders on main elements */
#pageWrapper, #masthead, #innerColumnContainer, #footer, #leftColumn, #rightColumn, #SOWrap{border:none !important;}

#leftColumn {width: 177px !important; margin:0 1px 0 -177px !important;}



/* no cutesyclub */
#masthead{display:none;}
#masthead {height:50px;}
a.rank.r3p {visibility:visible !important;}


/* heading and paragraph size and spacing */

h2{margin:0px  !important; padding:0px  !important; margin-top:6px !important; margin-bottom:3px !important; font-size: 130% !important;}

h3{margin:0px  !important; padding:0px  !important; margin-top:0px !important; margin-bottom:3px !important; font-size: 100% !important;}

#gamelog h3{margin-top:0px !important;}
p{margin-top:6px !important; margin-bottom:3px !important;  }

#middleColumn  { padding-top:0.5em !important;  }

/* Highlight games that are ready */
.status_green { background-color: transparent !important;}

#footer{padding-bottom:100px !important;}

#log, 
#full-log,
#chat,
#full-chat,
#chat-form,
#summary {width:60% !important;}

}
