@-moz-document domain("10fastfingers.com"), domain("10ff.net") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

#practice-main,
#text-practice,
#text-practice ul.pagination li.current,

.alert-danger,
.btn-success,
.button,
.list-group-item,
.nav-tabs>li.active>a,
.nav-tabs>li>a:hover,
.panel-primary,
.pagination>li>a, .pagination>li>span,
.tab-content,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

#CybotCookiebotDialogNav .CybotCookiebotDialogNavItemLink.CybotCookiebotDialogActive {
    border-bottom: 1px solid #fff;
}

/* Fonts 
_____________________________________________________________________________________________________*/

strong
{
	font-size: 2.5rem!important;
}

#homepage #main #content,
#competition-rank-table tbody td.rank span,

body,
small
{
	font-size: 1.85rem!important;
}

#competition-rank-table tbody td.keystrokes,

.ago,
.tests-taken-all,
.tests-taken,

input.tiny
{
	font-size: 1.52rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

#CybotCookiebotDialog .CybotCookiebotFader,
#advertisement,
#fs-sidewall-left-container,
#fs-sidewall-right-container,
#primisPlayerContainerDiv,

.fs-sticky-footer,

[data-freestar-ad*="__"],
[id*='google_ads_iframe']
{
	display: none!important;
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.col-md-offset-3 {
    margin-left: 13%;
}

#homepage #main #centerbox {
    margin-top: 1vw;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.pagination>li>a, .pagination>li>span
{
	padding: 13px 26px!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

#homepage #main #centerbox,

.container-modified,
.wrap
{
	max-width: none!important;
}

#homepage #main #centerbox a.cta_button,
#top1000-index-container #hardcore-mode .levels a.level .stars,
#top1000-index-container #softcore-mode .levels a.level .stars,

dd.lastpost, dd.redirect, dd.moderation, dd.time, dd.info,
input.tiny
{
	width: auto!important;
}

.col-md-6
{
	width: 100%!important;
}

.col-lg-8,
.container-modified,
.wrap
{
	width: 98%!important;
}

ul.topiclist dt {
    width: 73%;
}

#competition-rank-table tbody td.rank span {
    width: 77px!important;
}

dd.posts, dd.topics, dd.views {
    width: 10rem!important;
}

}

