@-moz-document domain("10fastfingers.com"), domain("10ff.net") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

#CybotCookiebotDialogFooter .CybotCookiebotDialogBodyButton:hover,
#CybotCookiebotDialogNav .CybotCookiebotDialogNavItem:hover a,
#CybotCookiebotDialogNav .CybotCookiebotDialogNavItemLink.CybotCookiebotDialogActive,
#text-practice ul.pagination li.current,

.highscore-box #table-data .tests-taken-all,
.table tr:hover,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

#CybotCookiebotDialog *, #CybotCookiebotDialogBodyUnderlay *,
#activity-module div.list-group-item,
#competition-rank-table tbody td.keystrokes,
#participation,
#row1,
#speedtest-main #row1,

.alert-danger,
.content,
.pagination li.ellipsis span,
.text-muted,

body,
li,
p,
small
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

#activity-module div.list-group-item strong,

h1,
h2,
strong,
th
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.age .text-muted,
.ago,
.author,
.lastpost,
.profile-joined,

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.notice
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

.btn-success,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

.profile-rank
{
	color: #ff9350!important;
}


}

@-moz-document domain("10fastfingers.com"), domain("10ff.net") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

.forabg,
.forumbg,
.headerbar,
.pm_read, .topic_read,
.sticky_read
{
	background-image: none!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#content-bg,
#main-content-trenner,
#practice-main,
#text-practice #sortable-table-wrap tr:hover td,
#text-practice,
#top1000-index-container #softcore-mode .levels a.level, #top1000-index-container #hardcore-mode .levels a.level,
#top1000-index-container,

.bg1,.bg2,
.button,
.forumbg,
.headerbar,
.highscore-box #table-data,
.highscore-box li a,
.list-group-item,
.main-layout #main-content, .main-layout .main-content,
.nav>li>a:hover, .nav>li>a:focus,
.navbar,
.pagination>li>a, .pagination>li>span,
.panel,
.tab-content,
.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th,

[style*="background: url('/img/layout-responsive/trans-white-bg-20.png')"],
ul.forums
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.btn-primary
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.btn-primary::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.btn-primary:hover::before
 {
  opacity: 1!important;
}

/* Red 
_____________________________________________________________________________________________________*/

#CybotCookiebotDialogBodyButtonDecline,
[value="Enter"]
{	
	background: radial-gradient(circle, #d90008a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

#CybotCookiebotDialogBodyButtonDecline::before,

[value="Enter"]::before
{
	background: radial-gradient(circle, #000 0%, #d90008 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

#CybotCookiebotDialogBodyButtonDecline:hover::before,

[value="Enter"]:hover::before {
  opacity: 1!important;
}

/* Yellow
_____________________________________________________________________________________________________*/

#CybotCookiebotDialogBodyLevelButtonLevelOptinAllowallSelection,
#homepage #main #centerbox a.cta_button#typing-competition
{	
	background: radial-gradient(circle, #fdb714a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

#CybotCookiebotDialogBodyLevelButtonLevelOptinAllowallSelection::before,
#homepage #main #centerbox a.cta_button#typing-competition::before
{
	background: radial-gradient(circle, #000 0%, #fdb714 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

#CybotCookiebotDialogBodyLevelButtonLevelOptinAllowallSelection:hover::before,
#homepage #main #centerbox a.cta_button#typing-competition:hover::before {
  opacity: 1!important;
}

/* Green 
_____________________________________________________________________________________________________*/

#CybotCookiebotDialogBodyLevelButtonLevelOptinAllowAll,
#homepage #main #centerbox a.cta_button#typing-test,

.btn-default
{	
	background: radial-gradient(circle, #09b705a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

#CybotCookiebotDialogBodyLevelButtonLevelOptinAllowAll::before,
#homepage #main #centerbox a.cta_button#typing-test::before,

.btn-default::before
{
	background: radial-gradient(circle, #000 0%, #09b705 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

#CybotCookiebotDialogBodyLevelButtonLevelOptinAllowAll:hover::before,
#homepage #main #centerbox a.cta_button#typing-test:hover::before,

.btn-default:hover::before {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

.forabg,
.list-group
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

#CybotCookiebotDialog,
#sidebar-md-lg,
#speedtest-main #words,
#top1000-index-container,
#words,

.wrap
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

#CybotCookiebotDialog:hover,
#sidebar-md-lg:hover,
#speedtest-main #words:hover,
#top1000-index-container:hover,
#words:hover,

.forabg:hover,
.list-group:hover,
.wrap:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
_____________________________________________________________________________________________________*/

.alert-danger
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

/* Blue Gradient 
_____________________________________________________________________________________________________*/

#input-row,
#speedtest-main #input-row,
#top1000-index-container #hardcore-mode .level-group,

.panel-primary>.panel-heading
{
	background: linear-gradient(to bottom, #A7C8E7, #000 100%)!important;
}

.headerbar
{
	background: linear-gradient(to bottom, #12A3EB, #000 100%)!important;
}

/* Yellow Gradient 
_____________________________________________________________________________________________________*/

blockquote
{
	background: linear-gradient(to bottom, #9d9d5d 0%, #000 100%)!important;
}

/* Orange Gradient 
_____________________________________________________________________________________________________*/

#top1000-index-container #softcore-mode .level-group
{
	background: linear-gradient(to bottom, #ff9404 0%, #000 100%)!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

#participation
{
	background: linear-gradient(to bottom, #424040 0%, #000 100%)!important;
}

/* Gradient Border 
_____________________________________________________________________________________________________*/

blockquote
{
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

#top1000-index-container #hardcore-mode .levels a.level:hover,
#top1000-index-container #softcore-mode .levels a.level:hover,

.table tr:hover,

a.list-group-item.active, a.list-group-item.active:hover, a.list-group-item.active:focus,
li.row:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

.label-success
{
	background: radial-gradient(ellipse at center, #07ed07 0%,#0000 70%)!important;
	padding: 13px 26px!important;
}

#competition-rank-table tbody td.rank span
{
	background: radial-gradient(ellipse at center, #FFbb00 0%,#0000 70%)!important;
	padding: 13px 26px!important;
}

#CybotCookiebotDialogNav .CybotCookiebotDialogNavItemLink.CybotCookiebotDialogActive,
#text-practice ul.pagination li.current,

.nav-tabs>li.active>a
{
	background: radial-gradient(ellipse at center, #00809d 0%,#0e0e0e00 70%)!important;
	padding: 13px 26px!important;
}


}

@-moz-document domain("10fastfingers.com"), domain("10ff.net") {
/* Image Settings */

#activity-module div.list-group-item,
#top1000-index-container #softcore-mode .levels a.level, #top1000-index-container #hardcore-mode .levels a.level,

.table tr,

li.row,
p
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

#activity-module div.list-group-item:hover,
#top1000-index-container #softcore-mode .levels a.level:hover,
#top1000-index-container #hardcore-mode .levels a.level:hover,

.table tr:hover,

li.row:hover,
p:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img:not(#main-logo):not(#logo-sidebar)
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}


}

@-moz-document domain("10fastfingers.com"), domain("10ff.net") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

/* Avatar ______________________________________________________________________________*/

[src*="https://www.gravatar.com/avatar/"] {
	background: url(https://i.ibb.co/f2wDXnH/Avatar.png) no-repeat!important;
	box-sizing: border-box;
}

[src*="https://www.gravatar.com/avatar/"] {
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 1px!important;
}

/* Search ___________________________________________________________________________*/

.fa-search:before {
    content: "";
}

.fa-search:before
{
	background: url(https://i.ibb.co/cJFxJM6/search3.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-search:before
{
	background-size: 15px!important;
	height: 36px!important;
	padding-left: 16px!important;
	width: 1px!important;
}

/* Settings ___________________________________________________________________________________*/

.fa-gear:before, .fa-cog:before {
    content: "";
}

.fa-gear:before, .fa-cog:before
{
	background: url(https://i.ibb.co/2dVYhWK/Setting-icon.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-gear:before, .fa-cog:before
{
	background-size: 15px!important;
	height: 36px!important;
	padding-left: 16px!important;
	width: 1px!important;
}


/* Star ___________________________________________________________________________*/

/* On ___________________*/

#top1000-index-container #softcore-mode .levels a.level .stars .star, #top1000-index-container #hardcore-mode .levels a.level .stars .star
{
	background: url(https://i.ibb.co/WH0qS2k/star.png) no-repeat!important;
	box-sizing: border-box;
}

/* Off __________________*/

selector
{
	background: url(https://i.ibb.co/VBcDjnC/star-empty-glow.png) no-repeat!important;
	box-sizing: border-box;
}

#top1000-index-container #softcore-mode .levels a.level .stars .star, #top1000-index-container #hardcore-mode .levels a.level .stars .star
{
	background-size: 25px!important;
	height: 36px!important;
	padding-left: 26px!important;
	width: 1px!important;
}


}

@-moz-document domain("10fastfingers.com"), domain("10ff.net") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
