css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://junkuchan.org/a/"), url-prefix("http://boards.4chan.org/a/") {

.logo {
background:url('http://img188.imageshack.us/img188/2350/dollarslogo.png') no-repeat 50% 0px !important;
height:61px;
color:#000 !important;
font-size:smaller !important;
text-align:left !important;
}

.logo img {
display:none;
}

.postarea {
background:#FCFCFC !important;
color:#000 !important;
position:relative;
width:500px;
margin:0 auto;
-moz-border-radius:20px;
padding:10px 10px 0;
}

.postarea form .postform {
padding-bottom:40px;
position:relative;
}

.postarea .rules li, .postarea .rules a {
font-size:smaller !important;
color:#000;
}

.postarea input[type='text'], .postarea input[type='password'], .postarea textarea {
color:#eee !important;
}

.postarea input:focus, .postarea textarea:focus {
background-color:#ccc !important;
color:#000 !important;
}

.postarea td input[type='file']{
color:#000 !important;
}

.postarea input[type='submit'] {
position:absolute;
bottom:10px;
margin-left:-50%;
left:auto;
background:-moz-linear-gradient(top,#FAFAFA, #FAFAFA 47%,#E8E8DC 50%, #E8E8DC) !important;
font-family:verdana,sans-serif;
font-weight:bold;
font-size:1.5em;
text-transform:uppercase;
text-align:center;
width:300px;
height:30px;
border-width:3px !important;
border-style:solid !important;
-moz-border-radius:10px;
color:#000 !important;
}

.postarea input[type='submit']:hover {
background:-moz-linear-gradient(top,#C9C9C0, #C9C9C0 47%,#7A7A74 50%, #7A7A74) !important;
color:#fff !important;
}

div[id^='thread'] > table, form[name='delform'] > table {
clear:both;
}

.doubledash {
display:none !important;
}

td.reply, td.replyhl, td.highlight {
float:left !important;
}

td.reply, td.highlight, td.replyhl {
color:#fff !important;
font-weight:bold;
position:relative !important;
border-color:#fff !important;
-moz-border-radius:10px;
border-width:3px !important;
border-style:solid !important;
display:block;
margin-left:120px !important;
padding-bottom:10px;
}

td.reply {
background:-moz-linear-gradient(top, #7CADD3,#7CADD3 27px, #446CAE 30px, #446CAE) !important;
}

td.highlight, td.replyhl {
background:-moz-linear-gradient(top, #F7BD44,#F7BD44 27px, #E07414 30px, #E07414) !important;
}

.postername, .commentpostername {
font-weight:bold !important;
display:block;
color:#EEA211 !important;
background-repeat:no-repeat !important;
background-position:50% 0 !important;
}

td.reply .postername, td.highlight .postername, td.reply .commentpostername, td.replyhl .commentpostername {
display:block;
position:absolute;
left:-100px;
width:100px;
text-align:center;
clear:both;
padding-top:50px;
top:0px;
overflow:visible;
margin:0 !important;
}

/* reply anon avatar */
td.reply .postername, td.reply .commentpostername {
background-image:url('http://img97.imageshack.us/img97/9367/lilanon.jpg') !important;
}

/* reply anon highlight avatar */
td.highlight .postername, td.replyhl .commentpostername {
background-image:url('http://img718.imageshack.us/img718/7435/lilanonorange.jpg') !important;
}

.postername:before, .commentpostername:before {
display:block;
width:22px;
height:22px;
position:absolute;
left:78px;
top:16px;
}

td.reply .postername:before, td.reply .commentpostername:before {
content:url('http://img33.imageshack.us/img33/99/bubblearrow.png');
}

td.highlight .postername:before, td.replyhl .commentpostername:before {
content:url('http://img188.imageshack.us/img188/3659/bubblearroworange.png');
}

/* OP avatar */
div[id^='thread'] > label > .postername, form[name='delform'] > .postername {
float:left;
margin-top:10px;
margin-right:10px;
background-image:url('http://img718.imageshack.us/img718/7435/lilanonorange.jpg') !important;
padding:50px 10px 0;
min-width:50px;
text-align:center;
top:0px;
overflow:visible;
margin:0 !important;
}

.reply a {
color:#4B2D8B !important;
}

.reply a:visited {
color:#2A0679 !important;
text-decoration:line-through;
}

.reply a:hover {
color:#B59228 !important;
}


.reply, .reply label, .replyhl label, .reply .reflink a, .replyhl .reflink, .reply .filesize, .reply .filesize a, .reply span a {
color:#7CADD3 !important;
text-shadow:rgba(0,0,0,0.1) -1px 0, rgba(0,0,0,0.1) 0 -1px,
            rgba(255,255,255,0.1) 1px 0, rgba(255,255,255,0.1) 0 1px,
            rgba(0,0,0,0.1) -1px -1px, rgba(255,255,255,0.1) 1px 1px;
}

.reply > blockquote, .replyhl > blockquote, .reply > .commentpostername, .replyhl > .commentpostername {
text-shadow:none !important;
}
.reply > blockquote, .replyhl > blockquote {
color:#fff !important;
}


/* avatar for poster with email link */
.postername a, .commentpostername .linkmail {
color:#8BCBFE !important;
font-size:11pt !important;
font-weight:bold !important;
background:none !important;
}

td.reply .commentpostername a:before, td.reply .postername a:before {
display:block;
position:absolute;
width:50px;
height:50px;
top:0px;
left:25px;
content:url('http://img188.imageshack.us/img188/1548/lilanonblue.jpg');
}

.unkfunc {
color:#EEA211 !important;
padding-left:10px;
}

.reflinkpreview {
z-index:100;
margin:-50px 0 0 -50px;
background:#000 !important;
}

.reflinkpreview .reply {
display:block !important;
min-width:100px !important;
}
.reflinkpreview div .postername {
background-color:#000 !important;
}

.reflinkpreview div .postertrip:before {
left:-65px;
}

.reflinkpreview div .postername:before {
display:none;
}

body {
background:#000 !important;
color:#fff !important;
}

/* avatar for tripfags */
.postertrip {
background:none !important;
}

.postertrip:before {
content:url('http://img707.imageshack.us/img707/4793/lilanongreen.jpg');
display:block;
width:50px;
height:50px;
position:absolute;
left:-75px;
top:0px;
}

}

/* fixes for /a/ */
@-moz-document url-prefix("http://boards.4chan.org/a/") {

.postarea input[type='submit'] {
-moz-appearance:none;
border:3px solid #000 !important;
}

.postarea input[type='text'], .postarea input[type='password'], .postarea textarea, .postarea label {
color:#000 !important;
}

}
